css:
@-moz-document url-prefix('https://cheddarapp.com') {

html {
  background: #e6e6e6;
}
body {
  background: #fff;
  max-width: 800px;
  overflow-x: hidden;
  padding-top: 0;
}
body > header {
  background: #e6e6e6;
  border-bottom-color: #d6d6d6;
  margin: 0 -9999em;
  padding: 2em 9999em .5em;
}
header.lists {
  background: #fff;
  margin: 0 -9999em;
  padding: 1em 9999em 0;
}
header.list {
  background: #f0f0f0;
  border-bottom: 1px solid #ddd;
  margin: 0 -9999em;
  padding: 1em 9999em 3.5em;
}
#task-create {
  margin-bottom: 4.5em;
}
header.list + div {
  margin-top: -3.5em;
}
#lists,
ol.tasks {
  background: #fff;
  margin: 0 -9999em;
  padding: 2em 9999em 1em;
}
ol.tasks li label:hover {
  cursor: pointer;
}
#lists {
  padding-top: .5em;
}
body > footer {
  background: #e6e6e6;
  border-top-color: #c6c6c6;
  margin: 0 -9999em !important;
  padding: 1.5em 9999em !important;
}
input {
  border-radius: 3px !important;
}
header.list a.edit {
  top: -2px;
}
ol.sortable span.handle:hover {
color: #ff7243;
}
li a img {
  top: 2px !important;
  vertical-align: top !important;
}
body > header a.your-lists {
color: #ff7243;
}
a {
  color: #444;
}
a,
a:hover {
  border-bottom: none;
}
a:hover {
  color: #ff7243 !important;
}
.shirt img {
  -webkit-filter: grayscale(100%);
  -webkit-transition: .1s ease-in-out;
}
.shirt:hover img {
  -webkit-filter: grayscale(0);
}
div.popover.archive {
  top: auto;
  right: auto;
  left: 50%;
  margin-left: 250px;
}
div#lightbox-container {
  background: hsla(0,0%,0%,.5);
}
div#lightbox {
  box-shadow: 0 3px 15px 2px hsla(0,0%,0%,.2);
}
div.grater {
  background: #fff;
  margin: 0 -9999em;
  padding: 1em 9999em 0;
}
div.grater > div + div {
  margin-bottom: 0;
}
.blue-box {
  margin-left: -.5em;
  margin-right: -.5em;
}
input[type="checkbox"] {
  position: relative;
  top: 1px;
}
label a {
  color: #777;
}

}
