*{-moz-box-sizing: content-box;-webkit-box-sizing: content-box; font-family:helvetica, arial, sans; }
textarea, input { outline: none; }
body{background:#eee url('http://img850.imageshack.us/img850/4044/86887622.png') repeat; font-size:16px; margin:0; padding: 0;  }

/*
