/*
Home page

*/
#fp{background:#000 url('http://img594.imageshack.us/img594/5864/85012763.png') repeat; margin:0; top:0; border-bottom:2px solid #fff;}

#fp form{width:650px;  margin:auto;  min-height: 170px; margin-top: 40px; }

#fp{margin-top:-22px;}
#fp h1{padding-top:50px;}
#fp h1 a {background:url('http://img715.imageshack.us/img715/3217/logoonwhite.png') ; background-size: 100%;  height:200px; background-repeat:no-repeat; }
#fp{color:#ccc;}
#fp a{color:#666;   font-size:18px; }
#fp a:hover{text-decoration:none; color:#d2e638; border:0;}

#fp input[value="I'm Feeling Lucky"]{display:none;}

p#chb{
   
}
#subm{position:relative;}
#subm input[type="submit"]{position:absolute; top:-102px; left:480px;}
#inp span{display:none;}
#howdl{ position:absolute; top:10px; right:30px; }

#inp{ float:none;  display:block; width:500px;}
#inp input, #fp input[type="submit"] {float:none !important;}
#chb{position:relative; float:left; margin-top:50px; height:100px; margin-top: -30px; margin-left: 60px;}
#chb label{float:left;}
/*
SEARCH HEADER

*/
#header{box-shadow:0 0 10px #444; border-bottom:1px solid #000; background:#000 url('http://img594.imageshack.us/img594/5864/85012763.png') repeat; color:#000; position:fixed; top:0; width:100%; z-index:222; padding:3px; font-size:0; }
#header a{color:#666; float:right; margin-right:20px; font-size:11px; }
#header a:hover{text-decoration:none; color:#fff; border:0;}
#TPBlogo{  opacity:0; }
#header a.img{ opacity:0.9; width:110px; margin-left:30px; margin-top:10px; background:url('http://img715.imageshack.us/img715/3217/logoonwhite.png') ; background-size: 100%; background-repeat:no-repeat;  display:block; float:left;}
#header a.img:hover{ opacity:1}

#header .inputbox, #inp input{ box-shadow:inset 0 0 9px #111 ; margin-bottom:30px; color:#ccc; float:left;  padding-right:35px; margin-left:20px; width:300px;  height:40px; font-size:20px; -webkit-appearance: none; border:2px solid #000; -webkit-transition: all 0.3s; transition: all 0.3s, -webkit-transition: all 0.5s;

  background: #444 -webkit-linear-gradient(top, rgba(70,70,70,1) 0%,rgba(70,70,70,0.1) 0%); /* Chrome10+,Safari5.1+ */
   }
#header .inputbox:hover, #inp input:hover{ background:#ccc; color:#333;  }
#header .inputbox:focus, #inp input:focus{box-shadow: 0 0 8px #d2e638 ; border-color:#d2e638; width:400px; color:#000; background:#fff; }


#header input.submitbutton, #fp input[type="submit"] {cursor:pointer;  float:left; font-size:18px; -webkit-appearance: none; background:#999; color:#000; font-weight:bold; border:0px solid #999; border-radius:5px; padding:11px; margin-top:8px;
  /*glossy! */
  background: #e2e2e2; /* Old browsers */
  background: -moz-linear-gradient(top, #e2e2e2 0%, #dbdbdb 50%, #d1d1d1 51%, #fefefe 100%); /* FF3.6+ */
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e2e2e2), color-stop(50%,#dbdbdb), color-stop(51%,#d1d1d1), color-stop(100%,#fefefe)); /* Chrome,Safari4+ */
  background: -webkit-linear-gradient(top, #e2e2e2 0%,#dbdbdb 50%,#d1d1d1 51%,#fefefe 100%); /* Chrome10+,Safari5.1+ */
  background: -o-linear-gradient(top, #e2e2e2 0%,#dbdbdb 50%,#d1d1d1 51%,#fefefe 100%); /* Opera 11.10+ */
  background: -ms-linear-gradient(top, #e2e2e2 0%,#dbdbdb 50%,#d1d1d1 51%,#fefefe 100%); /* IE10+ */
  background: linear-gradient(to bottom, #e2e2e2 0%,#dbdbdb 50%,#d1d1d1 51%,#fefefe 100%); /* W3C */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#e2e2e2', endColorstr='#fefefe',GradientType=0 ); /* IE6-9 */
}
#header input.submitbutton:hover, #header input.submitbutton:active, #fp input:active[type="submit"], #fp input:hover[type="submit"]{
  
  
  background: #e6f0a3; /* Old browsers */
  background: -moz-linear-gradient(top, #e6f0a3 0%, #d2e638 50%, #c3d825 51%, #dbf043 100%); /* FF3.6+ */
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e6f0a3), color-stop(50%,#d2e638), color-stop(51%,#c3d825), color-stop(100%,#dbf043)); /* Chrome,Safari4+ */
  background: -webkit-linear-gradient(top, #e6f0a3 0%,#d2e638 50%,#c3d825 51%,#dbf043 100%); /* Chrome10+,Safari5.1+ */
  background: -o-linear-gradient(top, #e6f0a3 0%,#d2e638 50%,#c3d825 51%,#dbf043 100%); /* Opera 11.10+ */
  background: -ms-linear-gradient(top, #e6f0a3 0%,#d2e638 50%,#c3d825 51%,#dbf043 100%); /* IE10+ */
  background: linear-gradient(to bottom, #e6f0a3 0%,#d2e638 50%,#c3d825 51%,#dbf043 100%); /* W3C */
  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#e6f0a3', endColorstr='#dbf043',GradientType=0 ); /* IE6-9 */
  opacity:0.9;
  }
#header input.submitbutton:active, #fp input:active[type="submit"]{opacity:1; margin-top:9px;}
#header br{ }

label{ opacity:0.5;  font-size:13px; color:#ccc; display:block;  height:20px; padding-top:20px;  padding:10px;    background:#333; margin:2px; float:right; }
label:hover{opacity:1;}
 label:active{background:#777;color:#fff;}
#category{  float:right;}





/*

detail page
*/

#detailsframe{}
#detailsframe #title{background:#000; color:#fff;}


#main-content, #browseContainer{margin-top:100px;}
#content{margin:0 !important; margin-left:100px; margin-right:30px;}

/*

listing - browse
*/
thead#tableHead th  {
background: #333; color:#999; font-size:14px; padding:10px; height:40px;
}
thead#tableHead th a{
color:#fff;
}
thead#tableHead th, thead#tableHead tr{height:10px !important;}
#tableHead a:hover{color:#d2e638; }

table#searchResult th, table#searchResult td{border:0;}

table#searchResult tr{border-bottom:1px solid #fff; height:60px;}
table#searchResult td.vertTh{font-weight:normal;}
table#searchResult td.vertTh center{text-align: left; font-size:0;}
table#searchResult td.vertTh center br{clear:none; display:none;}

table#searchResult tr, #detailsframe, #details {
/*background: #f1f0ed;*/
background:rgba(255,255,255,0.6);
}

table#searchResult td.vertTh center a{color:#777;font-size:14px; padding:0 10px;}
table#searchResult td.vertTh center a:first-child{color:#333; font-weight:bold; }
.detName{font-size:22px; margin-left:60px;}

a[title="Download this torrent using magnet"]{width:40px; opacity:0.6; height:40px; float:left; display:block; margin-top:-30px; background: url('http://img717.imageshack.us/img717/4489/magnetdw.png'); background-size: 100%; }
a:hover[title="Download this torrent using magnet"]{opacity:1;  }
a[title="Download this torrent using magnet"] img{opacity:0; }

img.dl{display:none; }
a[title="Download this torrent"]{display:none;}
img[title="This torrent has a cover image"]{display:none;}

table#searchResult td img {display:none;}

.detDesc{font-size:14px; margin-bottom:30px; margin-left:20px; }
.detLink{color:#777; font-weight:bold;}
.detLink:hover{color:#000; text-decoration:none; border:0; }



/* bye ads */
#img, #sky-right, #sky-banner, #adsfrm, #adsfrm{display:none !important ; width:0; height:0;}

footer, #foot{padding-top:80px; color:#fff; line-height:2em; border:0;}
footer a, #foot a{color:#333 !important; text-decoration:none; border:0 !important; background:rgba(255,255,255,0.8);   font-size:14px; padding:5px;  margin-top:10px;}
footer a:hover, #foot a:hover{text-decoration:none; background:#d2e638; border-radius:2px; border:0;}
