css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
html, #page-outer, .article-page, .group-page {
    background: #2C2C2C !important;
}
#page-container, .swift, .dropdown-menu, .twttr-dialog, .front-signup, .front-signin, .page-canvas, .mobile-download, .twttr-dialog .tweet-box, .has-sidebar {
    background: #333 !important;
}
.tweet, .flex-module, .list-link, .info, .profile-modal-extended, .profile-modal-header, .mini-profile-footer, .warning, .stream-end-inner, #feedback, .sticky a, .sticky, .spinner, .permalink-inner, .empty-timeline-footer, .discover-item, .tweet-box, .stats.js-mini-profile-stats {
    background: #444 !important;
    text-shadow: none !important;
}
.header-inner, .empty-timeline-header, .modal-header {
    background: #3C3C3C !important;
    text-align: center !important;
    border-bottom: 1px solid #000 !important;
}
.social-proof-tweet {
    background: #3C3C3C !important;
    text-align: center !important;
}
h1, .profile-modal-header h2 a {
    color: #FFFAFA !important;
}
h2, .empty-timeline-header, .tweet-counter, h3 {
    color: #DCDCDC !important;
    text-shadow: none !important;
}
a, li, h1, h2, h3, h4, h5, b, p, span, div {
    text-shadow: none !important;
}
.tweet > .content > p, .tweet-text, .dropdown-menu a, .dropdown-menu li, .info, .warning strong, #account-suspended, .discover-item-content div, .has-sidebar p, .article li {
    color: #eee !important;
}
p.bio, .location {
    color: #9F9F9F !important;
}
.tweet, .discover-item {
    border-bottom: 1px solid #000 !important;
}
.topbar-inner {
    background: #1C1C1C !important;
}
.new-tweets-bar {
    text-shadow: none !important;
    background: #2C2C2C !important;
    border-bottom: 0px !important;
    border-top: 0px !important;
}
.list-link, .stats.js-mini-profile-stats a {
    border-top: 0px solid #000 !important;
}
.stats.js-mini-profile-stats {
    border-bottom: 0px !important;
    border-top: 0px !important;
}
.swift, .sidebar, .modal-header {
    border: 0px !important;
}
.twitter-anywhere-tweet-box-editor {
    border-bottom: 0px !important;
}
textarea {
    background: #2C2C2C !important;
    color: #eee !important;
}
.request-favorited-popup {
    border-left: 1px solid #000 !important;
}
.js-tweet-stats-container.tweet-stats-container.already-open ul.stats, .tweet-stats-container ul.stats {
    border-top: 1px solid #000 !important;
}
}
