css:
@-moz-document url-prefix("https://mightytext.net/app/") {
input, input[type="password"], input[type="search"], isindex {
    border: 0 !important;
    background-color: #FFFFFF !important;
    border-bottom: 2px solid #33B5E5 !important;
}

input.ui-autocomplete-input {
    background-position: 95% 50% !important;
    margin-top: 5px !important;
    font-size: 16px !important;
    font-family: Helvetica, Arial, sans-serif !important;
    width: 300px !important;
}

span.promolinks {
    display: none !important;
}

span.tk-franklin-gothic-urw-cond {
    font-family: Helvetica, Arial, sans-serif !important;
}

#fb-like-button {
    display: none !important;
}

#list-of-tabs {
    width: 28% !important;
}

#msg {
    border: 0 !important;
}

#sms-container {
    margin-top: 8px !important;
}

#sms-thread-heading {
    background-color: #323232 !important;
    border-radius: 0px !important;
}

#top-header {
    background: #323232 !important;
}

#texty-logo img {
    margin-left: 10px !important;
    margin-top: 5px !important;
}

#tabs-phone-threads li {
    width: 100% !important;
}

#main-section {
    margin-left: 0 !important;
    width: 70% !important;
}

#uvTabLabel {
    display: none !important;
}

.button-angel {
    background: 0 !important;
    background-color: #EFEFEF !important;
    color: black !important;
    border-bottom: 1px solid #dfdfdf !important;
}

.sms-body {
    border-bottom-left-radius: 0 !important;
    border-bottom-right-radius: 0 !important;
    border-top-left-radius: 0 !important;
    border-top-right-radius: 0 !important;
    -webkit-box-shadow: white 0 0 0 !important;
    font-size: 14px !important;
    padding: 5px 10px !important;
    margin: 10px !important;
    color: #000 !important;
    opacity: 0.8 !important;
    border: 0 !important;
    background-color: white !important;
    max-width: 50% !important;
    min-width: 50% !important;
}

.ui-tabs .ui-tabs-nav li a {
    width: 95% !important;
}

.ui-state-active a, .ui-state-active a:link, .ui-state-active a:visited {
    background-color: #323232 !important;
}

.sms-line-item {
    margin-left: 0 !important;
    margin-bottom: 0 !important;
    border-bottom: 1px solid #DFDFDF !important;
}

.sms-time {
    float: right !important;
}

.sms-reply .sms-body {
    background: #EFEFFF !important;
    color: #222 !important;
    text-align: right !important;
}

.sms-reply.sms-line-item {
    background: #EFEFFF !important;
}

.sms-reply {
    padding-left: 21% !important;
}

div.user-alert {
    width: 100% !important;
}

button.button-angel {
    width: 100% !important;
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    border-style: none !important;
}

textarea:focus, select:focus {
    box-shadow: 0 !important;
    -webkit-box-shadow: 0 !important;
    -moz-box-shadow: 0 !important;
}

#inputsms-main {
    background-color: white !important;
}

#sendbox-main {
    border-top-width: 0 !important;
    border-left-width: 0 !important;
    border-right-width: 0 !important;
    border-bottom-width: 2px !important;
    border-color: #33B5E5 !important;
    max-height: 50px !important;
    font-family: Helvetica, Arial, sans-serif !important;
}
}
