css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forums.forzamotorsport.net") {

/* ***************************************************** */
/* Cleanup the forzamotorsports.net forums               */
/*  -reduce size of the left panel so 1 line posts don't */
/*   eat up half a page                                  */
/*  -reduce all pictures to 70px with full size on hover */
/*  -trim size of stuff surrounding posts.               */
/*  -make the search results text color visible [doh!]   */
/*  v1.0 02 Aug 2012                                     */
/* ***************************************************** */

/* left "ForumPostUser" panel mods */
/* left panel first container ForumPostUserPropertyList */

.useravatar {       /* the xbl gamercard stuff from ms lives here */
/*  display: none!important; */ /* skip all that user avatar mess */
}

.useravatar iframe {
    height: 105px!important;
}

 
.ForumPostUserAvatar.CommonPrintHidden { 
    height: 10px!important;
}

.ForumPostUserPropertyList { 
    height: 130px!important;
    text-align: right!important;
    margin: 0px!important;
    overflow: visible!important;
}

.ForumPostUserPropertyList img {
    display: none!important;
}


UL.ForumPostUserPropertyList LI {
    margin-right: 4px!important;
}

.UserProfileLink {
    text-align: center!important;
}

/* post all in td.ForumPostContentArea */
.ForumPostBodyArea {
    padding-top: 0px!important;
    margin-top: 0px!important;
}

.ForumPostBodyArea img {
    max-height: 60px!important;
}

.ForumPostBodyArea img:hover {
    max-height: 400px!important;
}

/* Header footer title */
.ForumPostHeader {
    padding: 2px 2px 2px 8px!important;
}

.ForumPostButtons {
    margin: 0px!important;
}

.ForumPostTitleArea {
    padding: 0px!important;
    margin: 0px!important;
}

.ForumPostAttachment {
    padding: 2px 2px 2px 8px!important;
}

.ForumPostFooterArea {
    padding: 2px!important;
    line-height: 10px!important;
}


/*  Fix search result text */
.CommonSearchResult {
    color: rgb(211, 213, 210)!important;
}


/* end forums post area cleanup */
}



/* *************************************** */
/* trim and clean gamercard stuff from ms  */
/* *************************************** */
@-moz-document domain("gamercard.xbox.com") {

.XbcGamercard  {
   height: 95px!important;
   width: 65px!important;
}

.XbcGamercard #Gamertag {
    width: 202px!important;
}

 
.RepContainer, .GamerscoreContainer, #PlayedGames  {
   display: none!important;
}


/* keep the gamerpic
.XbcGamercard #Gamerpic {display: none!important;}
*/

/* end gamercard.xbox.com cleanup */
}


/* end forzamotorsports.net forum  cleanup */
