css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pokecheck.org"), domain("sprites.pokecheck.org") {
html {
	min-height: 100%;
	position: relative;
}

body {
	color: #454545;
 	background: #f6f6f6;
 	font-size: 11pt;
 	font-family: verdana, helvetica, sans-serif;
 	margin:0px;
 	padding: 0px;
}

center img[src="i/howto.png"], img[src="i/howto2.jpg"], img[src="i/deposit.png"], img[src="i/download.png"], img[src="i/register.png"] { 
	box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
}

.linkbar.loginform {
	margin-top: -21px !important;
	padding-bottom: 20px !important;
	text-align: center;
}

.linkbar.loginform > .warnmsg {
    color: #167b1d !important;
}

a {
    text-decoration: none;
}

a:link {
	color: #2464a6 !important;
}

a:visited {
	color: #2464a6 !important;
}

a:hover, a:focus {
	color: #ce5337 !important;
}

form { 
	margin: 0px; padding: 0px; 
}

.mainheader {
 	background: #f6f6f6 !important;
 	padding: 25px !important;
 	border-bottom: none !important;
}

.maintitle a {
	color: #4cae5a !important;
}

.maintitle {
    color: #565656; 
	margin-top: 0px;
	text-shadow: 0px 1px 0px #ffffff;
}

h2, h3 {
    color: #505050;
}

.dnsaddress {
	margin: 20px auto !important;
	text-align: center;
	padding: 4px !important;
	background: #4cae5a !important;
	border-radius: 6px;
	border: 0px !important;
	font-size: 14pt !important;
	color: #ffffff;
	width: 256px !important;
}

.overflowbox {
 	background: #fefefe;
 	overflow: auto;
 	width: 100%;
 	height: 180px;
 	border-radius: 6px;
 	border: 1px solid #c5c5c5 !important;
}

.newslist {
	font-size: 10pt;
	margin: 15px 15px 15px -25px !important;
}

.newslist li {
	list-style-type: none !important;
    background: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/Pok%C3%A9%20Ball.png) 0px 5px no-repeat;
    padding: 3px 0px 3px 20px;
}

.featurestitle { 
	margin: 5px auto -5px; text-align: center; 
}

.disclaimer { 
	color: #ce3535 !important; font-weight: bold; 
}

.shoutbox {
 	overflow: auto;
 	width: 100%;
 	border-radius: 6px;
 	border: 1px solid #c5c5c5 !important;
 	margin-bottom: -1px;
}

.sbmsg {
 	width:100%;
 	font-size:10pt;
 	table-layout: fixed;
}

.linkbar {
    border-bottom: none !important;
}

.linkbar.searchbar {
	padding: 12px;
	box-shadow: 0px 1px #ffffff, 0px -1px rgba(0,0,0,0.2), inset 0px 1px #ffffff, inset 0px -1px rgba(0,0,0,0.2);
	background: #f0f0f0;
	background: -moz-linear-gradient(#f3f3f3, #eaeaea);
	background: -webkit-linear-gradient(#f3f3f3, #eaeaea);
	background: -o-linear-gradient(#f3f3f3, #eaeaea);  
	background: -ms-linear-gradient(#f3f3f3, #eaeaea);  
	background: linear-gradient(#f3f3f3, #eaeaea);
	text-align: center;
	border-bottom: none !important;
}

.fixedbar {
 	bottom: 0;  
 	position: absolute !important;
 	width: 100%;
 	padding: 5px 0px !important;
 	box-shadow: 0px -1px rgba(0,0,0,0.2), inset 0px 1px #ffffff;
 	border-top: none !important;
 	background: #f2f2f2 !important;
 	font-size: 10pt;
 	text-align: center;
}

input:not([type="checkbox"]), select, textarea {
	-moz-appearance: none !important; 
	-webkit-appearance: none !important;
}

input[type="text"], [type="password"], textarea {
    padding: 2px 2px !important;
    margin-right: 1px;
    color: #404040 !important;
	background: #fefefe !important;
	border-radius: 2px !important;
	border: none !important;
	box-shadow: inset 0px 1px rgba(0,0,0,0.15), inset 0px 0px 0px 1px #c4c4c4, inset 0px 2px 1px -1px rgba(0,0,0,0.4), 0px 1px #fdfdfd !important;
    outline: none !important;
}

input[type="text"]:hover, [type="password"]:hover, textarea:hover {
	box-shadow: inset 0px 1px rgba(0,0,0,0.25), inset 0px 0px 0px 1px #b1b1b1, inset 0px 2px 1px -1px rgba(0,0,0,0.4), 0px 1px #fdfdfd !important;
}

input[type="text"]:focus, [type="password"]:focus, textarea:focus {
	box-shadow: inset 0px 1px rgba(0,66,141,0.15), inset 0px 0px 0px 1px #8faac5, inset 0px 0px 2px 1px rgba(0,109,221,0.6), 0px 1px #fdfdfd !important;
}

input[type="button"]::-moz-focus-inner, input[type="submit"]::-moz-focus-inner {
    padding: 0 !important;
    border: 0 !important;
}

input[type="button"], [type="submit"], select {
	padding: 2px 5px !important;
	margin: 1px 1px !important;
	border-radius: 3px !important;
	border: none !important;
	color: #404040 !important;
	background: #fafafa !important;
	background: -moz-linear-gradient(#fcfcfc 0%, #f9f9f9 48%, #f2f2f2 51%, #eaeaea 100%) !important;
	background: -webkit-linear-gradient(#fcfcfc 0%, #f9f9f9 48%, #f2f2f2 51%, #eaeaea 100%) !important;
	background: -o-linear-gradient(#fcfcfc 0%, #f9f9f9 48%, #f2f2f2 51%, #eaeaea 100%) !important;
    background: -ms-linear-gradient(#fcfcfc 0%, #f9f9f9 48%, #f2f2f2 51%, #eaeaea 100%) !important;
	background: linear-gradient(#fcfcfc 0%, #f9f9f9 48%, #f2f2f2 51%, #eaeaea 100%) !important;
	box-shadow: inset 0px 0px 0px 1px #afafaf, inset 0px 2px #ffffff, 0px 1px rgba(0,0,0,0.05) !important;
}

input::-webkit-file-upload-button {
	padding: 2px 5px !important;
	margin: 1px 1px !important;
	border-radius: 3px !important;
	border: none !important;
	color: #404040 !important;
	background: #fafafa !important;
	background: -webkit-linear-gradient(#fcfcfc 0%, #f9f9f9 48%, #f2f2f2 51%, #eaeaea 100%) !important;
	background: linear-gradient(#fcfcfc 0%, #f9f9f9 48%, #f2f2f2 51%, #eaeaea 100%) !important;
	box-shadow: inset 0px 0px 0px 1px #afafaf, inset 0px 2px #ffffff, 0px 1px rgba(0,0,0,0.05) !important;
}

input[type="button"]:hover, [type="submit"]:hover, select:hover {
	box-shadow: inset 0px 0px 0px 1px #999999, inset 0px 2px #ffffff, 0px 1px rgba(0,0,0,0.08) !important;
}

input::-webkit-file-upload-button:hover {
	box-shadow: inset 0px 0px 0px 1px #999999, inset 0px 2px #ffffff, 0px 1px rgba(0,0,0,0.08) !important;
}

input[type="button"]:active, [type="submit"]:active {
	background: #e5e5e5 !important;
	background: -moz-linear-gradient(#eaeaea 0%, #e1e1e1 48%, #d8d8d8 51%, #c7c7c7 100%) !important;
	background: -webkit-linear-gradient(#eaeaea 0%, #e1e1e1 48%, #d8d8d8 51%, #c7c7c7 100%) !important;
   	background: -o-linear-gradient(#eaeaea 0%, #e1e1e1 48%, #d8d8d8 51%, #c7c7c7 100%) !important;
   	background: -ms-linear-gradient(#eaeaea 0%, #e1e1e1 48%, #d8d8d8 51%, #c7c7c7 100%) !important;
 	background: linear-gradient(#eaeaea 0%, #e1e1e1 48%, #d8d8d8 51%, #c7c7c7 100%) !important;
	box-shadow: inset 0px 0px 0px 1px #919191, inset 0px 2px 1px rgba(0,0,0,0.24), 0px 1px rgba(0,0,0,0.06) !important;
}

input::-webkit-file-upload-button:active {
	background: #e5e5e5 !important;
	background: -webkit-linear-gradient(#eaeaea 0%, #e1e1e1 48%, #d8d8d8 51%, #c7c7c7 100%) !important;
	background: linear-gradient(#eaeaea 0%, #e1e1e1 48%, #d8d8d8 51%, #c7c7c7 100%) !important;
	box-shadow: inset 0px 0px 0px 1px #919191, inset 0px 2px 1px rgba(0,0,0,0.24), 0px 1px rgba(0,0,0,0.06) !important;
}

select {
    padding: 1px !important;
    margin-right: 2px !important;
}

@media screen and (-webkit-min-device-pixel-ratio:0) {
select {
    padding-right: 17px !important;
    line-height: 16px !important;
    background: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/Arrow.png) no-repeat right 49%, 
    -webkit-linear-gradient(#fcfcfc 0%, #f9f9f9 48%, #f2f2f2 51%, #eaeaea 100%) !important;
}
}

/* Legality analysis messages */

.valid { 
	color: #006600; 
}
.warning { 
	color: #e16e1a !important; 
}
.error { 
	color: #ce3535 !important; font-weight: bold; 
}
.warnmsg { 
	color: red; 
}

.cont {
 	font-size: 8pt;
 	font-style: italic;
 	color: black;
}

.stattable {
 	background: #fefefe !important;
 	margin: 3px auto;
 	width:100%;
}

.statheader {
 	background: #ccc;
 	text-align: center;
}

.up { 
	color:#ab4848 !important;
}
.down { 
	color:#3e6287 !important;
}
.st1 {
	background: #f8f8f8 !important;
}
.st0 { 
	background: #ececec !important;
}
.sthp { 
	background: #e0eaf4 !important;
}
.move0 { 
	background: #ececec !important;
}
.move1 { 
	background: #f8f8f8 !important;
}

/* Pokemon detail tables */

.dblock {
 	width:300px;
 	background: #fefefe !important;
 	border-radius: 6px;
 	border: 1px solid #c5c5c5 !important;
 	padding: 5px;
 	margin: 10px;
 	text-align: left;
}

.dhead {
 	border-bottom: none !important;
 	text-align:center;
 	font-size:12pt;
 	margin-bottom: 4px !important;
}

/* Pokemon dropdown colors */

.red { 
	background:#c85f5f !important; color: rgba(255,255,255,0.83); text-shadow: 0 1px 1px rgba(0,0,0,0.38);
}
.gold { 
	background:#dbbe54 !important; color: rgba(255,255,255,0.97); text-shadow: 0 1px 1px rgba(0,0,0,0.38);
}
.emerald { 
	background:#4cae5a !important; color: rgba(255,255,255,0.91); text-shadow: 0 1px 1px rgba(0,0,0,0.38);
}
.platinium { 
	background:#999999 !important; color: rgba(255,255,255,0.91); text-shadow: 0 1px 1px rgba(0,0,0,0.38);
}
.black { 
	background:#3b3b3b !important; color: rgba(255,255,255,0.83) !important; text-shadow: 0 1px 1px rgba(0,0,0,0.38);
}

/* Result table rows */

.row0 {
 	background: #e4e4e4 !important;
 	cursor:pointer;
}

.row1 {
 	background: #ececec !important;
 	cursor:pointer;
}

.row1:hover { 
	background:#c2e6c8 !important; 
}
.row0:hover { 
	background:#bbdfc1 !important; 
}

/* Tooltips cue */

abbr, acronym {
 	border-bottom: 1px dashed gray;
 	cursor: help;
}

.pk0 {
 	border: none !important;
 	background:#f8f8f8 !important;
}

.pk1 {
 	border: none !important;
 	background:#ececec !important;
}

.box {
 	cursor: pointer;
 	font-size: 10pt;
 	padding: 1px 0px 10px 8px;
 	line-height: 10pt;
}

.pk0:hover { 
	background:#d2f3d7 !important; 
}
.pk1:hover { 
	background:#c2e6c8 !important; 
}

a.dexlink { 
	font-size:10pt; color:#550000; 
}

hr {
 	border: 0px none;
 	border-bottom: 1px solid black;
}

/* Shoutbox messages */

.privmsg { 
	background: #bee9bc !important; 
}
.admmsg1 { 
	background: #e0eaf4 !important; 
}
.admmsg0 { 
	background: #c7d9eb !important; 
}
.msg1 { 
	background: #ececec !important; 
}
.msg0 { 
	background: #fefefe !important; 
}
.msgdate { 
	font-size:8pt; color:#353535; white-space: nowrap; 
}

.shoutboxinput {
 	width: 83.5% !important;
 	height: 16px;
 	background: #fff;
 	border-radius: 4px;
 	border: 1px solid #707070;
 	margin-top: 3px !important;
}

.shoutboxsubmit {
 	width: 14.8% !important;
 	padding-bottom: 3px !important;
 	margin-left: 1px !important;
 	height: 20px !important;
}

.movetable {
 	background: #fefefe !important;
 	margin: 3px auto;
}

.betawarn {
 	border: none !important;
 	background: #fffdd3 !important;
 	padding: 3px;
 	margin: 5px 2px 0px;
 	text-align: center;
}

.legaltable {
 	background: #fefefe !important;
 	margin: 3px auto;
 	text-align: left;
 	width: 100%;
}

.legalheader { 
	background:#d9d9d9 !important; text-align: center; 
}
.legal0 { 
	background: #ececec !important; 
}
.legal1 { 
	background: #f8f8f8 !important; 
}

.bottombuttons { 
	text-align:center;width:100%;
}
.dlc { 
	font-style: italic; 
}

.rankings {
 	height: 300px;
 	overflow: auto;
 	width: 450px;
}

.zipnotice {
 	background: #fefefe !important;
 	border-radius: 6px;
 	border: 1px solid #c5c5c5 !important;
 	padding: 4px 8px !important;
 	width: 560px;
 	margin: auto;
 	font-size:10pt;
}

input.fileimport {
 	width: 600px;
 	background: #f6f6f6 !important;
}

.pubbox {
 	width: 850px;
 	background: #fefefe !important;
 	border: 1px solid #c5c5c5 !important;
 	padding: 5px;
 	margin:10px auto;
}

.boxowner {
 	font-size: 10pt;
 	font-style: italic;
 	text-align: center; 
}

.pklisthdr { 
	background: #d2d2d2 !important; 
}

.sortable {
    margin: 5px auto 3px auto;
}

.markings {
 	background: #c0c0c0 !important;
 	height: 10px;
 	padding-top: 1px;
 	width: 56px;
 	padding-left: 2px !important;
 	margin: 3px 0px 0px 1px;
}

.spritenotice {
 	width: 800px;
 	text-align: justify;
 	margin: 0px auto;
}

.spritereport {
 	width: 750px;
 	margin: 20px auto;
 	border: 1px solid gray;
 	padding: 5px;
 	background: #efefef;
}

.iconsubmit {
 	width: 750px;
 	margin: 20px auto;
 	border: 1px solid gray;
 	padding: 5px;
 	background: #efefef;
}

.icontable {
 	width: 800px;
 	margin: 10px auto;
 	border: 1px solid gray;
 	background: #efefef;
 	padding: 3px;
}

.faqentry {
 	margin: 8px auto;
 	background: #fefefe !important;
 	border: 1px solid #c5c5c5 !important;
 	padding: 8px;
 	width: 780px;
 	text-align: justify;
}

.statheader {
 	background: #d9d9d9 !important;
}

.headless {
 	margin: 0px auto 10px;
 	width: 720px;
 	background: #eee;
 	border: 1px solid black;
 	padding: 2px;
 	text-align: center;
}

.prefs {
 	background: #fefefe !important;
 	border: 1px solid #c5c5c5;
 	margin: 10px auto;
 	width: 800px;
}

.prefs tr.pklisthdr {
	background: #e0e0e0 !important;
}

.prefs tr.pk0:hover {
	background: #f8f8f8 !important;
}

.prefs tr.pk1:hover {
	background: #ececec !important;
}

legend {
 	font-weight: bold;
 	padding: 0px 5px;
}

.langbar {
 	padding: 27px 12px 0px 0px;
 	position: absolute;
 	top: 8px;
 	right: 15px;
}

.langbar img {
	box-shadow: 0 0 1px rgba(0,0,0,0.33);
}

.langbar img:hover {
    opacity: 0.75 ;
}    

.highlight {
 	background: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/Pok%C3%A9%20Ball.png) 0px 5px no-repeat, #fffdd3 !important;
}

.credits {
 	margin: 10px auto;
 	background: #fefefe;
 	width: 1024px;
}

.credits td {
    padding-left: 5px;
}

.credits tr.pk0:hover {
	background: #f8f8f8 !important;
}

.credits tr.pk1:hover {
	background: #ececec !important;
}

.atlevel {
 	font-size: 9pt;
}

.atlevel a {
 	color: #2464a6 !important;
 	cursor: help;
}

.sbu {
 	color: #000030;
 	cursor: help;
}

a.pknlink { 
	color: black; 
}
td.evs { 
	font-size: 80%; 
}
img.pki { 
	margin: -2px -2px; 
}
img.hpt { 
	margin: 1px 0px -1px; 
}
div.otflag { 
	min-width: 94px; 
}
span.shiny { 
	font-size: 10pt; 
}
div.scrollwrap { 
	max-height:600px;overflow:auto;width:95%;margin:auto; 
}
span.invalid { 
	color: darkred; 
}
span.valid { 
	color: #167b1d !important; 
}

img.hpbari {
 	margin: -4px 2px;
}

/* Sprites */

.header {
	background: #fefefe !important;
	border-bottom: 1px solid #c7c7c7 !important;
}

.header .disclaimer {
    font-weight: normal;
	color: #555555 !important;
}

.tab {
	background: #f6f6f6 !important;
	box-shadow: inset 0px 1px rgba(255,255,255,0.75);
	border: 1px solid #c7c7c7 !important;
	border-bottom:0px none !important;
}

.active, .tab:hover {
    background: #d2f3d7 !important;
}

.fixbar {
	bottom: 0;
	position: absolute !important;
	width: 100%;
	padding: 5px 0px !important;
	box-shadow: 0px -1px rgba(0,0,0,0.15), inset 0px 1px #ffffff;
    border-top: none !important;
	background: #f2f2f2 !important;
	font-size: 10pt;
	text-align: center;
}

/* Scrollbars */

::-webkit-scrollbar {
    width: 15px;
    height: 15px;
}

::-webkit-scrollbar-corner {
    background-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/Corner%20Piece.png);
}

::-webkit-scrollbar-track {
    margin: -11px;
}

::-webkit-scrollbar:horizontal {
    background-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/H%20Track.png);
    background-repeat: repeat-x;
}

::-webkit-scrollbar-thumb:horizontal {
    -webkit-border-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/H%20Thumb%20Enabled.png) 0 8 0 8;
    border-width: 0 8px;
    min-width: 24px;
}

::-webkit-scrollbar-thumb:horizontal:active {
    -webkit-border-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/H%20Thumb%20Pressed.png) 0 8 0 8;
}

::-webkit-scrollbar-button:horizontal {
    width: 26px;
}

::-webkit-scrollbar-button:horizontal:decrement {
    background-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/Left%20Arrows.png);
}

::-webkit-scrollbar-button:horizontal:decrement:active {
    background-position: -26px 0;
}

::-webkit-scrollbar-button:horizontal:increment {
    background-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/Right%20Arrows.png);
}

::-webkit-scrollbar-button:horizontal:increment:active {
    background-position: -26px 0;
}

::-webkit-scrollbar:vertical {
    background-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/V%20Track.png);
    background-repeat: repeat-y;
}

::-webkit-scrollbar-thumb:vertical {
    -webkit-border-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/V%20Thumb%20Enabled.png) 8 0 8 0;
    border-width: 8px 0;
    min-height: 24px;
}

::-webkit-scrollbar-thumb:vertical:active {
    -webkit-border-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/V%20Thumb%20Pressed.png) 8 0 8 0;
}

::-webkit-scrollbar-button:vertical {
    height: 26px;
}

::-webkit-scrollbar-button:vertical:decrement {
    background-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/Top%20Arrows.png);
}

::-webkit-scrollbar-button:vertical:decrement:active {
    background-position: -15px 0;
}

::-webkit-scrollbar-button:vertical:increment {
    background-image: url(https://dl.dropbox.com/u/900149/Pok%C3%A9check/Bottom%20Arrows.png);
}

::-webkit-scrollbar-button:vertical:increment:active {
    background-position: -15px 0;
}
}
