css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forums.forzamotorsport.net") {

/* ***************************************************** */
/* Cleanup the forzamotorsports.net forums               */
/* v1.1 03-Aug-2012 See change log at userstyles.org     */
/* ***************************************************** */

/* Title, Header, Footer */
.CommonImageTextButton, .CommonImageTextButtonBig {
    padding-top: 2px!important;
    padding-bottom: 2px!important;
}
.CommonTitleTable .titleGamercard {
    padding-bottom: 0px!important;
}
.CommonTopLevelTab,.CommonTabBar,.CommonTopLevelTabBar {
    height: 28px!important;
}
.CommonTitleBarSearchArea {
    padding: 0px 20px 0px 10px!important;
    margin-top: 1px!important;
}
.CommonBreadCrumbArea {
    padding-bottom: 0px!important;
}
.CommonDescription {
    display: none!important;
}
.CommonTitle {
    display: none!important;
/*    font-size: 14px!important;
    padding: 0px 0px 0px 8px!important;
    margin-top: -5px!important;
    text-align: left!important; */
}
.CommonFormField {
    padding: 0px!important;
}

.CommonContent {
    padding: 2px 0px 4px!important;
}
.CommonDescription {
    padding: 1px 2px!important;
    margin-top: 1px!important;
    margin-bottom: 1px!important;
}


/* left "ForumPostUser" panel mods */
/* left panel first container ForumPostUserPropertyList */

.useravatar {       /* the xbl gamercard stuff from ms lives here */
/*  display: none!important; */ /* skip all that user avatar mess */
}

.useravatar iframe {
    height: 85px!important;
}
.ForumPostUserAvatar.CommonPrintHidden { 
    height: 10px!important;
}
.ForumPostUserPropertyList { 
    height: 100px!important;
    text-align: right!important;
    margin: 0px!important;
    overflow: visible!important;
}
.ForumPostUserPropertyList img {
    display: none!important;
}
UL.ForumPostUserPropertyList LI {
    margin-right: 4px!important;
    margin-bottom: 0px!important;
}
.UserProfileLink {
    text-align: center!important;
}


/* The users post is in td's with id ForumPost */

/* Forum Post Title, Header, Attachment, Buttons, Footer  */ 
.ForumPostArea {
    margin-top: 6px!important;
    margin-bottom: 6px!important;
}
.ForumPostHeader {
    padding: 0px 2px 0px 8px!important;
}
.ForumPostTitleArea {
    padding: 0px 4px 0px 4px!important;
}
.ForumPostTitle {
    font-size: 110%!important;
}
.ForumPostAttachment {
    padding: 0px 4px 0px 8px!important;
    margin: 0px!important;
}
.ForumPostButtons {
    margin: 0px 4px 0px 0px!important;
}
.ForumPostFooterArea {
    padding: 0px!important;
    line-height: 5px!important;
}


/* Forum Post Body */
.ForumPostBodyArea {
    padding-top: 0px!important;
    margin-top: 0px!important;
}

.ForumPostBodyArea img {
    max-height: 50px!important;
    width: auto!important;
}

.ForumPostBodyArea img:hover {
    max-height: 400px!important;
}
.ForumPostContentText {
    padding: 0px 4px 0px 8px!important;
    margin-top: 4px!important;
    margin-bottom: 2px!important;
}

.ForumPostContentText p {
    padding-top: 2px!important;
    padding-bottom: 0px!important;
    margin-top: 2px!important;
    margin-bottom: 4px!important;
}

.ForumPostContentText BLOCKQUOTE {
    padding-top: 2px!important;
    padding-bottom: 0px!important;
    margin-top: 0px!important;
    margin-bottom: 4px!important;
}


/*  Fix search result text */
.CommonSearchResult {
    color: rgb(211, 213, 210)!important;
}


/* end forums post area cleanup */
}


/* *************************************** */
/* trim and clean gamercard stuff from ms  */
/* *************************************** */
@-moz-document domain("gamercard.xbox.com") {

.XbcGamercard {
    width: 55px!important;
    height: 85px!important;
}
#Gamerpic {
    width: 55px!important;
    height: 55px!important;
}
.XbcGamercard #Gamertag {
    width: 202px!important;
    height: 20px!important;
    line-height: 17px!important;
}
.RepContainer, .GamerscoreContainer, #PlayedGames  {
    display: none!important;
}

/* keep the gamerpic
.XbcGamercard #Gamerpic {display: none!important;}
*/

/* end gamercard.xbox.com cleanup */
}


/* end end forzamotorsports.net forum  cleanup */
