css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gnumonks.org") {
body, table[border="0"][width="100%"][cellpadding="0"][cellspacing="0"] {
font: 16pt sans-serif !important;
padding: 0 !important;
margin: 0 !important;
}
table[border="0"][width="100%"][cellpadding="0"][cellspacing="0"] tr:first-child, span.pagetitle {
font: 16pt sans-serif !important;
text-align: center !important;
}
td[colspan="6"][bgcolor="#000000"][height="1"]{
display: none; /* this is a pseudo-hr */
}
tr[valign="top"] {
padding: 0 !important;
margin: 0 !important;
}
tr[valign="top"] td:first-child {
display: none; /* this is a pseudo-hr */
}
tr[valign="top"] td[bgcolor="#6699cc"] {
padding: 0 10px 0 30px !important;
margin: 0 !important;
font: 14pt sans-serif !important;
}
tr[valign="top"] td[bgcolor="#6699cc"] p {
background-color: white !important;
}
tr[valign="top"] td[bgcolor="#6699cc"] ul {
padding: 0 10px !important;
margin: 0 !important;
font: 12pt sans-serif !important;
background-color: white !important;
}
tr[valign="top"] td[bgcolor="#6699cc"] ul li {
padding: 0 !important;
margin: 0 !important;
font: 12pt sans-serif !important;
}
td[bgcolor="#dddddd"][width="100%"]{
padding: 12px !important;
margin: 0 !important;
font: 12pt sans-serif !important;
}
}
