css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("quora.com") {
  .signup_cta_on_answer, /* Sign up for free (covering first answer) */
  .signup_bar,           /* Sign up for free (top bar) */
  .text_hider            /* Text hider (prevents selecting text via mouse) */
  {
    display: none;
  }
  /* Blurred answers */
  .blurred_answer_wrapper span.blurred_answer { color: #333; text-shadow: none; }
  /* Blurred answers link fix */
  .blurred_answer_wrapper span.blurred_answer a { color: #19558D; }
}
