css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members{

background:#FFFFFF url(http://static.tumblr.com/8yo5xxv/OtCm76axy/1440x900.jpg) no-repeat center bottom fixed !important

}




#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: transparent !important;

}



#dashboard_edit_post{

background-color: #000000!important

}



#right_column .dashboard_nav_item {

border-bottom:0px solid #eee !important;

background-image:none  !important

}



#right_column .dashboard_nav_item .dashboard_nav_title,

                    #right_column .dashboard_nav_item .dashboard_nav_title a,

                    #right_column .dashboard_nav_item a,

                    #right_column .dashboard_nav_item ul.dashboard_subpages,

                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {

                        color:#fff !important;

}



#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {

                        color:#888 !important ;

                    }



#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {

                        background: none !important;

                    }

#left_column #posts .notification .avatar {

                        border-color:transparent !important;

                        background-color:#a4c5ea!important;

opacity:0.7 !important;

                    }



#left_column #posts .notification .avatar:hover{

opacity:1 !important;

                    }

.post{opacity:0.8 !important; border-bottom:0px !important; color:#333}

.post:hover{opacity:3 !important}

.permalink{background-color: rgba(10,10,10,0.9) !important; background-image:none !important}

.active{background-color:#ffffff!important}

.nav_item_nipple{display:none !important}

.nav_menu{background-color:#7b8fc8!important; border-bottom:0px !important}



#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color:#534296!important;

background-image:none !important;

border:0px !important

}



#nav .nav_item .nav_menu {

border-bottom:0px !important

}



#nav .nav_item .nav_menu a {

color:#a393e3!important;

border-top-color:#a5a6af!important;

}



#nav .nav_item .nav_menu a:hover {

color:#fbfbfb !important;

}



.dashboard_switch_blog_menu_header{

background-color:#1b2424!important;

color:#666 !important;

}

.dashboard_switch_blog_menu_item{

background-color:#fefefe!important;

color:#666 !important;

border:0px !important

}

.dashboard_switch_blog_menu_footer{

background-color:#6a569b!important;

color:#666 !important;

}

.dashboard_switch_blog_menu_wrapper{

background-color:#6a7287!important;

}



#new_post_notice_container{

color:#fff !important

}



#new_post_notice_container a{

color:#fff !important

}



#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{

background-image:none !important

}



#footer{

background: rgba(10,10,10,0.6) !important;

background-image:none !important

}





.new_post_label {-webkit-text-stroke:0 !important;color:rgba(0,0,0,0) !important;opacity:3 !important;top: 0px !important;padding-top: 80px;}.new_post_label:hover {-webkit-text-stroke:0 !important;color:rgba(0,0,0,0) !important;opacity:3 !important;top: 0px !important;padding-top: 80px;}.post.is_mine.with_avatar.new_post {background: tranparent!important ;background-position: center bottom !important;background-repeat:repeat !important;}.new_post_label .new_post_label_icon {background-image: url('http://static.tumblr.com/rodxhpu/2Aam88u5s/icons__1_.png') !important;background-repeat: no-repeat;position: absolute;display: block;width: 76px;height: 90px;top: 8px !important;left: 0px !important;opacity:.90 !important;}.new_post_label:hover .new_post_label_icon:hover{opacity: 1.0 !important;}#new_post .arrow {background: none!important;width: 0px!important;height: 0px!important;border-right: solid 9px rgba(31,31,31,1)!important;border-bottom:9px solid transparent!important;border-left: 2px solid transparent!important;border-top: 9px solid transparent!important;margin-top: 0!important;}


#left_column #posts .notification {

                        background-color:tranparent!important;

                        color:#ffffff!important;

                        border-bottom-color: #7e85a3!important

                    }



#left_column #posts .notification .block {

                            color:transparent!important;

                        }



#left_column #posts .notification.alt {

                        background-color: tranparent!important;

border-bottom-color: #3d384e!important

                    }

#left_column #posts .notification.last_notification,

                    #left_column #posts .notification.single_notification {

                        border-bottom-color:#8e93bf!important;

                    }

#left_column #posts .notification a {

                        color:#c397a7!important;

                        border-bottom:0px !important;

                    }

#left_column #posts .notification blockquote {

                        border-left-color:#eee !important;

                    }



#left_column #posts .notification .avatar {

                        border-color:transparent !important;

                        background-color:#fbfbfb !important;

opacity:0.7 !important;

                    }



#left_column #posts .notification .avatar:hover{

opacity:1 !important;

                    }

.notification_type_icon{display:none !important}



h1,h2,h3,h4,h5,h6{color:#545066!important}



}




#logo {height: 0 !important;















	width: 0 !important;















	padding-left: 230px !important;















	padding-top: 64px !important;















	background: url(http://static.tumblr.com/rodxhpu/XSxm88uc8/logo2.png) no-repeat !important;}
