@-moz-document domain("roblox.com") {
a {
font-weight: normal;
}
#AuthenticationBannerSpan
{
border-radius: 4px !important;
background-image:None !important;
background-color:#D6F0DA !important;
}
.StandardBoxWhite {
position: relative;
top: -1px;
background: #EFEFEF;
border: 1px solid #CCC;
padding: 8px;
color: black;
}
.ProfileAlertPanel {
width: 348px;
padding: 5px 5px 5px 5px;
background-color: #EFEFEF;
border: solid 1px black;
height: 64px;
}
.ProfileAlertPanel {
width: 500px;
padding: 5px 5px 5px 5px;
background-color: #EFEFEF;
border: solid 0px #EFEFEF;
height: 64px;
}
#UserPlaces .PanelFooter, #GroupPlaces .PanelFooter, #Favorites .PanelFooter {
background-color: #EFEFEF;
color: #333;
font-family: Verdana,Sans-Serif;
margin: 0;
padding: 3px;
text-align: center;
}
.searchAlternatingItem {
background: #EFEFEF;
}
.searchItem {
background: #EFEFEF;
}
select {
FONT-FAMILY: Verdana, Arial, Helvetica, sans-serif;
font-size: 0.9em;
font-weight: bold;
background-color: #EFEFEF;
border-color: black;
}
.UserBlurb {
overflow: hidden;
width: 348px;
padding: 5px 5px 5px 5px;
line-height: 20px;
background-color: transparent;
}

#MasterContainer
{
background:url(http://www2.2ch.net/ba.gif) !important;
}

th
{
background:#A6A6A6 !important;
}

#BodyWrapper {
background: transparent !important;
background-attachment: scroll;
background-clip: border-box;
background-color: transparent;
}

body {
background:#EFEFEF !important; 
background-image: url(http://www2.2ch.net/ba.gif) !important;
}
#AdvertisingLeaderboard
{
display:none !important;
}
.forumRowHighlight
{
background:#EFEFEF !important;
}
.forumHeaderBackgroundAlternate
{
background:#EFEFEF !important;
}
.tableHeaderText
{
background:#A6A6A6 !important;
padding: 0px !important;
}
.forumAlternate
{
background:#EFEFEF !important;
}
.forumRow
{
background:#EFEFEF !important;
}

.tableBorder
{
border: 1px black solid !important;
background:#EFEFEF !important;
box-shadow: 0pt 1px 10px rgba(0, 0, 0, 0.4) !important; /*Shadows! S-so cool ;_;*/
}
.GridHeader
{
Background:#777777 !important;
}
.Navigation li:hover
{
background:#444444 !important;
}
#SmallHeaderContainer #Banner {
background-color: #444444;
}
a.btn-logo, a.btn-logo:visited {
background: none !important;
display: inline-block;
width: 123px;
height: 38px;
float: left;
}
#HeaderContainer {
	    background: #444444 !important;
	    height: 1px !important;
	    margin: 0 !important;
	    padding: 0 !important;
	    }
Header .Navigation {
width: 900px;
height: 30px;
background: url(http://www.roblox.com/images/cssspecific/rbx2/topNav_blue.png);
}
#Body
{
background: #EFEFEF !important;
}
.StandardBox
{
background:#EFEFEF !important;
}
.Navigation
{
background:#444444 !important;
border-top:1px dashed #ffffff;
}
.Repeater .AlternatingItemTemplateEven {
background: #EFEFEF;
padding: 5px 5px 5px 5px;
}
#Footer {
background: #EFEFEF;
text-align: center;
font-size: 0px;
color: #EFEFEF;
}
.ParentsSection
{
border:2px ridge #ffffff !important;
background:#333333 !important;
color:#ffffff !important;
}
.GridPager
{
background:#777777 !important;
}
.page
{
background-image:None !important;
background:#EFEFEF !important;
}
.DescriptionText
{
border:2px ridge #ffffff !important;
background:#333333 !important;
color:#FFFFFF !important;
}
div#Banner.BannerRedesign {
background: none !important;
background-color: rgb(25, 25, 25) !important;
height: 36px !important;
position: static !important;
}
div.mySubmenuFixed.Redesign {
position: static !important;
top: 35px !important;
}
/*making things that shouldn't exist no longer exist.*/
div.SystemAlert {
display:none
}
div.forceSpaceUnderSubmenu, div.forceSpace
/*there are many great mysteries to the universe
one of them is "Why did Roblox impliment this?"*/ {
display:none;
}
div.HeaderDivider {
display:none
}
}
