css:
@-moz-document domain("google.com") {



.hI, .ig, .iv, .hF, /* message headers */
.aju, /* div around the author image */
.gA.gt.ac5, /* the reply area */
.TI .T-I-ax7, .z0 .T-I-ax7, .G-atb .T-I-ax7, .T-I-ax7, /* buttons */
.T-I-KE, /* Compose, New Contact - white text needs fixing */ 
#gbqfb /* search button */ {

background-image: -moz-linear-gradient(top,  rgba(0,0,0,0.08) 0%, rgba(0,0,0,0.04) 44%, rgba(0,0,0,0) 100%) !important; /* FF3.6+ */
background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0.08)), color-stop(44%,rgba(0,0,0,0.04)), color-stop(100%,rgba(0,0,0,0))); /* Chrome,Safari4+ */
background-image: -webkit-linear-gradient(top,  rgba(0,0,0,0.08) 0%,rgba(0,0,0,0.04) 44%,rgba(0,0,0,0) 100%); /* Chrome10+,Safari5.1+ */
background-image: -o-linear-gradient(top,  rgba(0,0,0,0.08) 0%,rgba(0,0,0,0.04) 44%,rgba(0,0,0,0) 100%); /* Opera 11.10+ */
background-image: -ms-linear-gradient(top,  rgba(0,0,0,0.08) 0%,rgba(0,0,0,0.04) 44%,rgba(0,0,0,0) 100%); /* IE10+ */
background-image: linear-gradient(to bottom,  rgba(0,0,0,0.08) 0%,rgba(0,0,0,0.04) 44%,rgba(0,0,0,0) 100%); /* W3C */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#14000000', endColorstr='#00000000',GradientType=0 ); /* IE6-9 */

}


/* Adjust padding around the image for the person */
.aju {
  padding-left: 4px !important;
  padding-bottom: 2px !important;
}
.adf.ads, .adn.ads {
  padding-left: 0 !important;
}

/* image with hangout icon */
.TI .TJ {
  xmargin-top: -4px !important;
  height: 100% !important;
  width: auto !important;
}


}
