css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"); 



/* Urlbar and searchbar */

#urlbar,

.searchbar-textbox {

  border-radius: 0px 10px !important;

}



/* Squared tabs */

.tabbrowser-tab, .tabs-newtab-button {

  -moz-border-image: url("data:image/png;base64,") 4 3 2 3 / 0px 0px 0px 0px repeat stretch !important;

  border-radius: 0px 10px 0px 10px !important; }
  
.tabbrowser-tab, .tabs-newtab-button { 
  
  margin-bottom: 2px !important; }





/*More squared button style*/

#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button,

#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker,

#nav-bar .toolbarbutton-1 {

   border-radius: 0px !important;

}

 

/* Make sure the back button stays rounded in large icon mode

   (change to 0 to create a squared back button but beware - it really looks ugly */

#navigator-toolbox[iconsize="large"][mode="icons"] > #nav-bar #back-button { border-radius: 10000px 10000px 10000px 10000px !important; }



/* reduce the margins by 1px */

#nav-bar > .toolbarbutton-1 {

  margin-left:2px !important;

  margin-right:2px !important;

}
