css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
Global Black On White v1.0-1  by Gabriel Sharp(osirisgothra@hotmail.com)
http://paradisim.uuuq.com
http://userstyles.org/users/87204
*/

@-moz-document url-prefix("http://")
{

BODY 
{
background-color: black !important;
}

BODY *, body form *
{
background-color: transparent !important;
text-overflow: hidden !important;
color: #999 !important;
border-radius: 4px !important;
border-color: transparent !important;
-moz-appearance: none !important;
background-image: none !important;
font-family: "Terminal" !important;
font-size: 9pt !important;
font-weight: 400 !important;
}


img:hover
{
background-color: transparent !important;
opacity: 1.0 !important;
}

h1,h2,h3,h4,h5
{
background-color: transparent !important;
text-decoration: underline !important;
}

h5,h4
{
background-color: transparent !important;
color: #aaa !important;
}

h3
{
background-color: transparent !important;
color: #ccc !important;
}

h2
{
color: #ddd !important;
}

h1
{
color: white !important;
font-weight: bold !important;
text-decoration: none !important;
background-color: transparent !important;
}

a
{
text-decoration: none !important;
color: white !important;
background-color: transparent !important;
border-width: 0px !important;
}

a:hover
{
border-width: 1px !important;
border-radius: 4px !important;
color: black !important;
text-shadow: #444 0 0 1px !important;
}
a:visited
{
background-color: transparent !important;
color: #FFD9FF !important;
}

form *
{
background-color: transparent !important;
color: white !important;
}

form>div>button
{
background-color: #222 !important;
color: red !important;
}

img, img *, body>div>div>a
{
opacity: 0.50 !important;
border-radius: 4px !important;
margin: 6px !important;
}

}
