css:
@-moz-document domain("reddit.com"), domain("www.reddit.com") {
.score, .score.likes, .score.dislikes, .midcol.likes, .midcol.dislikes, .midcol.unvoted, .score.unvoted, .arrow.down, .arrow.up, .arrow.downmod, .arrow.upmod, .score, .downvotes, .upvotes, span .userkarma, .titlebox span.karma, span.word, span.number {
     display: none;
     }
.rank {
     margin-right: 15px;
    }
span.user a, span.user a:-webkit-any-link {
     visibility: visible !important;
     font-size: 11px !important;
     line-height: 12px !important;
     padding-bottom: -1px !important;
     }
span.user {
     visibility: hidden;
     font-size: -0px;
     }
}

@-moz-document regexp("www\\.reddit.com\/user/") {
.titlebox h1, span.fancy-toggle-button, div.bottom, div.giftgold {
     visibility: visible !important;
     font-size: 11px !important;
     line-height: 12px !important;
     }
.titlebox h1 {
     font-size: 19px !important;
     }
.titlebox {
     visibility: hidden;
     font-size: 0px;
     }
}
