css:
@-moz-document domain("youtube.com") {
  /* ========== AdKiller ========== */
  .ad-div,
  #google_companion_ad_div,
  #watch-video-extra,
  #watch-channel-brand-div
  {
    display: none !important;
  }
  .yt-uix-expander-collapsed #watch-description-clip {
    position: static !important;
  }

  /* ========== YoutubePlayer ========== */
  /*  Allways active 'watch-wide' class */
  #watch-video-container {
    background-color: transparent !important;
    background-image: none !important;
  }
  #watch-video {
    background-color: #171717 !important;
    background-image: -moz-linear-gradient(top,#111,#171717) !important;
    box-shadow: inset 0 0 12px;
    border-radius: 4px;
  }
  #watch-player {
    margin: 0 auto !important;
    height: 520px !important;
    width: 960px !important;
/*    height: 570px !important;
    width: 960px !important;*/
  }
  #watch-video.wide,
  #watch-video.large {
    width: 1280px !important;
  }
  #watch-video.wide #watch-player,
  #watch-video.large #watch-player {
    height: 748px !important;
    width: 1280px !important;
  }
  /*More videos from user button*/
  #watch-more-from-user{
  margin: 0 auto !important;
  width:980px !important;
  }
  /*MINE*/
  #ytmp3procom{ margin-left:10px !important;}
  #content-container{ margin-top:5px !important;}
  #watch-video-container, #watch-main-container, #footer-container, .yt-horizontal-rule span, #page, html{ background:#FFFFFF !important;}
  #eow-title{color:#FFFFFF !important;}
  #watch-headline-title{ background-color:#171717 !important; padding:3px 5px 3px 5px !important;}
  #watch-headline-container{ margin-top:7px !important;}
  #watch-headline-user-info{ margin-top:10px !important;}
  /*Space between like-unlike and neighbors*/
  #watch-like-unlike{ margin-left:25px !important; margin-right:25px !important;}

  /* ========== watch-sidebar ========== */
   /*Hide the "More from youtube" area*/
    #watch-context {display:none;}
    /*Add the scroll to the suggested videos, also reduces the height to match with the video and limits the number of suggested videos*/
    #watch-sidebar {height:450px; overflow:auto;}
    #watch-related > .video-list-item:nth-child(n+9) {display:none !important}
  #watch-sidebar {
    margin-top: 0 !important;
    padding-top: 10px !important;
  }
}
