css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix("http://live.nicovideo.jp/") {

/*-------------------------------------------------*/

 .content_312 
,#web_pc_footer_box 
,.ads_468 
,#google_adsense
,.mb16p4 
,.ads_tag 
,#nicoads 
,.foot_pagetop 
,#des_1 
,.btn_info_close 
,.content_360 
,.mb8p4 
,a[href*="premium"] 
,tr[valign="bottom"]
	{
	display: none !important;
	}

/*
.ichiba div:nth-child(5)
	{
	display: none !important;
	}
*/


#watch_footer_ox_ad
	{
	display: none !important;
	}

#des_2
	{
	display: block !important;
	visibility: visible !important;
	}


.siteHeaderAva {
    background : #333 !important;
}


}

.search_word{
    background-color : #333;
    border: 1px solid #111 !important;
}

.search_word{
    background-color : #CCC;
    border: 1px solid #BBB !important;
}


@-moz-document url-prefix("http://live.nicovideo.jp/watch/") {

/*-------------------------------------------------*/


#live_header ,#page_header {
    background : #666 !important;   
}

div#live_header > div > h1#logo a {
    height : 59px !important;
}

#watch_title_box {
    border-top : 0px !important;
}


#search_target {
    background : #555 !important;
}


.submit {
    background : #333 !important;
    background-image : none !important;
}

.submit:before {
    font-size: 60% !important;
    content: "S" !important;
}



.tag_title {
    display: none !important;
}


.cover {
    margin-left : 2px !important;
}


#watch_title_box *, .footerInner * {
    text-decoration : none !important;
    color : #CCC !important;
}


#watch_player_top_box *, .utilty_menu * {
    text-decoration : none !important;
    color : #666 !important;
}

#advertisement_box * {
    text-decoration : none !important;
}



.provider_icon {
    display: none !important;
}

.commu_name, .ch_name {
    padding-left : 12px !important;
}


#watch_niconicoukoku_open_btn {
    /*background : none !important; */
    background : #333 !important;
    border : 1px solid #444 !important;
}


#watch_niconicoukoku_open_btn:after {
    font-size: 80%;
    color : #555;
    content: " Ad";
    position : absolute;
    left : 10px;
    top : 20px;
    text-align : center !important;
    vertical-align : middle !important;
    
    /*border : 1px solid #333 !important;*/
}


#watch_niconicoukoku_open {
    background : #CCC !important;
}

#watch_niconicoukoku_open * {
    color : #666 !important;
}

/*
#tab li h3 {
    -webkit-border-top-left-radius:10px;
    -webkit-border-top-right-radius:10px;
    border : 1px !important;
}
*/


#footer_ads {
    display: none !important;
}

.search_word{
    background-color : #555;
    border: 1px solid #555555 !important;
}


}



@-moz-document url-prefix("http://live.nicovideo.jp/recent") {

/*-------------------------------------------------*/

body,
.nofix,
#page_header,
#page_cover,
#live_header,
#page_cover,
#wrapper,
.bgUp,
.bgUnder {
    background : #F2F2F2 !important;   
}

#right {
    background : #F8F8F8 !important;
    padding-left :10px;
    padding-right :10px;
}

div#live_header > div > h1#logo a {
    height : 59px !important;
}

#taglist {
    background : #F8F8F8 !important;
    
}

.tagBox {
}

/*
div + .tag + .spriteIcon {
    display: none !important;
}
*/

#taglist > .tag {
    display: none !important;
}


.audience, .coment {
    background : #F8F8F8 !important;
    padding-left : 0 !important;
    -webkit-border-radius: 4px;
}



.audience:after {
    font-size: 60%;
    content: " 人";
}

.coment:after {
    font-size: 60%;
    content: " コメ";
}


.userBox .time, .txt .time{
    color : #CCC !important;
    background : #333 !important;
    /*border-bottom:3px solid #333 !important;*/
    
    border-bottom:0px !important;
    
    /*border-right:1px solid #000 !important;*/
    
    left : 0 !important;
    
    vertical-align : middle !important;
    padding-top : 3px !important;
    font-weight: 600 !important;
    
    -webkit-box-shadow: 1px 1px 2px #999;

}


.userBox {
    /*padding-top : 8px !important;*/
    /*padding-bottom : 10px !important;*/
    margin-bottom : 25px !important;
}

#pullDownSwitch {
    margin-bottom : 15px !important;
}

/*
#pullDownSwitch > .lvline {
    padding : 0 !important;
}
*/


#userFrame div.userBox img{
    border : none !important;
    -webkit-border-radius: 3px;
    background : none !important;
}



/*
.tagMember {
    background-position: -670px -10px !important;
}
*/



#pagerFrame {
    padding-top : 20px ;
}

#pickupFrame  {
    padding-top : 30px;
}


.tagBox > nobr > a {
    color : #666 !important;
}

.search_word{
    background-color : #CCC;
    border: 1px solid #BBB !important;
}


}
