/*Clear DPStream
Version 4*/

/*suppression de pub*/
@-moz-document url("http://www.dpstream.net/"), url("http://dpstream.net/"), url('http://www.dpstream.net/?vue_serie=non'), url('http://dpstream.net/?vue_serie=non'), url('http://www.dpstream.net/?vue_serie=oui'), url('http://dpstream.net/?vue_serie=oui'){
iframe{display:none!important;}
}

@-moz-document url("http://dpstream.net/film.html"), url("http://www.dpstream.net/film.html"), url-prefix("http://www.dpstream.net/film.html#"), url-prefix("http://dpstream.net/film.html#"){
center > center > iframe{display:none!important;}
div.ef{background:grey!important;}
td#ty12,td#ty6{margin-left:15px!important;}
td#ty13,td#ty7{width:25%!important;margin-left:10px!important;}
td#ty11{margin-left:20px!important}
td#ty5{margin-left:12px!important;}
td#ty1,td#ty8{margin-left:15px!important}
td#ty2,td#ty1,td#ty3,td#ty4,td#ty5,td#ty6,td#ty7{text-align:center!important;}
td#ty3,td#ty10{margin-left:10px!important;}

}

@-moz-document url-prefix("http://www.dpstream.net"), url-prefix("http://dpstream.net") {

[id="top"] iframe{display:none!important;}
body > table > tbody {background:white!important;}
[class="raf2"] div > a{display:none!important;}
table.table_fond{padding-bottom:0!important;}
/*espacements*/
div.rml1,div.rml2{margin-bottom:10px!important;}

/*colore le fond de certains liens en blanc quand on y passe la souris*/
a.top_a:hover, a.t:hover, a.lienfilm:hover, a.msg:hover{background:white!important;}

/*modifie le "menu coeur"*/
div#menu_accueil0{
background:#fff!important;
border:none!important;
box-shadow:0 0 4px 0px!important;}
[id="menu_accueil0"] hr{display:none!important;}

[class="rmt"] img{box-shadow:none!important;}

/*supprime un morceau noir transparent*/
[class="rr1"] div.tgg{background:none!important;}

/*ajoute des ombres*/
[class="rr1"] span{text-shadow:blue 1px 1px 1px !important;}

/*espacement des "top"*/
div.raccueil4 {
padding: 20px 0px !important;}
div.raccueil6 {
padding: 20px 0px !important;}

/*définit la couleur du texte*/
b,a.b,i {color: #a5acb1 !important;}
a {color: #a5acb1 !important;}
a.classepi1{background-color:white!important;}

/*définit l'image de fond*/
body {
background-image: url(http://konachan.com/image/71b8004b7395664d50fff1b637c50f52/Konachan.com%20-%20137680%20animal%20bou_nin%20cat%20flowers%20original%20scenic%20sky.jpg)!important;
background-color:#000000 !important;
background-attachment:fixed;
background-size:100%!important;
}

/*supprime l'arvertissement -16 ans*/
div#decon{display:none !important;}

/*modifie la couleur des barres de recherche*/
input {
background: #f2f5f7 !important;
border: none !important;
}

/*supprime les étoiles pas jolies des notes des films*/
a.b,td {
background: rgba(0, 0, 0, 0) !important;
}

/*remplace le logo "dpstream"*/
div#top {
background-image: url(http://img21.imageshack.us/img21/1254/dpstream.png) !important;
}

/*suppression de traits noirs gênants*/
.haut, .bas {
 display:none;
}
.tah th, th.top_menu_droite, th.top_menu_gauche {
 border:none !important;
 background: rgba(0, 0, 0, 0.0)
}
.tgg, .tggm, .tah, .b3, h1, #conteneur {
 border-left:0 !important;
}
.tgg, .tggm, .tah, .b3, h1, #conteneur {
 border-right:0 !important;
}
h3, h3.tgg {
 border-bottom:none !important;
}

/*espacement de la barre en haut*/
div.rmt {
padding: 6px !important;}

/*trucs très utiles que j'ai la flemme d'expliquer*/

#top a:hover {
 background:transparent !important;
}

a {
 -webkit-transition: all 150ms ease;
 -moz-transition: all 150ms ease;
 -o-transition: all 150ms ease;
}

.img-syno, .ras6 img {
 border:none;
 border-radius: 10px;
 box-shadow: inset 0 1px 5px rgba(0, 0, 0, .5), 0 1px 0 rgba(255, 255, 255, .3), 0 -1px 0 rgba(0, 0, 0, .6);
}

/*colore les textes en orange au passage de la souris*/
a:hover {
 color:orange !important;
}

/*espacements*/
#top {
 margin-bottom:0px !important;
}

/*alignements*/
td {
text-align:justify!important;
}

/*colore des titres en orange*/
.rmt a, h3.tgg, h3.tgg a {
 color:#ff5e10 !important;
}

span {
 color: orange !important;
 }

/*change la couleur de l'ensemble en blanc au lieu de gris foncé*/
#conteneur, .tggm, table.table_fond {
 background:#ffffff !important;
}

/*crée une ombre et un arrondi à l'ensemble*/
#conteneur {
 border-radius:20px;
 box-shadow: #979797 0px 0px 7px;
}

/*supprime certaines bordures*/
div.tgg {
border: none !important;
}

/*modifie le bas de la page, le rend plus joli*/
#pied {
 background:#1d1d1d;
 width:100% !important;
 margin:10px auto 0 !important;
 box-shadow: #000000 0px 0px 3px;
}
#pied .piedpage {
 float:none !important;
 display:inline-block !important;
 border:none!important;
 margin:10px!important;
}

/*ajoute des ombres aux images et videos*/
embed {
box-shadow: #000000 0px 0px 3px;
}
}

