/*

 * Diandian Dashboard - Frosted Custom Theme

 *

 * Author      : Luffycheung

 * Updated     : December 26th, 2012

 * Description : Optimize tag page,block the google ads.Logo/Cursor changed.


 */


@-moz-document url-prefix("http://www.diandian.com/") {


body, a, a:hover {cursor: url(http://srcqn.qiniudn.com/Cursor.PNG), progress !important;}
#content-holder ,.feed .link-to-post-holder ,#header-holder,a.nav-item,ul#nav-blog-list li.active a.blog-url,.misc-action .active { background: #89D0E9 url(http://srcqn.qiniudn.com/default_bg.PNG) center left  repeat fixed !important; z-index:0;
}
#content{
position: absolute;

background:  url(http://srcqn.qiniudn.com/E023F.png) repeat !important;
min-height: 70%;
min-width: 895px;}

#aside .sidebox ,a.followed-tag.clearfix span ,.sidemenu li.sub a,.sidemenu li .sidemenu-item-extra ,.tag-nav li a, .tag-nav li a:link, .tag-nav li a:visited,#permalink-holder .permallink input,.jp-seek-bar.playing-mask-i ,.aside-editor-title ,.radarpro-item-name a,#side-search-input.input-tip{color:rgb(134, 0, 0) !important;}

#pb-main,div.l{
 background:  url(http://srcqn.qiniudn.com/E023F.png) repeat !important;
}
  
#header-holder{


background-image: url(http://srcqn.qiniudn.com/header_title_bg.PNG) !important;

}


ul#nav-blog-list li.active a.blog-url{filter: alpha(opacity=0) ;
-webkit-transition: opacity .5s ease-out ;
-moz-transition: opacity .5 ease-out ;

background-image: url(http://srcqn.qiniudn.com/header_title_bg1.PNG) !important;
}

#nav-index,#aside-promotion a ,div.aside-op-desc,div.sidebox ins,div.pb-share-tip{display:none !important;}

#logo a {
height: 85px !important;
width: 105px !important;
margin-left: 2px;

background: url('http://srcqn.qiniudn.com/Totoro.GIF')   no-repeat -9px 5px !important;}
a.header-pop-notice.large-pop-notice.blog-pop-notice.themegarden-pop-notice{
display:none;}


.feed-bd .feed-rt, .feed-bd .feed-cmt, .feed-bd .feed-del, .feed-bd .feed-edit, .feed-bd .feed-nt, .feed-bd .feed-block, .feed-bd .feed-report, .feed-bd .editor-promote-cancel, .feed-bd .editor-warning, .feed-bd .editor-black, .feed-bd .editor-black-top10,a.feed-top, .feed-bd .editor-unblack-top10 {
height: 18px;
line-height: 18px;
border-bottom: 1px solid
#1E3B50;
background:
#144668;
background: -moz-linear-gradient(top,
#235B7B,
#144465);
background: -webkit-gradient(linear, 0 0, 0 bottom, from(
#235B7B), to(
#144465));
background: -o-linear-gradient(top,
#235B7B,
#144465);
border-radius: 2px;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
-o-border-radius: 2px;
padding: 0 5px;
box-shadow: 0px 1px 2px
#CCC;
-o-box-shadow: 1px 1px 1px #CCC;
-webkit-box-shadow: 1px 1px 1px
#CCC;
-moz-box-shadow: 1px 1px 1px #CCC;
color:
white;}

#content-holder {
    background: url('http://srcqn.qiniudn.com/Shimakaze.png')no-repeat fixed 0px 60px  !important;
    padding-left: 33px;
}

#main {
    padding-left: 11px;
}

#content {
    padding-bottom: 20px;
    padding-left: 0;
    padding-right: 10px;
    padding-top: 20px;
}

#tag-go-latest-holder a,#tag-sub-nav {
    color: rgb(134, 0, 0) ;
    position: fixed;
    right: 0;
    top: 40%;
    width: 200px;
    height: 175px;
    background: url("http://srcqn.qiniudn.com/totalview.png") no-repeat scroll left top 28px  !important;
}

#apply-editor a {
    color: rgb(134, 0, 0) ;
    position: fixed;
    right: 90px;
    top: 70%;
}

#tag-sub-nav {
    padding-bottom: 9px;
}
div#content .pb-main-bottom .r，div#content .pb-main-top .r {
    background: url(http://srcqn.qiniudn.com/E023F.png) repeat-y !important;
    _background: url(http://srcqn.qiniudn.com/E023F.png) repeat-y !important;
}


ol#feed-list .feed-avatar{ border-bottom: 0px solid #0A2939; }

#main  .publisher .pb-avatar a.blog-avatar {-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important; }
#main  #feed-list .feed .feed-avatar  a.blog-avatar {-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important; }

}
