@-moz-document url("chrome://speeddial/content/speeddial.xul"){

#speeddialWindow {
  /*background-color:#3E3E3E !important;*/
  background-color:#f1f1f1 !important;
}

#speeddialWindow * tabpanel {
  color:#fff!important;
}

.sd-tabpanel {
 padding:0px !important;
 background-color:#0AD;
/*background-color:#3f3131 !important;*/ 
 box-shadow:
 0px -2px 5px 0px rgba(0,0,0,0.2),
 inset 0px 1px 3px -2px rgba(255,255,255,1) !important;
 border-bottom:solid #505050 5px !important;
}

.sd-tabpanel:after{
 position:fixed !important;
 top:3em !important;
 right:0.7em!important;
 height:27px !important;
 width:92px !important;
 content:" ";
 background:url("data:image/svg+xml;base64,") !important;
 background-repeat:no-repeat !important;
}

.sd-tabpanel:before {
 position:fixed !important;
 bottom:0.5em;
 right:0.7em;
 content:"SpeedDial by Josep del Rio. http://speeddial.uworks.net/";
 font-family:Times, Times New Roman, serif;
 color:rgba(0,0,0,0.7);
}

/*-----------------------------------------------------------*/

.sd-tabs {
 height:28px !important;
 margin:0px !important;
 padding:0px !important;
 background-color:#505050 !important;
 background:-moz-linear-gradient(top,rgba(0,0,0,0),rgba(0,20,40,0.3)) #505050 !important;
 background:linear-gradient(top,rgba(0,0,0,0),rgba(0,20,40,0.3)) #505050 !important; 
 border-bottom:solid 3px #0AC !important;
}

.sd-tabs-right,
.sd-tabs-left {
 border:none !important 
}

.sd-tab {
 font-size:0.8em !important;
 padding:6px 5px 0px 5px !important;
 margin:0px !important;
 border:solid 0px #000 !important;
 background-color:transparent !important;
 color:#eee !important;
-moz-transition:background-color 0.5s;  
}

.sd-tab:hover, 
.sd-tab[selected="true"] {
 background-color:#606060 !important;
 color:rgba(255,255,255,1.0) !important;
-moz-transition:background-color 0.2s;
}

.sd-tab[selected="true"] {
 /*border-bottom:solid 1px #F16529 !important;*/
 border-bottom:solid 0px #ff5000 !important;
 background-color:#0AC;
 background:-moz-linear-gradient(bottom,rgba(0,0,0,0.2),rgba(0,0,0,0)) #0AC !important;
 background:linear-gradient(bottom,rgba(0,0,0,0.2),rgba(0,0,0,0)) #0AC !important;
 opacity:1.0;
-moz-transition:background-color 0.5s; 
}

/*[[StackEffect]]*/

/*-----------------------------------------------------------*/

.speeddial-container {
 margin:0px !important;
 border:solid 1px #FFF !important;
 border-top-width:0px !important;
}

.speeddialThumb {
 position:relative !important;
 top:0px !important;
 left:0px !important;
 margin:0px !important;
 border:1px rgba(0,0,0,0.2) solid;
 padding:2px;
 background:#FFF !important;
 z-index:100 !important;
-moz-transition:
 -moz-transform 0.15s ease-in-out, 
  box-shadow 0.4s,
  border-color 0.2s 
 !important;
}

.speeddialThumb[assigned="t"]{
 margin:-3px 0px 0px 0px !important;
 border-radius:3px 3px 0px 0px !important;
}

row:first-child .speeddialThumb{
 margin:0px !important;
}
.speeddialThumb[assigned="t"]:hover {
/*  height:40em !important;*/
 border-color:#0AF;
 box-shadow:0px 5px 15px rgba(41,41,61,1) !important;
-moz-transform:scale(1.02,1.02) !important;
 z-index:200 !important;
}

row:first-child .speeddialThumb[assigned="t"]:hover{
-moz-transform:
  translate(0px,-5px) 
  scale(1.02,1.02) !important;
}

row:last-child .speeddialThumb[assigned="t"]:hover{
 box-shadow:0px -5px 15px rgba(41,41,61,1) !important;
-moz-transform:
  translate(0px,5px) 
  scale(1.02,1.02) !important;
}

.speeddialThumb[assigned="t"]:after {
 position:absolute;
 content:" " !important;
 top:27px;
 left:0px;
 right:0px;
 bottom:0px;
 box-shadow:inset 0px -4px 4px -2px #333 !important;
 background:transparent;
 background:-moz-linear-gradient(bottom, rgba(0,10,20,0.3), rgba(200,230,255,0) 20%) !important;
 background:-linear-gradient(bottom, rgba(0,10,20,0.3), rgba(200,230,255,0) 20%) !important;
-moz-transition:box-shadow 0.5s !important;
}

row:nth-child(1) .speeddialThumb[assigned="t"]:after {
 background:rgba(0,0,0,0.25) !important;
 background:-moz-linear-gradient(bottom,rgba(0,10,20,0.3),rgba(0,0,0,0.25) 20%) !important;
 background:linear-gradient(bottom,rgba(0,10,20,0.3),rgba(0,0,0,0.25) 20%) !important;
}

row:nth-child(2) .speeddialThumb[assigned="t"]:after {
 background:rgba(0,0,0,0.2) !important;
 background:-moz-linear-gradient(bottom,rgba(0,10,20,0.3),rgba(0,0,0,0.2) 20%) !important;
 background:linear-gradient(bottom,rgba(0,10,20,0.3),rgba(0,0,0,0.2) 20%) !important;
}

row:nth-child(3) .speeddialThumb[assigned="t"]:after {
 background:rgba(0,0,0,0.1) !important;
 background:-moz-linear-gradient(bottom,rgba(0,10,20,0.3),rgba(0,0,0,0.1) 20%) !important;
 background:linear-gradient(bottom,rgba(0,10,20,0.3),rgba(0,0,0,0.1) 20%) !important;
}

row:nth-child(4) .speeddialThumb[assigned="t"]:after {
 background:rgba(0,0,0,0.05) !important;
 background:-moz-linear-gradient(bottom,rgba(0,10,20,0.3),rgba(0,0,0,0.05) 20%) !important;
 background:linear-gradient(bottom,rgba(0,10,20,0.3),rgba(0,0,0,0.05) 20%) !important;
}

row:last-child .speeddialThumb[assigned="t"]:after {
 background:transparent !important;
 background:-moz-linear-gradient(bottom,rgba(0,10,20,0.3),rgba(200,230,255,0) 20%) !important;
 background:linear-gradient(bottom,rgba(0,10,20,0.3),rgba(200,230,255,0) 20%) !important; 
}

row .speeddialThumb[assigned="t"]:hover:after {
 background:transparent !important;
 cursor:pointer!important;
}

.speeddial-top-left,
.speeddial-top-right,
.speeddial-top-middle {
 width:1px !important;
 height:auto !important;
 background:#FFF !important;
 background:-moz-linear-gradient(bottom, #D0D0D0, #EAEAEA 15%, #FFF) !important;  
 background:linear-gradient(bottom, #D0D0D0, #EAEAEA 15%, #FFF) !important;   
 margin:0px !important;
 text-shadow:
   0px 1px 1px #fff !important;
 text-align: center;
 color:#555 !important;
 border-bottom:solid 1px #C0C0C0 !important;
}

.speeddial-top-left {
 width:1px !important;
 border-right:dashed 1px #CACACA;
 box-shadow:-1px 0px 1px #CCC;
}

.speeddial-top-right {
 width:1px !important;
 border-left:dashed 1px #CACACA;
 box-shadow:-1px 0px 1px #CCC;
}

.speeddialThumb[assigned="t"]:hover .speeddial-top-middle {
 background:#FFF !important;
}

/*-----------------------------------------------------------*/

.speeddialThumb[assigned="f"] {
 background:transparent!important;
 background:-moz-linear-gradient(bottom, rgba(0,0,0,0.2), rgba(0,0,0,0) 60%) !important; 
 background:linear-gradient(bottom, rgba(0,0,0,0.2), rgba(0,0,0,0) 60%) !important;  
 box-shadow:none;
 opacity:0.2 !important;
-moz-transition:opacity 0s, box-shadow 0s !important;
 transition:opacity 0s, box-shadow 0s !important;
}

.speeddialThumb[assigned="f"] * {
 visibility:hidden;
}

.speeddialThumb[assigned="f"]:hover {
 background:rgba(0,0,0,0.2)!important;
 background:-moz-linear-gradient(bottom, rgba(0,0,0,0.2), rgba(100,100,100,0.05) 60%) !important;
 background:linear-gradient(bottom, rgba(0,0,0,0.2), rgba(100,100,100,0.05) 60%) !important; 
 box-shadow:inset 0px -10px 20px -10px #333;
 border-color:transparent !important;
 opacity:1.0 !important;
-moz-transition:opacity 8s 2s, box-shadow 8s 2s !important;
 transition:opacity 8s 2s, box-shadow 8s 2s !important;
}

hbox[hideUnused="true"] .speeddialThumb[assigned="f"] {
 opacity:0 !important;
-moz-transition:opacity 0s !important;
 transition:opacity 0s !important;
}

hbox[hideUnused="true"] .speeddialThumb[assigned="f"]:hover {
 opacity:0.2 !important;
-moz-transition:opacity 15s 5s ease-in !important;
 transition:opacity 15s 5s ease-in !important;
}

.speeddialThumb[assigned="f"] .speeddial-container {
 border-radius:4px !important;
}

.speeddial-middle-left,
.speeddial-middle-middle,
.speeddial-middle-right,
.speeddial-top-middle img,
.speeddialThumb[assigned="f"] .speeddial-top-middle {
 display:none !important;
}

}

