css:
@-moz-document url("http://veoday.com/") {
#top {
background:url(http://www.allthefunin1.com/resources/farmer_riding_tractor_hg_clr.gif) 140% 220px no-repeat;
-webkit-animation:shave 45s infinite 5s;-moz-animation:shave 45s infinite 5s;-ms-animation:shave 45s infinite 5s;animation:shave 45s infinite 5s;
}
.container > .logo,
.container > .baseline,
.container > h1 {position:relative;-webkit-animation:oops 45s infinite 5s;-moz-animation:oops 45s infinite 5s;-ms-animation:oops 45s infinite 5s;animation:oops 45s infinite 5s;}
@-webkit-keyframes shave {
	0%   { background-position:140% 220px; }
	100% {background-position:-100% 220px;}
}
@-moz-keyframes shave {
	0%   { background-position:140% 220px; }
	100% {background-position:-100% 220px;}
}
@-ms-keyframes shave {
	0%   { background-position:140% 220px; }
	100% {background-position:-100% 220px;}
}
@keyframes shave {
	0%   { background-position:140% 220px; }
	100% {background-position:-100% 220px;}
}

@-webkit-keyframes oops {
	13% {top:0px;}
        14% {top:-90px;}
        33% {top:-90px;}
        34% {top:0px;}
       100% {top:0px;} 
}
@-moz-keyframes oops {
	13% {top:0px;}
        14% {top:-90px;}
        33% {top:-90px;}
        34% {top:0px;}
       100% {top:0px;} 
}
@-ms-keyframes oops {
	13% {top:0px;}
        14% {top:-90px;}
        33% {top:-90px;}
        34% {top:0px;}
       100% {top:0px;} 
}
@keyframes oops {
	13% {top:0px;}
        14% {top:-90px;}
        33% {top:-90px;}
        34% {top:0px;}
       100% {top:0px;} 
}

#background_home {-webkit-animation:wind 45s linear infinite;-moz-animation:wind 45s linear infinite;-ms-animation:wind 45s linear infinite;animation:wind 45s linear infinite;}
@-webkit-keyframes wind {
	0% {background-position:0% 0%;}
       100% {background-position:1701px 0%;}
}
@-moz-keyframes wind {
	0% {background-position:0% 0%;}
       100% {background-position:1701px 0%;}
}
@-ms-keyframes wind {
	0% {background-position:0% 0%;}
       100% {background-position:1701px 0%;}
}
@keyframes wind {
	0% {background-position:0% 0%;}
       100% {background-position:1701px 0%;}
}

body {-webkit-animation:cam 45s linear infinite;-moz-animation:cam 45s linear infinite;-ms-animation:cam 45s linear infinite;animation:cam 45s linear infinite;}
@-webkit-keyframes cam {
  0% {-webkit-transform: none;}
  10% {-webkit-transform: scale(2) rotate(0deg) translateX(0px) translateY(200px); }
  15% {-webkit-transform: translateX(-100%) translateY(116.5px) scale(3);}
  21% {-webkit-transform: translateX(-85%) translateY(116.5px) scale(3);}
  28% {-webkit-transform: scale(1.5)}
  46% {-webkit-transform: scale(1)}
}
@-moz-keyframes cam {
  0% {-moz-transform: none;}
  10% {-moz-transform: scale(2) rotate(0deg) translateX(0px) translateY(200px); }
  15% {-moz-transform: translateX(-100%) translateY(116.5px) scale(3);}
  21% {-moz-transform: translateX(-85%) translateY(116.5px) scale(3);}
  28% {-moz-transform: scale(1.5)}
  46% {-moz-transform: scale(1)}
}
@-ms-keyframes cam {
  0% {-ms-transform: none;}
  10% {-ms-transform: scale(2) rotate(0deg) translateX(0px) translateY(200px); }
  15% {-ms-transform: translateX(-100%) translateY(116.5px) scale(3);}
  21% {-ms-transform: translateX(-85%) translateY(116.5px) scale(3);}
  28% {-ms-transform: scale(1.5)}
  46% {-ms-transform: scale(1)}
}
@keyframes cam {
  0% {transform: none;}
  10% {transform: scale(2) rotate(0deg) translateX(0px) translateY(200px); }
  15% {transform: translateX(-100%) translateY(116.5px) scale(3);}
  21% {transform: translateX(-85%) translateY(116.5px) scale(3);}
  28% {transform: scale(1.5)}
  46% {transform: scale(1)}
}
}
