css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(tpb.ipredator.se), domain("thepiratebay.gl"), domain("thepiratebay.se"), domain("thepiratebay.is"), domain("thepiratebay.sx"), domain("thepiratebay.ac"), domain("thepiratebay.pe"), domain("thepiratebay.gy"), domain("194.71.107.164"){
 

body {
    text-align: center!important;
    font-family: Verdana,Arial,Helvetica,sans-serif!important;
    font-size: 0.7em!important;
    margin: 10px!important;
    color: rgb(250, 250, 200)!important;
    background: none repeat scroll 0% 0% rgb(15, 15, 15)!important;
    line-height: 1.3em!important;}



a, a:link, a:focus {
    color: rgb(250, 250, 200)!important;
    text-decoration: none!important;}

input{color: rgb(0, 128, 255)!important;}

p#chb{color: rgb(250 ,250, 200)!important;}

strong{color: rgb(250, 250, 200)!important;}

table#searchResult tr {
    background: none repeat scroll 0% 0% rgb(10, 10, 10)!important;
    }

th{-moz-appearance: none !important;
color:#fff!important;
    border: 1px solid !important;
    border-color:rgb(10, 33, 197)!important;
    background-image: -moz-linear-gradient( top center , rgb(8, 8, 8),   
    rgb(20,20,20))!important;
    background: -webkit-linear-gradient( top , rgb(8, 8, 8),   
    rgb(20,20,20))!important;
border:0px!important;}

table#searchResult th, table#searchResult td {
    padding: 3px;
    border: 2px solid rgb(10, 33, 197)!important;}

#details {
    padding: 0px 10px 10px 20px;
    background: none repeat scroll 0% 0% rgb(10, 10, 10)!important; 
    border: 1px solid rgb(10, 33, 197)!important;}

#detailsframe #title {
    height: 1%;
    border: 1px solid !important;
    border-color:rgb(10, 33, 197)!important;
    background-image: -moz-linear-gradient( top center , rgb(8, 8, 8),   
    rgb(20,20,20))!important;
    background: -webkit-linear-gradient( top , rgb(8, 8, 8),   
    rgb(20,20,20))!important;
    border: 1px solid rgb(10, 33, 197)!important;
    border-bottom: 0px !important;}

#detailsframe {border-top: 0px!important;}
                
table#searchResult a:hover {color: rgb(0, 128, 255)!important;}

a:hover {color: rgb(0, 128, 255)!important;
         border-bottom:0px!important;}

a {border-bottom: none!important;}

.nfo{border: 1px solid rgb(10, 33, 197)!important;
    background: none repeat scroll 0% 0% rgb(8, 8, 8)!important;
    padding:4px!important;
   border-radius:4px!important;}

#comments .comment {
    background: none repeat scroll 0% 0% rgb(8, 8, 8)!important;
    border: 1px solid rgb(10, 33, 197)!important;
    padding:4px!important;
    border-radius:4px!important;}

#browseContainer dt {
   border: 1px solid rgb(10, 33, 197)!important;
   padding:4px!important;
   border-radius:4px!important;
    border: 1px solid !important;
    border-color:rgb(10, 33, 197)!important;
    background-image: -moz-linear-gradient( top center , rgb(8, 8, 8),   
    rgb(20,20,20))!important;
    background: -webkit-linear-gradient( top , rgb(8, 8, 8),   
    rgb(20,20,20))!important;} 

#filelistContainer {
    margin: 3em 0px 1em;
    min-height: 50px;
    border: 1px solid rgb(210, 185, 166);
     border: 1px solid !important;
    border-color:rgb(10, 33, 197)!important;
    background-image: -moz-linear-gradient( top center , rgb(8, 8, 8),   
    rgb(20,20,20))!important;
    background: -webkit-linear-gradient( top , rgb(8, 8, 8),   
    rgb(20,20,20))!important;} 


#fp {
background: url("http://i1324.photobucket.com/albums/u604/maksta/the-pirate-bay-transparent_zps28262c8c.png") no-repeat top center !important;
   position: relative !important;
   top: 0px !important;
   height:290px!important;}

#fp > *, body > footer {
   position: relative !important;
   top: 300px !important;}

#main-content {
    margin-left: 20px!important;
    padding: 50px!important;
    margin-bottom:80px!important;}

#q{text-align: center!important;
   margin-top: 80px!important;
   margin-left:5px!important;}

IMG[src="/static/img/promo.jpg"],
IMG[src="http://image.bayimg.com/fe8333b0ab6a6d4fc567abf1cfef5aaf08f181c0.jpg"],
h1,   
div#foot,
footer,
#fp iframe,
#TPBlogo
{display: none!important;}
 
INPUT.https_form ,INPUT.searchBox{color:#fff!important;
    border: 1px solid !important;
    border-color:rgb(10, 33, 197)!important;
    background-image: -moz-linear-gradient( top center , rgb(8, 8, 8),   
    rgb(20,20,20))!important;
    background: -webkit-linear-gradient( top , rgb(8, 8, 8),   
    rgb(20,20,20))!important;}
  
button,SELECT#category, 
input[type="reset"],
input[type="button"],
input[type="submit"]{-moz-appearance: none !important;
color:#fff!important;
    border: 1px solid !important;
    border-color:rgb(10, 33, 197)!important;
    background-image: -moz-linear-gradient( top center , rgb(8, 8, 8),   
    rgb(20,20,20))!important;
    background: -webkit-linear-gradient( top , rgb(8, 8, 8),   
    rgb(20,20,20))!important;} 

OPTGROUP,option{ background-color:rgb(10, 10, 10)!important;}

/* check boxes */
input[type="checkbox"] {-moz-appearance: none !important;
color:#fff!important;
    border: 1px solid !important;
    border-color:rgb(10, 33, 197)!important;
    background-image: -moz-linear-gradient( top center , rgb(8, 8, 8),   
    rgb(20,20,20))!important;
    background: -webkit-linear-gradient( top , rgb(8, 8, 8),   
    rgb(20,20,20))!important;} 

h2{ 
    border-color:rgb(10, 33, 197)!important;
 background:transparent!important;}
}
