.breadcrumb .active a{color:#BBBBBB;}


.breadcrumb{padding:7px 14px;margin:0 0 18px;list-style:none;background-color:#323232;background-image:-moz-linear-gradient(top, #22222, #323232);background-image:-ms-linear-gradient(top, #222222, #323232);background-image:-webkit-gradient(linear, 0 0, 0 100%, from(#222222), to(#323232));background-image:-webkit-linear-gradient(top, #222222, #323232);background-image:-o-linear-gradient(top, #ffffff, #f5f5f5);background-image:linear-gradient(top, #222222, #232323);background-repeat:repeat-x;filter:progid:DXImageTransform.Microsoft.gradient(startColorstr='#2222', endColorstr='#232323', GradientType=0);border:1px solid #555555;-webkit-border-radius:3px;-moz-border-radius:3px;border-radius:3px;-webkit-box-shadow:inset 0 1px 0 #121212;-moz-box-shadow:inset 0 1px 0 #121212;box-shadow:inset 0 1px 0 #121212;}.breadcrumb li{display:inline-block;*display:inline;*zoom:1;text-shadow:0 1px 0 #121212;}

.pagination a{color:222222;float:left;padding:0 14px;line-height:34px;text-decoration:none;border:1px solid #555555;border-left-width:0;}
.pagination a:hover,.pagination .active a{background-color:#BBBBBB;}
.pagination .active a{color:#555555;cursor:default;}
.well{background-color:#454545;}

.btn-group.open .btn.dropdown-toggle{background-color:#343434;}

.label,.badge{font-size:10.998px;font-weight:bold;line-height:14px;color:#BBBBBB;vertical-align:baseline;white-space:nowrap;text-shadow:0 -1px 0 rgba(0, 0, 0, 0.25);background-color:#555555;}
.label{padding:1px 4px 2px;-webkit-border-radius:3px;-moz-border-radius:3px;border-radius:3px;}
.badge{padding:1px 9px 2px;-webkit-border-radius:9px;-moz-border-radius:9px;border-radius:9px;}
a.label:hover,a.badge:hover{color:#ffffff;text-decoration:none;cursor:pointer;}
.label-important,.badge-important{background-color:#b94a48;}
.label-important[href],.badge-important[href]{background-color:#953b39;}
.label-warning,.badge-warning{background-color:#f89406;}
.label-warning[href],.badge-warning[href]{background-color:#c67605;}
.label-success,.badge-success{background-color:#468847;}
.label-success[href],.badge-success[href]{background-color:#356635;}
.label-info,.badge-info{background-color:#03567c;}
.label-info[href],.badge-info[href]{background-color:#2d6987;}
.label-inverse,.badge-inverse{background-color:#333333;}
.label-inverse[href],.badge-inverse[href]{background-color:#1a1a1a;}

.popover-title{padding:9px 15px;line-height:1;color:#555555;background-color:#BBBBBB;border-bottom:1px solid #555555;-webkit-border-radius:3px 3px 0 0;-moz-border-radius:3px 3px 0 0;border-radius:3px 3px 0 0;}

.popover-content{padding:14px;background-color:#555555;-webkit-border-radius:0 0 3px 3px;-moz-border-radius:0 0 3px 3px;border-radius:0 0 3px 3px;-webkit-background-clip:padding-box;-moz-background-clip:padding-box;background-clip:padding-box;}.popover-content p,.popover-content ul,.popover-content ol{margin-bottom:0;}

.thumbnail{display:block;padding:4px;line-height:1;border:1px solid #555555;-webkit-border-radius:4px;-moz-border-radius:4px;border-radius:4px;-webkit-box-shadow:0 1px 1px rgba(0, 0, 0, 0.075);-moz-box-shadow:0 1px 1px rgba(0, 0, 0, 0.075);box-shadow:0 1px 1px rgba(0, 0, 0, 0.075);}


