css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {

body{

	background: url(http://ic.pics.livejournal.com/codekitsune/21116942/29847/original.jpg) fixed !important;

}

#dashboard_index #container {

	background: none !important;

}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

	background: none !important;

}

#posts .post {

	background: url(http://ic.pics.livejournal.com/codekitsune/21116942/30290/original.jpg) !important;

	color: #ddd;

	border-bottom: 1px solid #F56c17 !important;

	border-top: 1px solid #F56c17 !important;

	border-left: -1px solid #666 !important;

	border-right: -1px solid #666 !important;



}

#posts .post img {

	border-radius: 10px;

}

#posts .post .footer_links .source_url a,

#posts .post * {

	color: #ddd !important;

}



#posts .post .permalink,

.arrow ,

#right_column .radar .content.is_media,

#right_column .radar .content .mask,

#right_column .controls_section .recessed {

	background: none !important;

}

.post_question_nipple,

.autopagerize_page_separator,

div.l-header .selection_nipple,

div.l-header .selected .selection_nipple

.autopagerize_page_separator_blocks,

.autopagerize_page_info {

	display: none !important;

}



#posts .post .post_question {

background: rgba(0, 0, 0, 0.2) !important;

}

.post_question_nipple +div+div {

	border-top: 1px solid #777 !important;

}

#posts ol.notes li.like ,

#posts ol.notes li.reblog {

background-color: rgba(0, 0, 0, 0.2)  !important;

}



#posts .notification {

background-color: rgba(0, 0, 0, 0.4)  !important;

border-top: 1px solid #009999 !important;

border-bottom: 1px solid #009999 !important;

border-right: 1px solid #009999 !important;

border-left: 1px solid #009999 !important;

}



#posts .notification.alt .nipple {

border-right: 6px solid #333 !important;

}

#posts .notification .nipple {

width: 0;

height: 0;

border-top: 6px solid transparent;

border-bottom: 6px solid transparent;

border-right: 6px solid #333 !important;

position: absolute;

left: -5px;

top: 10px;

}



#posts .notification a:not(.avatar_frame) {

	border-bottom: none !important;

}

#posts .post .conversation_lines .chat_line:first-child{

border-top: 1px solid #333 !important;

}

#posts .post .conversation_lines .chat_line,

#posts .post .conversation_lines .chat_line:nth-child(even) {

border-right: 1px solid #333 !important;

border-bottom: 1px solid #333 !important;

border-left: 1px solid #333 !important;

}

#posts .post .conversation_lines .chat_line:nth-child(even) {

background-color: rgba(0, 0, 0, 0.2) !important;

}



#posts .post .conversation_lines .chat_line:nth-child(odd) {

background-color: rgba(0, 0, 0, 0.4) !important;

box-shadow: 0 0 10px #000;

}

.answer_container {

background: rgba(0, 0, 0, 0.16) !important;

border-left: 1px solid #51535A !important;

border-right: 1px solid #51535A !important;

border-bottom: 1px solid #777 !important;

}

#main_tag #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

	background: none !important;

}

#dashboard_controls div[style="float:right;"],

#pagination a {

background: rgba(0, 0, 0, 0.16) !important;

border: 1px solid #444 !important;

border-radius: 5px;

box-shadow: inset 0 0 10px #000;

padding: 5px 20px !important;

margin-right: 10px;

}

#dashboard_controls a[href="/dashboard"] {

background: rgba(0, 0, 0, 0.16) !important;

border: 1px solid #444 !important;

box-shadow: inset 0 0 10px #000;

border-radius: 5px;

padding: 10px;

}

#dashboard_controls a[href="/dashboard"]:hover,

#dashboard_controls div[style="float:right;"]:hover,

#pagination a:hover {

box-shadow: 0 0 10px #000;

border: 1px solid #222 !important;



}



div.l-header {

background: rgba(0, 0, 0, 0.3) !important;

margin-bottom: 0!important;

border-radius:  0 0 10px 10px;

border: 1px solid #222 !important;

}

#footer {

background: rgba(0, 0, 0, 0.3) !important;

padding-bottom: 15px;

margin-bottom: 0!important;

border-radius: 10px 10px 0 0;

border: 1px solid #666 !important;

}

#right_column .controls_section li *,

div.l-header .tab > a {

color: #fff !important;

text-decoration: none !important;

text-shadow: -1px 1px 3px black,

 1px	1px 3px black !important;

 font-weight: normal!important;



}

#right_column .radar .attribution * ,

#dashboard_controls a[href="/dashboard"],

#dashboard_controls div[style="float:right;"] span,

#pagination a,

#footer a ,

#footer strong{

color: #fff !important;

text-decoration: none !important;

text-shadow: -1px 1px 3px black,

 0px	1px 3px black,

 1px	1px 3px black !important;

}



div.l-content #search_query,

#right_column .controls_section li:last-child{

background-color: rgba(255, 255, 255, .05) !important;

box-shadow: inset 0 1px 0 rgba(255,255,255,.09) !important;

border-top: 1px solid rgba(0, 0, 0, .09);

}

#right_column .radar .attribution{

background-color: rgba(255, 255, 255, .05) !important;

box-shadow: inset 0 1px 0 rgba(255,255,255,.09) !important;

border: 1px solid rgba(0, 0, 0, .09);

margin: 0!important;

border-radius: 0 0 10px 10px !important;

}

#right_column .controls_section .selected {

background: #333 !important;

background: -moz-linear-gradient(top,#444 0,#333 50%,#222 50%,#222 100%) !important;

background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#444),color-stop(50%,#333),color-stop(50%,#222),color-stop(100%,#222)) !important;

}

#posts .notification .avatar_frame {

	background: none !important;

}

#posts .notification.first_notification {

border-top: solid 1px #444 !important;

}

#posts .post pre {

	background: rgba(0, 0, 0, 0.3) !important;

	border-radius: 10px;

}

/*「 High-Res Photos 改」に対応ここから

#container,

#left_column {

	width: auto !important;

}

div.l-header {

	width: auto !important;

}



div.l-header #logo {

	width: 86px!important;

	height: 23px !important;

	margin-top: 25px !important;

}

#right_column {

	position: absolute;

	top:0px;

	left:135px;

	z-index: 200;

	background: url(https://github.com/ThetaApple/Tumblr_iOS/raw/master/back.jpg) !important;

	box-shadow: 0 0 10px #000;	

	width: 150px !important;

	border-radius: 10px;

	height: 38px !important;

	margin-top: 20px !important;

	overflow: hidden;

		transition: all 0.3s ;

		-o-transition: all 0.3s ;

		-moz-transition: all 0.3s ;

		-webkit-transition: width 0.3s,height 3s;

}



#right_column:hover {

	height: 100% !important;

	width: 215px !important;	

}



#posts .post.new_post {

	width: 525px !important;

	padding-right: 10px !important;

	background: none !important;

	box-shadow: inset 0 0 10px #000 !important;

}



#posts .post.quote .post_content{

	width: 525px !important;

	float: left;

}



#posts .post.audio,

#posts .post.regular,

#posts .post.note,

#posts .post.quote  {

	width: 525px !important;

}

