css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.amazon.co.uk") 
{
/*This controls the background.*/  
    html   
        {
            background-image:url(http://fc02.deviantart.net/fs71/f/2011/270/b/3/black_and_white_library_by_stardeht-d4b36e3.jpg) !important; /* This line is where you enter the URL of the background*/    
            background-repeat:no-repeat !important; /* This line stops the background from repeating*/
            background-attachment: fixed !important; /* This line fixes the background, so it is frozen whilst scrolling. Lovely!*/
            background-position:bottom !important; /*allow most of image in full screen without distort*/
            background-size: cover !important; /*allow most of image in full screen without distort*/
        }
        
        
/*These removes unwanted adverts. Boo!*/
    DIV#rightcol.amabot_right 
        {display: none !important;}
    DIV#center1.amabot_center
        {display: none !important;}
    DIV#uwlext_promo.hideInitial
        {display: none !important;}
    DIV#centerA.bunkBed
        {display: none !important;}
    DIV#centerB.bunkBed
        {display: none !important;}
    DIV#purchase-sims-feature
        {display: none !important;}
    DIV#rhf
        {display: none !important;}
    DIV.tafContainerDiv
        {display: none !important;}
    DIV#DAae1
        {display: none !important;}
    DIV#listmania.bucket
        {display: none !important;}
    DIV#revSAFR
        {display: none !important;}
    DIV#DAcrt    
        {display: none !important;}
    DIV#s9DpES.unified_widget.rcm.widget.s9DpES    
        {display: none !important;}
    div#tagContentHolder
        {display: none !important;}
    span.search-box
        {display: none !important;}
    div.navFooterLine.navFooterLinkLine.navFooterDescLine
        {display: none !important;}
    div.navFooterLine.navFooterLinkLine.navFooterPadItemLine
        {display: none !important;}
    div#divsinglecolumnminwidth.singlecolumnminwidth>div.bucket
        {display: none !important;}


    
    
/*Allows for slightly seethrough main content*/    
    header
    {margin-top: 50px;}
    
    body
        {
            max-width:1100px !important;/*additional max width*/
            margin: 0 auto !important;          
            opacity:0.88 !important;
            border-top-right-radius: 20px;
            border-top-left-radius: 20px;
            background-color:white !important;
            
            
            
        }
       DIV#content
        {
            background-color:white !important;
            
            max-width:1100px !important;/*additional max width*/
        }
      DIV#page-footer
        {
            background-color:white !important;
            border-bottom-right-radius: 100px 50px;
            border-bottom-left-radius: 100px 50px;
            max-width:1100px !important;/*additional max width*/
            margin-bottom: 50px;
        }    
    
  
        
    DIV#nav-bar-outer{
         max-width:1100px !important;/*additional max width*/
         margin: 0 auto !important;
        }
        
    ul#nav-cross-shop-links{
    text-align:center !important;
    width: 600px !important}  
 
 
 /* The next few things focus purely on item pages */

    table.buyingDetailsGrid
            {float: left!important;}    
    div.cBox.grayBox
            {display: none !important;}
            
            body.dp
        {
            border-bottom-right-radius: 100px 50px;
            border-bottom-left-radius: 100px 50px;
            margin-bottom: 50px !important;;
           
        }
            
  table.productImageGrid
    {margin-left : 10px !important;}
   
       

}
