css:
@-moz-document domain("mlpchan.net") {
/*

PartyTime.css for MLPchan.net
By Tangerine Cookie - 2012 (cookietangerine@gmail.com)
This work is licensed under a Creative Commons Attribution-ShareAlike 3.0 Unported License.
http://creativecommons.org/licenses/by-sa/3.0/

Version 1.2 - 8/13/2012 - Moved images to Photobucket

*/

body {
background-color:#eb9cc2!important;
background-image:url(http://i306.photobucket.com/albums/nn277/raingna/PartyPonyTheme/PinkieHeadPop.png);
background-repeat:no-repeat;
background-position:right top;
background-attachment:fixed;
color:#000!important;
font-family:arial,helvetica,sans-serif!important;
font-size:10pt!important;
padding-left:5px!important;
padding-right:5px!important;
margin:0 8px!important;
}

a,a:visited {
color:#be1c79!important;
}

div.blotter {
color:#000!important;
}

.boardlist.top {
box-shadow:0 1px 5px #be1c79!important;
}

.boardlist.top .boardlistpart a.boardlistactive {
padding-top:3px!important;
font-weight:700!important;
border-top:3px solid #be1c79!important;
}

.boardlist.top .boardlistpart a:hover {
background-color:#eb9cc2!important;
}

div.post.reply {
background-image:url(http://i306.photobucket.com/albums/nn277/raingna/PartyPonyTheme/CM.png);
background-repeat:no-repeat;
background-position:right bottom;
background-color:#f788be!important;
box-shadow:2px 2px 10px #be1c79!important;
border-top-left-radius:15px!important;
border-top-right-radius:5px!important;
border-bottom-left-radius:5px!important;
border-bottom-right-radius:15px!important;
border-style:none!important;
margin:5px;
}

div.post.reply.highlighted {
background-image:url(http://i306.photobucket.com/albums/nn277/raingna/PartyPonyTheme/CM.png);
background-repeat:no-repeat;
background-position:right bottom;
background-color:#ff99ba!important;
box-shadow:0px 0px 15px #80d1f5!important;
}

#qr {
background-color:#e980b3!important;
border-bottom-left-radius:15px!important;
border-bottom-right-radius:15px!important;
border-color:#be1c79;
border-style:solid;
border-width:1px;
}

header div.subtitle,h1 {
color:#be1c79;
text-align:center;
}

h2
{
color:#be1c79;
}

span.trip {
color:#be1c79!important;
}

p.intro span.subject {
color:#be1c79!important;
font-weight:700!important;
font-size:14px!important;
}

/*Pinkie Pie is best Pony*/

p.intro span.name {
color:#be1c79!important;
font-weight:700!important;
font-size:13px!important;
}

p.intro time {
color:#be1c79!important;
}

span.quote {
color:#fdf6ae!important;
}

/* Saikar for mod */

.hashtag {
background:#eb9cc2!important;
box-shadow:1px 1px 5px #be1c79!important;
color:#be1c79!important;
font-style:italic!important;
display:inline-block;
margin-right:.5em;
border-top-left-radius:15px!important;
border-top-right-radius:5px!important;
border-bottom-left-radius:5px!important;
border-bottom-right-radius:15px!important;
border-color:#be1c79!important;
border-style:solid!important;
border-width:1px!important;
padding:.2em .5em;
}

div.boardlist,#updateSettings {
background-color:#e980b3!important;
}

form table tr th,div.banner {
background-color:#e980b3!important;
border-style:none!important;
}
}
