css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.lastfm.de/music/"), 
url-prefix("http://www.last.fm/music/"),
url-prefix("http://www.lastfm.es/music/"),
url-prefix("http://www.lastfm.fr/music/"),
url-prefix("http://www.lastfm.it/music/"),
url-prefix("http://www.lastfm.jp/music/"),
url-prefix("http://www.lastfm.pl/music/"),
url-prefix("http://www.lastfm.com.br/music/"),
url-prefix("http://www.lastfm.ru/music/"),
url-prefix("http://www.lastfm.se/music/"),
url-prefix("http://www.lastfm.com.tr/music/"),
url-prefix("http://cn.last.fm/music/") {

    
    .artist-ad-wrapper,
    #hero-video
    { display: none; }
    
    .album-details .ecommerce-dropdown,
    .featured-tracks
    { display: none !important; }
    
    /* Ähnliche Künstler */
    
    .artist-bio-sidebar,
    .artist-similar-sidebar 
    { width: auto !important; clear: left !important; }
    
    /* Top-Titel */
    #artist-top-tracks
    { float: left; width: 620px; margin-right: 20px; }
    
    .horizontalOptions 
    { margin-bottom: 5px !important; }
    
    .responsive table.tracklist td, .responsive table.chart td 
    { line-height: 15px !important; }
    
    .responsive table.tracklist, .responsive table.chart 
    { font-size: 11px !important; }
    
    .responsive td.subjectCell div
    { height: 15px !important; }
    
    .responsive div.chartbar span
    { height: 18px !important; }
    
    /* Top-Alben */
    .artist-top-albums
    { float: left !important; width: 300px !important; }
    
    .artist-top-albums .album-list
    { float: left !important; width: 310px !important; margin-left: 0px !important; }
    
    .artist-top-albums .g3,
    .artist-top-albums .g6
    { width: 140px !important; margin-left: 5px !important; margin-right: 15px !important; }
    
    .artist-top-albums .g3:nth-child(even),
    .artist-top-albums .g6:nth-child(even)
    { margin-right: 0 !important; }
    
    .artist-top-albums li.g3 .label,
    .artist-top-albums li.g6 .label
    { margin: 0 !important; min-height: 36px; }
    
    .artist-top-albums .ecommerce-dropdown
    { display: none !important; }
    
    .artist-top-albums .album-item-cover
    { height: 140px !important; margin-bottom: 8px !important; }
    
    .artist-top-albums .text-over-image
    { max-width: 140px !important; max-height: 140px !important; }
    
    .artist-top-albums .text-over-image img
    { width: 100% !important; height: 100% !important; margin-right: 0 !important; }
    
    .artist-top-albums .album-item-details
    { padding: 3px 6px !important; width: 128px !important; }

    .artist-top-albums .album-item-detail-wrapper
    {padding: 0 !important; margin: 0 !important; }

    /*.new-release
    { background-color: #BE0D1D !important; padding: 1px !important; }*/
    
    /* Hörtrend */
    .artist-listening-trend
    { width: 610px !important; }
    
    /* Events */
    section.artist-events
    { float: right !important; width: 300px !important; margin-right: 10px; }
    
    section.artist-events > h2
    { margin-left: 10px !important; }
    
    .events-catalogue-pages
    { margin: 0 0 0 10px !important; }
    
    .events-catalogue-pages > li
    { clear: both; border-bottom: none !important; border-top: 1px solid #dbdbdb; padding-top: 12px !important; }
    
    .events-catalogue-pages > li.first
    { border-top: none !important; padding-top: 0 !important; }
    
    .events-catalogue-pages .g5
    { width: 36px !important; }
    
    .events-catalogue-pages .calSheetBig
    { width: 34px !important; }
    
    .events-catalogue-pages .calSheetBig .month
    { font-size: 11px !important; height: 14px !important; line-height: 15px !important; }
    
    .events-catalogue-pages .calSheetBig .day
    { font-size: 15px !important; height: 20px !important; line-height: 20px !important; }
    
    .events-catalogue-pages .g5 .media-body
    { display: none !important; }
    
    .events-catalogue-pages .g5,
    .events-catalogue-pages .venue
    { margin-bottom: 3px !important; }
    
    .events-catalogue-pages .venue img
    { display: none; }
    
    .events-catalogue-pages .going
    { margin: 4px 0 8px 14px !important; }
    
    .artist-listening-trend
    { float: left !important; }
    
    .artist-social
    { clear: both; }
    
    .artist-events .moduleOptions,
    .artist-events .btn--add
    { display: none !important; }

    /* Friends listen */
    /*.music-resource-stats
    { margin-top: 14px !important; }*/
    
    .artist-overview
    { position: relative; }
    
    #friends-who-listen-to
    { position: absolute; top: 362px; right: 10px; }
    
    .user-listen-counts li
    { margin-bottom: 0 !important; min-height: 66px !important; }
    
    .user-listen-counts .userImage img:first-child
    { width: 46px !important; height: 46px !important; }
    
    .user-listen-count-name
    { padding-left: 0 !important; margin-left: -10px !important; }
    
    .user-listen-counts .chartbar span
    { padding-top: 1px !important; margin-top: 2px; padding-left: 8px !important; }
    
    /* Shoutbox Sidebar */
    .artist-social .artist-listeners .userImage img,
    .artist-social .artist-groups .media-link-hook img
    { width: 48px !important; height: 48px !important; }
}

@-moz-document url-prefix("http://www.last.fm/music/") {

    .events-catalogue-pages .going
    { margin: 4px 0 8px 14px !important; position: relative; }
    
    .events-catalogue-pages .going > div
    { position: absolute; bottom: 2px; right: 16px; }

}
