css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gikz.pl")  {

body { 	font: normal 18px/1.8 Arial, sans-serif !important; }
.nspsmall p.nspText {	
    font-size: 15px !important; 
    }
div.nspArtPage {
    width:85%!important;
    }
DIV#gkLeft.gkMain.gkCol {
    width:14% !important; 
    }
DIV#gkContent.gkMain.gkCol.gkContentRL {
    width:65%!important; 
    }
DIV.nspArtScroll1 {
    width:100%!important;
    }
DIV.nspArtScroll2.nspArtPage {
    width:100%!important;
    }
.gkWrap { 
    width: 100%!important; 
    max-width: 100%!important; 
    }
DIV#gkRightTop {
    width:80%!important; 
    }
DIV#gkMainBlock {
    width:105%!important; 
    }

span.itemDateModified {
	float: right;
	font-style: italic;
	color: #999;
	font-size: 14px
	!important;
    }
.itemAuthorBlock {
	margin: 0px 0 0px!important;
	padding: 0px 0 0px!important;
    }
.itemNavigation {
	margin: 0px!important;
	padding: 0px!important;
    }
a.readon.fright {
    float: left !important;
    background: #838B8B !important;
    border-radius: 5px;
    padding-right:5px !important;
    padding-left:5px !important;
    }
.gkContentRL {
    padding-left: 20px !important;
    padding-right: 27px !important;
    }
.nspArt p.nspInfo {
    font-size: 68% !important;
    }
p.nspText.tleft.fleft { 
    width: 100% !important;
    padding:5px !important;
    background: #EEF3E2 !important;
    border-radius: 5px;
    }
H4.nspHeader.tleft.fnone { 
    width: 100% !important;
    background: #C1CDCD;
    border-radius: 5px;
    padding-right:5px !important;
    padding-left:5px !important;
    }
div#gkSearch {
	float: right !important;
	margin-top: 56px !important;
	margin-right: 30px !important;
    }
IMG.nspImage.tleft.fleft { 
    width: 70% !important;
    }
.nspPages3 { 
    width: 100% !important;
    }
a.nspImageWrapper.tleft.fleft {
    width: 76% !important;
    text-align: center !important;
    padding-left: 25% !important;
    }
DIV.nspArtPage.nspCol3 {
    width: 100% !important;
    }
#gkBottomWrap a {
    color: rgb(50,50, 50) !important;
    }
UL.k2UserBlockActions {
    background: #EEF3E2 !important;
    border-radius: 5px;
    }
H3.catItemTitle {
    padding-right:5px !important;
    padding-left:5px !important;
    background: #C1CDCD;
    border-radius: 5px;
    }
a.readon.inline {
    background: #838B8B !important;
    border-radius: 5px;
    padding-right:5px !important;
    padding-left:5px !important;
    }
#gkButtons {
    margin-top: 60px !important;
    }
DIV.mod-comment-meta.small { 
    margin-bottom:10px !important;
    }
.nspCol6 {
    width: 150px !important;
    }
SPAN.catItemImage { 
    text-align: center 
    }
DIV#gkHeader.clear.clearfix {
    height: 100px !important;
       }
DIV.box.nsp { 
    padding: 0px !important
    }
#gkMainMenu {
    float: left;
    border-bottom: 0px solid rgb(16, 16, 16)!important;
    height: 30px !important;
    }
DIV.gk-menu {
    height: 30px !important;
    }
UL.gkmenu.level0 {
    height: 30px !important;
    }
LI.first.active {
    height: 33px !important;
    }
a#menu101.first.active {  
        height: 20px !important 
    }
#gkMainMenu > div > ul > li > a {
	padding: 5px 27px  !important;
	margin-top: 0px !important;
	margin-bottom: 20px !important;
	display: block;
	height: 20px !important;
	line-height: 25px !important;
	font-size: 14px;
	text-transform: uppercase;
	color: #444;
	border-right: 1px solid #d5d5d5 !important;
	font-weight: bold;
    }
SPAN.menu-title {
    margin-top: 0px !important;
    }    
}
