css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pinboard.in") {
body {
    font-family: "Lucida Grande" !important;
    color:#292929}

a {color:#292929!important;padding:2px}
a:hover,.user_navbar a:hover,#tag_cloud_header a:hover {background:#292929!important;color:#FFF!important}

.display .when {
    color: #bbb !important;
    margin-left: 0px !important;
    padding-left: 0px !important;}

.display {border-bottom:1px solid #eee; width:100%; padding:0 0 20px}
.display .bookmark_title {
    font-weight: normal;
    font-size: 18px !important;
    line-height: 24px;
    font-family: Georgia, serif;
    margin:0 0 5px;}
.display a.bookmark_title {color:#292929;}
.display a.unread {background:#fdebbf}
.display a.bookmark_title:hover {color:#ff0099}
.display .description {
    font:14px/18px Georgia !important;
    margin:5px 0;
    padding:7px !important;
    background:#EEE}
.display .tag {
    font:11px "Lucida Grande";
    display:inline-block;
    padding:2px 5px !important;
    margin:5px 0 !important;
    background:#ecf7fb}
.display .source {
    font-size:11px;
    display:inline-block;
    margin:5px 0 !important;
    padding:0 !important;
    text-decoration: underline !important;
    color: #aaa !important;}
.display .edit_links a {
    color: #aaa !important;
    font-size:11px !important;
    text-decoration: underline !important;}

.user_navbar a, #tag_cloud_header a {
    font-size:13px !important;
    color:#059ece !important;
    font-weight:bold}
    
#nextprev .next_prev {
    font-size: 16px !important;
    color: #666 !important;
    background-color: #f0f0f0;
    padding: 0 3px !important;
}
#nextprev a:hover {
    color: #fff !important;
    background-color: #999 !important;
}

/* EDIT CONTROLS */

#edit_bookmark_form .edit_form_input {
    font-size: 13px !important;
}

#edit_bookmark_form .description {
    font-family: "Lucida Grande" !important;
    height: 8em !important;
    font-size: 13px !important;
    line-height: 18px !important; 
}

#edit_bookmark_form .submit {
    font-size:18px !important;
}

#edit_bookmark_form .reset {
    font-size:14px !important;
    border-color: #f0f0f0 !important;
    background-color: #fdfdfd !important;
    color: #999 !important;
}

/* STARS */

.star {
    font-size:22px !important;
    display:inline-block;
    font-weight:bold;
    margin: 0px 10px -25px -20px;}
.star span {visibility:hidden}
.star:before {content: "â™¥"}
.selected_star, .star:hover {color:#cc0066!important}

#bookmarks .private {background:#fff!important}
#bookmarks .private .display:before {
    content:"PRIVATE: ";
    font-size:10px;
    color:#0099cc}

#top_menu a {
    color: #aaa !important;
}
#top_menu a:hover {
    color: #eee !important;
    background-color: #999 !important;
}

}
