css:
@-moz-document domain("news.ycombinator.com") {

span.comment font {
    color: #aaa !important
}


FONT[color="#000000"] {
    color: #aaa !important;
}
table {
    background-color: #222 !important;
    max-width: 700px !important;
}

tbody tr td,
html body,
input{
    background-color: #222 !important;
}

table tbody {
    background-color: #222 !important;

}

span.pagetop {
    color: #bbbbbb !important;
    padding-bottom:40px !important;
    font-family:Georgia, Times, serif !important;
    font-size:10pt !important;
    line-height: 10px !important;
}

span.pagetop a:link, span.pagetop a:visited  {
    font-family:Georgia, Times, serif !important;
    font-size:10pt !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    margin-bottom: 10px !important;
    padding-bottom: 4px !important;
    font-weight: normal !important;
    border-bottom: 0px solid #666 !important;
}

span.pagetop a:hover, span.pagetop a:active {
    font-family:Georgia, Times, serif !important;
    font-size:10pt !important;
    color:#f60 !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    margin-bottom: 10px !important;
    padding-bottom: 4px !important;
    font-weight: normal !important;
    border-bottom: 0px solid #ffa86e !important;
}

body {
    font-family: Georgia, Times, serif !important;
    font-size:12pt !important;
    color:#999 !important;
}

td {
    font-family: Georgia, Times, serif !important;
    font-size:12pt !important;
    color:#999999 !important;
    padding: 3px !important;
}

.admin td {
    font-family: Georgia, Times, serif !important;
    font-size:8.5pt !important;
    color:#aaa !important;
}

td.subtext {
    font-family: Georgia, Times, serif !important;
    font-size: 12px !important;
    color:#999999 !important;
    border-bottom: 1px solid #222 !important ;
}

form {
    margin-bottom: 0px !important;
}

input {
    font-family:Georgia, Times, serif !important;
    font-size:15px !important;
    color:#aaa !important;
    background-color: #666 !important;
    width: auto !important;
    border: 1px solid #666 !important;
    border-radius: 4px !important;
}

input[type="text"]    {
    font-family:Georgia, Times, serif !important;
    font-size:15px !important;
    color:#aaa !important;
    background-color: #666 !important;
    color: #aaa !important;
    width: auto !important;
    font-family: Georgia, Times, serif !important;
    font-size: 15px !important;
    border: 1px solid #666 !important;
    border-radius: 4px !important;
}

input[type="submit"] {
    font-family:Georgia, Times, serif !important;
    background-color: #f60 !important;
    color: #111 !important;
    width: auto !important;
    font-family: Georgia, Times, serif !important;
    font-size: 13px !important;
    border: 1px solid #f60 !important;
    border-radius: 4px !important;
    cursor: pointer !important;
}

textarea {
    font-family:Georgia, Times, serif !important;
    font-size:15px !important;
    color:#555 !important;
    border: 1px solid #666 !important;
    border-radius: 4px !important;
    width: 660px !important;
    height: 100px !important;
}

a:link    {
    color:#ffa86e !important;
    text-decoration:none !important;
    border-bottom: 1px solid #666 !important;
}

a:hover, a:active    {
    color:#ffa86e !important;
    text-decoration:none !important;
    border-bottom: 1px solid #ffa86e !important;
}

a:visited {
    color:#666 !important;
    text-decoration:none !important;
    border-bottom: 1px solid #666
}

.default {
    font-family:Georgia, Times, serif !important;
    font-size: 12pt !important;
    color:#999999 !important;
    border-bottom: 1px solid #666 !important;
    padding: 0px !important;

}

.admin   {
    font-family:Georgia, Times, serif !important;
    font-size:8.5pt !important;
    color:#aaa !important;
}

.title   {
    font-family:Georgia, Times, serif !important;
    font-size: 12pt !important;
    color:#999999 !important;
    padding-top: 12px !important;
    font-weight: normal !important;
}


.title a:link {
    font-weight: normal !important;
    font-family:Georgia, Times, serif !important;
    font-size: 14pt !important;
    color:#aaa !important;
    margin-right: 6px;
}

.title a:hover, .title a:active {
    border-bottom: 1px solid #ffa86e !important;
}

.title a:visited {
    color:#666;
    border-bottom:1px solid #666;
}

.adtitle {
    font-family:Georgia, Times, serif !important;
    font-size: 9pt !important;
    color:#999999 !important;
}

.yclinks {
    font-family:Georgia, Times, serif !important;
    font-size: 8pt !important;
    color:#999999 !important;
    padding-top: 15px !important;
}

.yclinks a:link, .yclinks a:visited {
    color: #bbb !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    font-weight: normal !important;
    border-bottom: none !important;
}

.yclinks a:hover, .yclinks a:active {
    color: #f60 !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
    font-weight: normal !important;
    border-bottom: none !important;
}

.pagetop {
    font-family:Georgia, Times, serif !important;
    font-size: 12pt !important;
    color:#aaa !important;

}

.comhead {
    font-family:Georgia, Times, serif !important;
    font-size: 9pt !important;
    color:#888888 !important;
}

.comment {
    color: #888 !important;
    font-family:Georgia, Times, serif !important;
    font-size: 15px !important;
    line-height: 22px !important;
    padding-top: 10px !important;
 	white-space: pre !important;
	white-space: pre-wrap !important;
	white-space: pre-line !important;
	word-wrap: break-word !important;
}

.dead    {
    font-family:Georgia, Times, serif !important;
    font-size: 10pt !important;
    color:#888 !important;
}
i {
    font-style: italic !important;
}

u {
    text-decoration: none !important;
    border-bottom: 1px solid #222 !important;
    font-size: 12px !important;
    margin-top: -10px !important;
}


.comment a:link, .comment a:visited  {
    color: #ffa86e !important;
    border-bottom: 1px solid #666 !important;
    font-weight:normal !important;
}


.comment a:hover, .comment a:active {
    color: #ffa86e !important;
    border-bottom: 1px solid #ffa86e !important;
    font-weight:normal !important;
}


.dead a:link, .dead a:visited {
    color:#666 !important;

}
.pagetop a:visited {
    color:#aaa !important;
}

.topsel a:link, .topsel a:visited {
    color:#222 !important;
}


.subtext {
    font-family:Georgia, Times, serif !important;
    font-size: 8pt !important;
    color:#999999 !important;
    padding-bottom:20px !important;
    font-style: italic !important;
}

.subtext a:link {
    padding-left:2px !important ;
    padding-right:4px !important;
    color: #ffa86e !important ;
    font-weight: normal !important;
    border-bottom: 1px solid #666 !important;
}

.subtext a:hover, .subtext a:active {
    text-decoration:none !important;
    border-bottom: 1px solid #ffa86e !important;
}


.subtext a:visited {
    padding-left:2px !important ;
    padding-right:4px !important;
    color: #ffa86e !important ;
    font-weight: normal !important;
    border-bottom: 1px solid #666;
}

.comhead a:link, .comhead a:visited  {
    padding-left:2px !important ;
    padding-right:4px !important;
    color: #ffa86e !important ;
    font-weight: normal !important;
    border-bottom: 1px solid #666 !important;
}

.comhead a:hover, .comhead a:active  {
    padding-left:2px !important;
    padding-right:4px !important;
    color: #ffa86e !important ;
    font-weight: normal !important;
    text-decoration:none !important;
    border-bottom: 1px solid #ffa86e !important;
}

.default p {
    margin-top: 2px !important;
    margin-bottom: 0px !important;
    padding-bottom: 5px;
    padding-top: 15px !important;
    text-rendering:optimizeLegibility !important;
}

.pagebreak {
    page-break-before:always !important;
}

pre {
    overflow: auto !important;
    padding: 2px !important;
    max-width:600px !important;
    font-size: 13px !important;
}

pre:hover {
    overflow:auto !important;
}

center > a:first-child {
    padding:3px 5px ;
    position: relative;
    top: 6px;
    border-bottom: none !important;
}


center > a:nth-child(3) {
    border-bottom: none !important;
}

center > a:first-child:before {
    content:"▲";
    bottom:3px;
    position:relative;

}

center > a:nth-child(3):before {
    top:3px;
    position: relative;
    content:"▼";
}

center > a:nth-child(3):before,
center > a:first-child:before {
    color: #777;
}

.votearrow {
    display: none !important;
}

}
