css:
/*Messy and probably soon to be outdated code is below.*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(mlpchan.com) {

div.content
{
    color: #ddd !important;
}

span.omittedposts
{
    color: #ddd !important;
    font-size: 15px !important;
}

a.boardlink:hover, ul li a:hover, ul li#sitestyles:hover, ul li#removeframes
{
    background-color: #222 !important;
}

ul li.tab, ul li.current
{
    background-color: #222 !important;
}


html, body
{
	background-color:#090a0a !important;
	color: #ddd !important;
/*	color: #000 !important;*/
}


blockquote, li, td
{
    color: #dddddd !important;
}

td.postblock
{
    background-color: #222 !important;
}

tr td.highlight
{
    background-color: #141616 !important;
    border: 2px #444 solid !important;
}

label span.filetitle
{
    color: #47A591 !important;
}

h1, h2, span.plus
{
    background-color: #222 !important;
    color: #ddd !important;
    border:0px !important;
}

.reply
{
    background-color: #0f0f0f !important;
}

.unkfunc
{
    color: #00ab3f !important;
}



label
{
    color: #bd2b83 !important;
}

a
{
    color: #3090b5 !important;
}

.filesize
{
    color: #2a7fa0 !important;
}

span.admin font, div.logo
{
    color: #E33F4E !important;
}

div.replymode, div#watchedthreads.watchedthreads
{
    background-color: #222 !important;
    border: 2px #333 solid !important;
}

div#watchedthreadsdraghandle.postblock
{
    background-color: #111 !important;
    color: #E33F4E !important;
}

div#watchedthreads.watchedthreads span#watchedthreadlist
{
    color: #ddd !important;
}

div.navbar, div.adminbar
{
    color: #F76674 !important;
}

.reflinkpreview
{
	background-color: #111 !important;
	color: #ddd;
	border: 2px #333 solid !important;
}

body table
{
    border:none !important;
}

table tbody tr td
{
    color: #aaa !important;
    border: none !important;
}

}
