css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.youtube.com/") {

/* Setup html and body ________________________________________*/

html, body, #content {
 padding: 0!important;
 margin: 0!important;
 background: #000!important;
}

#content-container { /* Move main container below the top bar */
 position: relative!important;
 top: 70px!important;
}

/* Play list at bottom of screen __________________________________*/

#playlist-bar-bar, #playlist-bar-options-menu, #playlist-bar-tray-container {
 background: #000!important;
}

#playlist-bar-info button, #playlist-bar-controls button, #playlist-bar-tray button { 
 border-top: 1px solid #96d1f8!important;
 background: -moz-linear-gradient(top, #02101a, #08324f)!important;
 padding: 5px 10px!important;
 border-radius: 8px!important;
 margin-top: -20px!important;
 box-shadow: rgba(0,0,0,1) 0 1px 0!important;
 text-shadow: rgba(0,0,0,.4) 0 1px 0!important;
 color: white!important;
 font-size: 14px!important;
 font-family: Georgia, serif!important;
 text-decoration: none!important;
 vertical-align: middle!important;
}

#playlist-bar-info button:hover, #playlist-bar-controls button:hover, #playlist-bar-tray button:hover {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
}

#playlist-bar-info button:active, #playlist-bar-controls button:active, #playlist-bar-tray button:active {
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

#playlist-bar-controls button, #playlist-bar-tray button {
 margin-top: 0px!important;
}

#playlist-bar-options-menu ul li span, .video-list li a {
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
 color: #fff!important;
}

#playlist-bar-options-menu ul li span:hover {
 background: -moz-linear-gradient(top, #4d0808, #1a0202)!important;
 color: #ff0!important;
}


/* Remove stuff _______________________________________________*/

#ad_creative_1, #masthead-user-image, .masthead-link-separator, #pyv-placeholder, #instream_google_companion_ad_div, #google_companion_ad_div {
 display: none!important;
}

/* Main Page __________________________________________________*/

  /* Top bar __________________________________________________*/

#masthead-container {
 position: fixed!important;
 top: 0!important;
 left: 0!important;
 width: 100%!important;
 height: 60px!important;
 background: #000!important;
 z-index: 999!important;
}

#masthead-nav a, #masthead-user-button { 
 border-top: 1px solid #96d1f8!important;
 background: -moz-linear-gradient(top, #02101a, #08324f)!important;
 padding: 5px 10px!important;
 border-radius: 8px!important;
 box-shadow: rgba(0,0,0,1) 0 1px 0!important;
 text-shadow: rgba(0,0,0,.4) 0 1px 0!important;
 color: white!important;
 font-size: 14px!important;
 font-family: Georgia, serif!important;
 text-decoration: none!important;
 vertical-align: middle!important;
}

#masthead-nav a:hover, #masthead-user-button:hover {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
}

#masthead-nav a:active, #masthead-user-button:active {
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

  /* Left column ______________________________________________*/

.guide {
 background: #000!important;
}

.guide div h3 a, .guide div ul a {
 border-top: 1px solid #96d1f8!important;
 background: -moz-linear-gradient(top, #02101a, #08324f)!important;
 padding: 5px 10px!important;
 border-radius: 8px!important;
 box-shadow: rgba(0,0,0,1) 0 1px 0!important;
 text-shadow: rgba(0,0,0,.4) 0 1px 0!important;
 color: white!important;
 font-size: 14px!important;
 font-family: Georgia, serif!important;
 text-decoration: none!important;
 vertical-align: middle!important;
} 

.guide div h3 a:hover, .guide div ul a:hover {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 border-right-color: rgba(150,255,0,.8)!important;
 color: #ff0!important;
}

.guide div h3 a:active, .guide div ul a:active {
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

  /* Center column ____________________________________________*/

.feed-item-container { /* Set background color for video selections*/
 background: #000!important;
 color: #fff!important;
} 

.feed-item-container:hover { /* Set background color for video selections on hover*/
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
} 

.feed-item-container.first:hover {
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

.feed-item-content h4 {
 height: 50px!important;
}

.feed-item-content h4 a { /* Video title */
 display: block!important;
 line-height: 1.4em!important;
 text-align: justify!important;
 text-decoration: none!important;
 font-size: .6em!important;
 white-space: pre-wrap!important;
}

.feed-item-content h4 a:hover {  
 color: #ff0!important;
}

.yt-user-name, .yt-uix-redirect-link {
 text-decoration: none!important;
}

.yt-user-name:hover, .yt-uix-redirect-link:hover {
 color: rgba(255,255,0,.5)!important;
}

  /* Right column _____________________________________________*/

.sidebar-module-header {
 margin-top: -250px!important;
 color: rgba(255,255,0,.7)!important;
}

#video-sidebar h2 {
 color: rgba(255,255,110,.9)!important;
}

#video-sidebar p {
 color: rgba(255,255,255,.9)!important;
}

.video-list-item a:hover { /* Set background color for video selections on hover*/
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
} 

.video-list-item span {
 text-decoration: none!important;
}

.video-list-item span:hover {
 color: rgba(255,255,0,1)!important;
}

/* End main page ______________________________________________*/

/*________________________________________________________________*/

/* Watch video Page ___________________________________________*/

  /* See all video slider _____________________________________*/
.yt-uix-slider-nums.yt-uix-pager button, #watch-channel-discoverbox button, #watch-more-related-button { 
 border-top: 1px solid #96d1f8!important;
 background: -moz-linear-gradient(top, #02101a, #08324f)!important;
 padding: 5px 10px!important;
 border-radius: 8px!important;
 box-shadow: rgba(0,0,0,1) 0 1px 0!important;
 text-shadow: rgba(0,0,0,.4) 0 1px 0!important;
 color: white!important;
 font-size: 14px!important;
 font-family: Georgia, serif!important;
 text-decoration: none!important;
 vertical-align: middle!important;
}

.yt-uix-slider-nums.yt-uix-pager button:hover, .yt-uix-slider-slide-item div:hover, #watch-channel-discoverbox button:hover, #watch-more-related-button:hover  {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
}

.yt-uix-slider-nums.yt-uix-pager span:hover, .yt-uix-slider-slide-item div:hover {
 color: #ff0!important;
}

.yt-uix-slider-nums.yt-uix-pager button:active, #watch-channel-discoverbox button:hover, #watch-more-related-button:active {
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

.yt-uix-slider-slide-item.last div {
 color: #ff0!important;
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

.yt-uix-slider-slide-item.last span{
 color: #ff0!important;
}

 /* Left column _______________________________________________*/

#watch-headline-title {
 color: #ff0!important;
}
 /* Change button background */
#watch-headline-user-info button, #watch-actions button { 
 border-top: 1px solid #96d1f8!important;
 background: -moz-linear-gradient(top, #02101a, #08324f)!important;
 padding: 5px 10px!important;
 border-radius: 8px!important;
 box-shadow: rgba(0,0,0,1) 0 1px 0!important;
 text-shadow: rgba(0,0,0,.4) 0 1px 0!important;
 color: white!important;
 font-size: 14px!important;
 font-family: Georgia, serif!important;
 text-decoration: none!important;
 vertical-align: middle!important;
}

#watch-headline-user-info button:hover, #watch-actions button:hover {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
}

#watch-headline-user-info button:active, #watch-actions button:active {
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

#watch-actions-area, .yt-uix-expander-head div  {
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

#eow-description {
 color: rgba(255,255,255,.7)!important;
}

  /* Comment background __________________________________________*/

.comments-section ul li:hover {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
}

.yt-alert-message a {
 color: #ff0!important;
 text-decoration: none!important;
}

.yt-alert-message {
 color: #fff!important;
}

#eow-description a, .comments-post-alert a, .live-comments-setting span a, .live-comments-setting a, .comment-action.comment-action-showparent {
 text-decoration: none!important;
}

#eow-description a:hover, .live-comments-setting span a:hover, .live-comments-setting a:hover, .comment-action.comment-action-showparent:hover {
 color: rgba(255,255,0,.5)!important;
}

.comments-post-alert a:hover {
 color: rgba(255,0,0,1)!important;
}

#live_comments li:hover {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
}

.yt-uix-pager a { 
 border-top: 1px solid #96d1f8!important;
 background: -moz-linear-gradient(top, #02101a, #08324f)!important;
 padding: 5px 10px!important;
 border-radius: 8px!important;
 box-shadow: rgba(0,0,0,1) 0 1px 0!important;
 text-shadow: rgba(0,0,0,.4) 0 1px 0!important;
 color: white!important;
 font-size: 14px!important;
 font-family: Georgia, serif!important;
 text-decoration: none!important;
 vertical-align: middle!important;
}

.yt-uix-pager a:hover {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
}

.yt-uix-pager a:active {
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

 /* Right column ______________________________________________*/

.context-head a, #watch-context-body {
 background: #000!important;
}

.video-thumb.ux-thumb.yt-thumb-square-64 {
 margin-left: -56px!important;
} 

.watch-sidebar-section {
 margin-left: -30px!important;
}

.watch-sidebar-head {
 color: #ff0!important;
}

.video-list-item.watch-pyv-vid {
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

.video-list-item.watch-pyv-vid:hover {
 background: -moz-linear-gradient(top, #4d0808, #1a0202)!important;
}

#ppv-container li a span {
 color: #fff!important;
}

#ppv-container li a span:hover {
 color: #ff0!important;
}

#ppv-container li a {
 background: -moz-linear-gradient(top, #002400, #005700)!important;
 color: #fff!important;
}

.yt-badge-std {
 background: #000!important;
 color: #fff!important;
}

#ppv-container li a:hover {
 background: -moz-linear-gradient(top, #005700, #002400)!important;
}

.related-video.yt-uix-contextlink.yt-uix-sessionlink span {
 

 margin: auto!important;
}

.title, .description {
 font-size: 8px!important;
 color: #fff!important;
 letter-spacing: 1px!important;
 font-family:  Arial,"Sans-serif",Verbina,times!important;
}

/* End watch video page _______________________________________*/

/*________________________________________________________________*/

/* Search results Page ________________________________________*/

#lego-refine-block button { 
 border-top: 1px solid #96d1f8!important;
 background: -moz-linear-gradient(top, #02101a, #08324f)!important;
 padding: 5px 10px!important;
 border-radius: 8px!important;
 box-shadow: rgba(0,0,0,1) 0 1px 0!important;
 text-shadow: rgba(0,0,0,.4) 0 1px 0!important;
 color: white!important;
 font-size: 14px!important;
 font-family: Georgia, serif!important;
 text-decoration: none!important;
 vertical-align: middle!important;
}

#lego-refine-block button:hover {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
}

#lego-refine-block button:active {
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

  /* Drop down for "Filter" __________________________________*/

#search-lego-refinements div ul li {
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

#search-lego-refinements div ul li a:hover {
 border-top-color: #38080c!important;
 color: #ff0!important;
 background: -moz-linear-gradient(top, #4d0808, #1a0202)!important;
}

.lego-content-selected {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #ff0!important;
}

.lego-content-selected:hover {
 border-top-color: #28597a!important;
 background: -moz-linear-gradient(top, #02101a, #08324f)!important;
 color: #ff0!important;
}

  /* Drop down for "Sort By" _________________________________*/
.yt-uix-button-menu.yt-uix-button-menu-text {
 background: #000!important;
}

.yt-uix-button-menu.yt-uix-button-menu-text li {
 border-top-color: #38080c!important;
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

.yt-uix-button-menu.yt-uix-button-menu-text li span:hover {
 border-top-color: #38080c!important;
 color: #ff0!important;
 background: -moz-linear-gradient(top, #4d0808, #1a0202)!important;
}

#results-main-content ol {
 background: #000!important;
}

.promoted-videos.list-view.pyv-promoted-videos li {
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

.promoted-videos.list-view.pyv-promoted-videos li:hover {
 background: -moz-linear-gradient(top, #4d0808, #1a0202)!important;
}

.result-item-main-content h3 a {
 color: rgba(255,255,0,.5)!important;
 text-decoration: none!important;
}

.result-item-main-content h3 a:hover {
 color: rgba(255,255,0,1)!important;
}

.yt-uix-tile.yt-lockup-list.yt-tile-default.yt-grid-box { 
 background: #000!important;
 padding: 5px 10px!important;
}

.yt-uix-tile.yt-lockup-list.yt-tile-default.yt-grid-box:hover {
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
 color: #fff!important;
}

.yt-uix-tile.yt-lockup-list.yt-tile-default.yt-grid-box p:hover {
 color: #fff!important;
}

.yt-uix-tile.yt-lockup-list.yt-tile-default.yt-grid-box:active {
 background: -moz-linear-gradient(top, #1a0202, #4d0808)!important;
}

.yt-lockup-content h3 a, .playlist-detail-title.yt-uix-sessionlink span {
 color: rgba(255,255,0,.5)!important;
 text-decoration: none!important;
}

.yt-lockup-content h3 a:hover, .playlist-detail-title.yt-uix-sessionlink span:hover {
 color: rgba(255,255,0,1)!important;
}

/* Right column __________________________________________________*/

#pyv-ads p {
 color: #fff!important;
}

#pyv-ads div {
 background: #000!important;
}

#pyv-ads div:hover {
 background: -moz-linear-gradient(top, #08324f, #02101a)!important;
}

  /* Footer __________________________________________________*/

#footer {
 position: relative!important;
 bottom: -60px!important;
}

















/* End search results Page ____________________________________*/

/*________________________________________________________________*/










}
