css:
@-moz-document domain("fitocracy.com") {

/* ===================================
   Makes notification bubble bigger.
====================================== */

#global_notification_bubble > div {
  font-size: 22px !important;
  line-height: 22px !important;
  min-width: 66px !important;
}

#global_notification_bubble {
margin: 3px !important;
}

/* ===================================
   Reduces search (for notifications)
====================================== */

#nav_search {
  float: left;
  margin: 4px 4px 0 !important;
  width: 230px !important;
}

#nav_search input {
  width: 200px !important;
  padding: 8px 8px !important;
  padding-right: 8px !important;
}

/* ===================================
   Eliminates invite friends widget.
====================================== */

div#invite-friends-widget {
  display: none !important;
}

/* ===================================
   Bigger profile pictures in feeds.
====================================== */

.stream_item {
  padding: 15px 15px 15px 15px !important;
}

.avatar.medium {
  height: 136px !important;
  width: 136px !important;
  margin-top: 5px !important;
  margin-bottom: 5px !important;
  margin-left: 5px !important;
  margin-right: 5px !important;
}

.stream-item-right {
  margin-left: 166px !important;
  width: 432px !important;
  position: relative !important;
}

/* ===================================
   Misc improvements to visibility.
====================================== */

ul.action_detail li.pr {
  font-weight: bold !important;
  color: rgb(40,91,159) !important;
}

ul.action_detail li.stream_note {
  color: #993333 !important;
  margin: 10px 0 0 0 !important;
}

.stream-author {
  font-size: 22px !important;
  font-weight: bold !important;
}

.stream_total_points {
  font-size: 18px !important;
  font-weight: bold !important;
}

.headline-emphasis {
  font-size: 14px !important;
  font-weight: bold !important;
}

.give_prop {
  color: #295CA0 !important;
}

.unhide_comment {
  color: #295CA0 !important;
}

/* ===================================
   Made comment box fit on one line.
====================================== */

.user_comment textarea {
  width: 330px !important;
  height: 120px !important;
}

/* ===================================
   Condensing misc vertical elements.
====================================== */

.action_detail > li {
  margin-bottom: 15px !important;
}

ul.action_detail li.stream_note {
  margin: 5px 0 0 0 !important;
}

/* ===================================
   If you want to change BG colors.
====================================== */

#duel-main-wrapper,
div#big-logo-panel       { background-color: #ffffff !important; }
div#wrapper              { background-color: #ffffff !important; }
div#content              { background-color: #ffffff !important; }
div#main-content-column  { background-color: #ffffff !important; }

/* ===================================
   Fixing things that break on duels.
====================================== */

.stream-duel .face-box .date-range img {
  display: block !important;
  margin-left: 80px !important;
  margin-top: 3px !important;
  margin-bottom: 3px !important;
}

}
