css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.baidu.com") {

*{font-family:"微软雅黑" !important;}

/*请叫我博士*/

html{ background: url(data:image/png;base64,) no-repeat center 14.5em !important;

       background-position:center 60px !important;}

body{background:transparent !important;}	



/*首页面元素隐藏*/

div#m p span#sekj a,

div#m p#lg img,

div#m p#cp,

div#m p#lh,

div#m p a#setf,div#m p#lk,

div.s-content a img,

div#bottom_container.bottom.bottom_ct1 div.bottom-line1,

div.bottom-line2,

#foot,#setf{display:none !important;}



p#lm{margin-top:15px !important;}

p#lm a{color:black !important;}

/*搜索框*/

.s_ipt_wr{

border:none !important;

background:none!important;}

#kw,.s_ipt{

margin:0px 0px 0px -2px !important;

padding:0 0 0 5px !important;

width:480px !important;

height:30px !important;

line-height:30px !important;

border-color:#8c8c8c !important;

border-radius: 3px 0px 0px 3px !important;

border-width:1px !important;

border-style:solid !important;

background:none !important;

-webkit-appearance:none !Important;

-webkit-transition-duration:0.1s;}



#kw:hover,.s_ipt:hover{

border-color: #6A6A6A !important;

border-style: solid !important;

border-width: 1px !important;

box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1) inset !important;

-webkit-transition-duration:0.1s;

}



#kw:focus,.s_ipt:focus{

border:1px solid #6A6A6A !important;

box-shadow: 0 1px 2px rgba(0,0,0,0.4) inset !important;

outline: medium none !important;

}



/*百度头部*/

div#u,p#u{background:#2D2D2D !important;height:21px!important;

box-shadow: 0px 0px 2px 1px black !important;line-height:21px !important;}

div#u,p#u{padding-top:4px!important;}

div#u a,p#nv a,p#u a{color:#BBB !important;font-size:14px !important;

text-decoration:none!important; 

font-weight:bold !important;}

strong.un{color:#BBB !important;font-size:14px !important;text-decoration:none!important; 

font-weight:bold !important;background:none!important;}

p#nv b,p#u b{color:white !important;font-weight:bold !important;}

#s_username_top{background:transparent!important;}

div#m div#fm{margin:130px 0px 0px 0px !important;}

p#nv{

position:absolute !important;

top:0;

left:80px;

margin-left:-80px !important;

background:transparent!important;

padding:0px 0px 0px 0px !important;

line-height:28px !important;

}

#s_username_menu{margin-right:20px !important;}

#s_username_menu.s-username-menu a,#userMenu a{background:#2D2D2D!important; color:#BBB !important;}

#s_username_menu.s-username-menu a:hover ,#userMenu a:hover{background:gray!important; color:white !important;}



/*搜索列表*/

div{border:none !important;}

table#st{

width:467px  !important;

border:1px solid #8c8c8c !important;

box-shadow:0 0 3px 1px lightgray !important;

margin:0px 0px 0px 0px !important;

}

tr.mo td{box-shadow:0px 0px 1px 1px #8C8C8C !important;text-shadow:0px 1px 1px rgba(0,0,0,.2) !important;

}



/*位置*/

span.s_btn_wr input.s_btn{

		margin:8px 0px 0px 42px !important;

	}

 #kw.s_ipt{

		margin:9px 0px 0px 0px !important;

	}	

body div#fm form span.btn_wr input#su.btn{ 

margin:-1px 0px 0px -20px !important;

}	



.s-mod-shortcut,.s-container a,.s-topsearch-title{color:black!important;text-shadow:0px 1px 1px rgba(0,0,0,.5) !important;font-weight:bold !important;}

.s-title{box-shadow:0px 2px 3px rgba(0,0,0,0.2)  !important;border-radius:0 0 15px 15px !important;border:1px solid #bbb !important;}

.sc-dragitem{color:black !important;text-shadow:0px 1px 1px rgba(0,0,0,.5) !important;  font-weight:bold !important;}

.sc-dragitem:hover,.s-container a:hover{text-decoration:underline !important;color:black !important;}

#s_mod_shortcut_add{background:transparent !important;}

#s_mod_shortcut_add em,#s_mod_hot_more em{color:black !important;}

}

/*字体颜色*/

@-moz-document 

url-prefix("http://www.baidu.com"),

url-prefix("http://zhidao.baidu.com/q")

{

a{color:#1524E3 !important;text-decoration:none!important;}

a:hover {color:#1524E3 !important;text-decoration:underline!important;}

a:visited {color:rgb(75,26,145) !important;text-decoration:none!important;}

}



/*百度全局样式1*/

@-moz-document 

url-prefix("http://www.baidu.com"){



/*搜索按钮*/

.s_btn,#su.btn{

       margin:-1px 0px 0px -8px !important;

	  -webkit-appearance:none !important;

		color:transparent !important;

		border:1px solid #bbb !important;

		border-radius:0px 5px 5px 0px !important;

		background:url(data:image/png;base64,) 

		no-repeat center center, -webkit-linear-gradient(top, rgba(51,51,51,1) 100%, rgba(51,51,51,1) 100%) !important;

		width: 95px !important;

		height: 34px !important;}

.s_btn:hover,#su.btn:hover{box-shadow:0px 0px 2px 1px #A5A5A5 inset  !important;

-webkit-transition-duration:0.3s;}

.s_btn_wr,.btn_wr{background:transparent !important;}}



/*百度全局样式2*/

@-moz-document 

domain("news.baidu.com"),

domain("mp3.baidu.com"),

domain("image.baidu.com"),

domain("video.baidu.com"),

domain("ditu.baidu.com"),

url-prefix("http://www.baidu.com/s"),

url-prefix("http://www.baidu.com/baidu"),

url-prefix("http://www.baidu.com/more"),

url-prefix("http://www.baidu.com/gaoji/preferences")

{

/*搜索栏*/

TABLE[align="center"], table[width="100%"]{background:transparent !important;}

table.bi, div#usrbar {background:transparent !important;}

div#head {padding-left: 0 !important; width: 100% !important;}

div#head>* {margin-left: 10px !important;}

div#tool {margin: 0 !important; height: auto !important;}



/* 搜索结果*/

.rest,#wrapper{background:#F9F9F9 !important;}

#container #page{margin-top:5px !important;}

td[align="left"],.f16.EC_PP,td[align="right"],table[id] + br {display: none !important;}

td.f:hover,#wikif:hover{background:#E5F0F5 !important;}

td.f,#wikif{background-color: #F1F1F1 ! important;border-top: dotted 1px lightgray !important;

border-bottom: dotted 1px lightgray !important;border-radius:2px !important;}



.ec_pp_f , table+br , table[align="right"] , div#wrapper>table[align="right"] , div#wrapper>table[bgcolor="#f5f5f5"]

{display: none !important;}



body>div#out>div#in>div#wrapper>div#container>table

{ width: 49.8% !important;height: 116px !important;

float: left !important;border-spacing: 3px !important;

table-layout: fixed !important;overflow: auto !important; }



/*链接悬停高亮*/

a:hover, a:hover>font, td.f>a[target="_blank"]:hover>font

 { text-decoration:underline !important;

 }

/*关键词高亮*/

em, font[color="#c60a00"]

 {

  color: red !important;

  text-decoration:none !important;

}

}



/*百度图片*/

@-moz-document domain("image.baidu.com") {



 /*高亮*/

#imgid a strong 

 { text-shadow:1px 1px 1px rgba(128,128,128,.9) !important;

  color: red !important;

  text-decoration:none !important;}

}





/*百度搜索页面*/

@-moz-document url-prefix("http://www.baidu.com/s"),url-prefix("http://www.baidu.com/baidu"),

url-prefix("http://www.baidu.com/more"),url-prefix("http://www.baidu.com/gaoji"),url-prefix("http://www.baidu.com/cache/user/html/login.html")

{

html{ background:none!important;}



/*位置*/

span.s_btn_wr input.s_btn{

		margin:-1px 0px 0px -62px !important;

	}

 #kw.s_ipt{

		margin:0px 0px 0px 0px !important;

		height:32px !important;

	}

table#st{

margin:-9px 0px 0px 0px !important;

width:478px !important;

}

/*顶部头*/

#head{

background:#F1F1F1 !important;

border-bottom:1px solid #E5E5E5 !important;

padding-bottom:10px !important;

}

/*头部*/

.s_tab{

position:absolute !important;

box-shadow: 0px 0px 2px 1px black !important;

top:0px;

left:80px;

margin-left:-80px !important;

background:#2D2D2D !important;

width:99%;

height:26px!important;

padding:7px 0px 0px 10px !important;

}

.s_tab a{color:#BBB !important;

font-size:14px !important;

font-weight:bold !important;}

.s_tab b{color:white !important;font-weight:bold !important;}

.s_logo{

display:none !important;

}

#tb_mr > SMALL{

color:#BBB !important;

}

#u a{

color:#BBB !important;

font-weight:bold !important;

}

strong.un{

background:none !important;

color:#BBB !important;

font-weight:bold !important;

font-size:14px !important;

margin-right:-5px !important;

}

#u{margin-top:-3px!important;}

#u ul{background:#2D2D2D !important;}

#u ul a:hover {background:gray!important; color:white !important;}

div#u{box-shadow:none !important;}

#more{

top:30px !important;

left:330px !Important;

background:#2D2D2D !important;

height:auto !important;

width:auto !Important;

}

#more > a{

color:#BBB !important;

}

#more > a:hover {background:gray!important; color:white !important;}



/*去掉横向滚动条*/

body{overflow-x:hidden !important;width:auto !Important;height:auto !important;}



/*页码*/

p#page a,p#page strong,p#page a.n{-webkit-transition: all 0.5s ease-in 0s !important;background:#fafafa !important;

border-radius: 20px 30px 10px 10px / 70px 20px 38px 20px !important;font-size: 12px !important;

padding: 0px 6px !important;color: black !important;text-transform: uppercase !important;border:1px solid lightgray !important;}



p#page a:hover,p#page strong:hover,p#page a.n:hover

{background:#fafafa!important;border-radius: 20px 30px 10px 10px / 70px 20px 38px 20px !important;}

    

p#page a,p#page strong,p#page a.n{-webkit-transition: all 0.5s ease-in 0s !important;background:#fafafa !important;

border-radius: 70px 20px 38px 20px / 20px 30px 10px 10px !important;color:black !important;

font-size: 12px !important;margin-left: 2px !important;padding: 0px 4px !important;

text-transform: uppercase !important;border:1px solid lightgray !important;}

}



@-moz-document url-prefix("http://www.baidu.com/cache/user/") {

html{background:none !important;}

}
