css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("github.com") {
    .notification-indicator .mail-status.unread {
        -webkit-animation-name: github-warp-mail-status;
        -webkit-animation-duration: .5s;
        -webkit-animation-iteration-count: infinite;
        -webkit-animation-direction: alternate;
           -moz-animation-name: github-warp-mail-status;
           -moz-animation-duration: .5s;
           -moz-animation-iteration-count: infinite;
           -moz-animation-direction: alternate;
            -ms-animation-name: github-warp-mail-status;
            -ms-animation-duration: .5s;
            -ms-animation-iteration-count: infinite;
            -ms-animation-direction: alternate;
             -o-animation-name: github-warp-mail-status;
             -o-animation-duration: .5s;
             -o-animation-iteration-count: infinite;
             -o-animation-direction: alternate;
                animation-name: github-warp-mail-status;
                animation-duration: .5s;
                animation-iteration-count: infinite;
                animation-direction: alternate;
    }

    @-webkit-keyframes github-warp-mail-status {
        from {
            background: rgb(247, 101, 93);
            -webkit-box-shadow: 0 0 3px 0 rgb(247, 101, 93);
                    box-shadow: 0 0 3px 0 rgb(247, 101, 93);
        }
        to {
            background: rgb(65, 131, 196);
            -webkit-box-shadow: none;
                    box-shadow: none;
        }
    }
    @-moz-keyframes github-warp-mail-status {
        from {
            background: rgb(247, 101, 93);
            -moz-box-shadow: 0 0 3px 0 rgb(247, 101, 93);
                 box-shadow: 0 0 3px 0 rgb(247, 101, 93);
        }
        to {
            background: rgb(65, 131, 196);
            -moz-box-shadow: none;
                 box-shadow: none;
        }
    }
    @-ms-keyframes github-warp-mail-status {
        from {
            background: rgb(247, 101, 93);
            -ms-box-shadow: 0 0 3px 0 rgb(247, 101, 93);
                box-shadow: 0 0 3px 0 rgb(247, 101, 93);
        }
        to {
            background: rgb(65, 131, 196);
            -ms-box-shadow: none;
                box-shadow: none;
        }
    }
    @-o-keyframes github-warp-mail-status {
        from {
            background: rgb(247, 101, 93);
            -o-box-shadow: 0 0 3px 0 rgb(247, 101, 93);
               box-shadow: 0 0 3px 0 rgb(247, 101, 93);
        }
        to {
            background: rgb(65, 131, 196);
            -o-box-shadow: none;
               box-shadow: none;
        }
    }
    @keyframes github-warp-mail-status {
        from {
            background: rgb(247, 101, 93);
            box-shadow: 0 0 3px 0 rgb(247, 101, 93);
        }
        to {
            background: rgb(65, 131, 196);
            box-shadow: none;
        }
    }
}
