css:
@-moz-document domain("fitocracy.com") {
#content {
width:1200px;
}
.small-logo-panel {
padding-top:5px;
padding-bottom:5px;
}
.stream-item-right {
width:780px;
margin-left:80px;
}
.stream_item {
width:780px;
padding-top:5px;
padding-bottom:5px;
padding-left:10px;
}
.action_detail .stream_note {
margin:0px;
}
.vert-nav {
font-size:10px;
}
.vert-nav li a {
font-size:20px;
padding-left:10px;
padding-bottom:5px;
padding-top:5px;
}
.action_detail > li, .action_detail > li:first-child {
margin:5px;
}
.action_detail > li ul {
margin:0px;
margin-left:20px;
}
.action_prompt {
margin:0px;
}
.social_row {
margin:0px;
}
.comment_list {
padding:0px;
}
.user_comment {
margin:0px;
padding:0;
}
.user_comment textarea {
width:550px;
}
.user_comment_input {
padding:0px;
}
.proppers {
margin-bottom:12px;
padding-top:5px;
}
#profile-top-overview-panel .left, #profile-top-overview-panel .right {
height:50px;
}
#profile-stats-panel li .li-container {
padding-top:6px;
}
#profile-stats-panel li .stat-value {
font-size:25px;
line-height:1em;
}
#profile-stats-panel li .stat-label{
line-height:1em;
}
#profile-feed-container .main-content-header {
padding:10px;
padding-left:20px;
}

.multi-select-filter-menu li:first-child, .multi-select-filter-menu li, .multi-select-filter-menu li:last-child {
margin:0px;
padding:0px;
padding-top:1px;
}
.multi-select-filter-menu li:first-child{
padding-top:5px;
}
.multi-select-filter-menu li:last-child {
padding-bottom:5px;
}
.multi-select-filter-menu .collapsible-content .collapsible-content {
padding-left:10px;
}
.multi-select-filter-menu li a {
padding:5px;
margin-left:20px;
margin-right:20px;
}
.multi-select-filter-menu .header {
padding:10px;
margin-left:20px;
}
.dropdown-menu-sidebar-column > .header-subtitle-sidebar {
padding:5px;
padding-left:20px;
font-weight:bold;
}
#track-menu {
padding-top:5px;
}
.multi-select-filter-menu li a.collapse-trigger {
padding-left:5px;
}
.action_detail > li, .action_detail > li:first-child {
background-color:#DDDDDD;
border:thin solid #AAAAAA;
float:left;
max-width:250px;
margin:2px;
padding:2px;
}
.action_detail > li > ul > li {
line-height:12px;
}
.desc_history_content ul.action_detail li {
padding:1px;
}
.social_row {
clear:left;
}
.desc_history_content ul.action_detail li {
line-height: 17px;
}
.history_date_label {
clear:left;
}

ol.activity-form {
margin:0px;
}
.activity-form li {
margin:2px;
}
.entry-fields {
padding: 10px;
}
div.sets {
padding:0;
}
.activity-form li label {
margin:0;
}
#track-date-picker {
padding:5px;
}
#activity-search-container {
padding:5px;
}
#activity-search-container > span {
right:15px;
top:17px;
}
.sidebar-column {
padding:5px;
}
}
