css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com") {

    /* repost position fix*/
    .wall_module .published_by_wrap {
        margin: 3px 0 !important;
    }
    
    /* Remove border for tabs bar */
    #feed_bar,.summary_wrap, .fwp_summary_wrap, .t_bar {
        border:none !important;
    }
    
    /* Feed bar standard VK bar color */
    #feed_bar {
        background-color:#F7F7F7 !important;
    }
    
    /* Change edit feed tabs button and toggle search button position*/
    .feed_tab_add, .feed_tab_search {float:right !important;}
    
    /* Set tabs color */
    ul.t0 li.active_link a {
        background-color:#F7F7F7 !important;
        color:#2B587A !important;
    }
    
    input.search, input.big_text{border:1px solid transparent !important}
    
    /* Hide share button on wall */
    .wide_wall_module .post_share {
        display:none;
    }
}
