css:
@-moz-document domain("news.ycombinator.com") {
body {
    margin: 0 100px !important;
    font-family: Verdana, sans-serif;
}

font[color="#000000"] {
   color: #262323 !important;
}

a:link {
    color: #454545 !important;
}

a:visited {
    color: #828282 !important;
}

.subtext a:link,
span.comhead a:link {
    color:#00628B !important;
}

.subtext a:visited,
span.comhead a:visited {
    color: #3b8eb1 !important;
}

.title, .default, td, tr, .comment, .title {
    font-size: 100% !important;
}

.default {
    max-width: 40em;
}

.subtext {
    font-size: 70% !important;
}

.comhead {
    font-size: 80% !important;
}

.comment p {
    line-height: 1.4em;
}

table[style="padding:2px"] {
    padding-left: 2em !important;
    padding-right: 2em !important;
}

.pagetop {
    color: white !important;
    padding-left: 1em;
}

.pagetop div {
    background: #f2dede;
    border: 1px solid #eed3d7;
    color: #b94a48;
    display: inline;
    padding: 0.2em;
}

td {
    line-height: normal !important; 
}

.pagetop font {
    color:white !important;
}

a[href*="user?id="],
#profile_link {
    text-transform: none !important;
}
.pagetop a {
    color: white !important;
    font-size: 110%;
    text-shadow: -1px -1px #ADADAD;
    text-transform: capitalize;
}

.pagetop b a {
    font-size: 180% !important;
    padding-right: 120px;
}

table[width="100%"] {
    height: 82px;
    padding: 1em 0 1em 0;
}

img[src$="y18.gif"] {
    width: 24px;
    height: 24px;
}

tbody tr td table { 
    padding-left: 1em;
}

tbody tr:nth-of-type(3) {
    margin-left: 10px !important;
}
tr[style="height:10px"] {
    height: 20px !important;
}
tr[style="height:5px"] {
    height: 20px !important;
}

tr .title:first-child + td {
    padding-right: 0.5em;
}

table[bgcolor="#f6f6ef"] {
    background-color: white !important;
}
 
img[src="http://ycombinator.com/images/s.gif"] + table {
    display: none;
}

img[src="http://ycombinator.com/images/s.gif"] + table + br + center {
    background-color: #F60;
    padding-top: 1em;
    color: white;
}

textarea, td {
    font-size: 100%;
}

form[action*="/x"] input {
    font-size: 100%;
}

form[action*="/y"] table {
    font-size: 100%;
}

span.topsel a {
    font-weight: bolder;
}

#user_popup + center > table > tbody > tr:last-child >  td {
    padding-top: 4em;
}

#user_popup + center > table > tbody > tr:nth-child(3) >  td {
    padding-top: 0em;
}


.votearrow {
    background: none !important;
    width: 1em !important;
    height: 1em !important; 
    margin-left: 6px !important;
    margin-top: 0px !important;
    margin-bottom: 4px !important;
}

.votearrow:before {
    content: "â–²"; 
    color: #9a9a9a; 
    font-family: monospace; 
    font-weight: bold; 
    position: relative;
}

.votearrow:hover:before {
    color: #00628B;
}

pre {
    white-space: pre-wrap;
}

}
