css:
@-moz-document url-prefix("https://www.facebook.com/"), url-prefix("https://apps.facebook.com/") {
#blueBar {
background: -webkit-linear-gradient(top, rgba(114, 134, 179, 1) 0%,rgba(61, 88, 146, 1) 100%) !important;
z-index: 200;
}

.no_js #navAccount:hover .navigation, #navAccount.openToggler .navigation {
box-shadow: 1px 1px 15px gray;
}

.slim #blueBar {
-webkit-box-shadow: 0 1px 5px 1px rgba(0, 0, 0, .52);
}

.fbJewel a.jewelButton:hover {
-webkit-transition: background-color 1s;
background-color: rgba(255, 255, 255, 0.1);
}


.fbJewel a.jewelButton{
-webkit-transition: background-color 0.5s;
margin: 0 3px;
}

#leftCol {
background: transparent;
position: fixed;
box-shadow: 5px 0px 5px rgba(0, 0, 0, 0.14);
z-index: 100;
bottom: 0;
top: 39px;
}

.ego_column {
display: none;
}

#pageNav .navItem a:hover{
-webkit-transition: background-color 0.5s;
background-color: rgba(255, 255, 255, 0.1);
}

#pageNav .navItem a{
-webkit-transition: background-color 0.5s;
}

.pas{
display:none;
}

#navSearch {
margin: 2px 0 0 24px;
}


#jewelContainer .openToggler .jewelButton {
margin: none;
}

.uiStreamStory .uiStreamAttachments .photoRedesignAspect, .uiStreamStory .uiStreamAttachments .photoRedesignCover, .uiStreamStory .uiStreamAttachments .photoRedesignLink {
box-shadow: 2px 2px 3px rgba(0, 0, 0, 0.49);
}

.fbPhotoSnowlift .snowliftOverlayBar.rightButtons {
display: none;
}

#jewelContainer .openToggler .jewelButton {
margin: 0 2px;
}

body {
background-image: url("http://s.ytimg.com/yt/imgbin/www-refreshbg-vflC3wnbM.png");
}

.timelineLayout{
background-image: url("http://s.ytimg.com/yt/imgbin/www-refreshbg-vflC3wnbM.png");
}

div#contentCol.homeWiderContent, .homeWiderContent #bottomContent {
//box-shadow: 3px 3px 10px rgba(0, 0, 0, 0.2) inset;
}

.uiSideNavCount {
border: 1px solid;
}

.hasLeftCol .homeWiderContent div#contentArea {
padding-left: 28px;
width: 501px;
}

.uiSideNav .sideNavItem .imgWrap {
-webkit-filter: grayscale(80%);
-webkit-transition: 4s all;
}

.uiSideNav .sideNavItem:hover .imgWrap {
-webkit-filter: grayscale(0%);
-webkit-transition: .1s all;
}

ul.fbUfiNoBorder div.ufiItem, ul.fbUfiNoBorder li.ufiItem{
background: -webkit-linear-gradient(top, #F5F5F5 0%,#F0F0F0 100%) !important;
border-bottom: 1px solid white;
}


._4z0 .uiScrollableAreaBody, ._4z0, ._6jw {
width: 239px !important;
}

._4b {
width: 978px;
}

._2nc .uiScrollableAreaBody, ._2nc {
width: 740px !important;
}


}
