@namespace url(http://www.w3.org/1999/xhtml);
/*
Seesaa ãƒ–ãƒ­ã‚°
http://blog.seesaa.jp/
*/

@-moz-document domain("seesaa.net") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #e4e2d4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e4e2d4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#container
, #container div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#container * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#container a:link { color : #0000ee !important; }
#container a:visited { color : #551a8b !important; }
#container a:hover { color : #ee0000 !important; }
#container a:hover { background-color : transparent !important; }
/* /destroy div layout */

#links > .side
, #links > .sidetitle
, .hatena-bookmark-widget-footer
, .syndicate
, .syndicate + table
, .hatena-bookmark-widget-title img
, #links-left > a
, #links-left > div:first-child + div + div + div
, #links-left > div:first-child + div + div + div + div
, #links-left > div:first-child + div + div + div + div + div
, #links-left > div:first-child + div + div + div + div + div + div
, #links-left > div:first-child + div + div + div + div + div + div + div
, #links-left > div:first-child + div + div + div + div + div + div + div + div
, #links-left > div:first-child + div + div + div + div + div + div + div + div + div
, #links-left > div:first-child + div + div + div + div + div + div + div + div + div + div
{ display : none !important; }

.hatena-bookmark-widget ul li
, .hatena-bookmark-widget ul li a.hatena-bookmark-entrytitle
, .title
, .date
{ padding : 0 !important; }

.hatena-bookmark-widget ul
, .hatena-bookmark-widget ul li
, .title
, .date
{ border : 0 !important; }

.hatena-bookmark-widget ul li a.hatena-bookmark-entrytitle
{ display : inline !important; }

.hatena-bookmark-widget ul li a.hatena-bookmark-entrytitle
{ margin-right : 0.5em !important; }

#container #hatena-bookmark-widget0
, #container #comments .comments-body
, #container .blog
, #container #links-left
{ margin : 1em 0 !important; }

}
