@-moz-document domain("pc.nikkeibp.co.jp") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #e4e2d4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e4e2d4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#bs_content
, #bs_content div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#bs_content * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#bs_content a:link { color : #0000ee !important; }
#bs_content a:visited { color : #551a8b !important; }
#bs_content a:hover { color : #ee0000 !important; }
#bs_content a:hover { background-color : transparent !important; }
/* /destroy div layout */

#bs_header
, #ja-mainnav
, .bs_breadcrumb
, #bs_search
, #bs_rightcont
, #bs_footer
, #co_osusume
, #co_kiji h5
, #co_kiji a img
, #co_topicbtn
, a[href="#top"] img
, a[href*="print_leaf"]
, .arrowR a img
, .arrowL a img
, #gn_gnav ul li
{ display : none !important; }

#co_kiji li
, #co_kiji h4
, #bs_content
, h1
{ padding : 0 !important; }

#co_kiji li
, #co_kiji h4
, #bs_content
, h1
{ margin : 0 !important; }

#gn_gnav
{ width : auto !important; }

#gn_gnav
{ height : auto !important; }

#gn_gnav
{ background-color : transparent !important; }

#gn_gnav ul li:first-child
{ display : block !important; }

a > b
{ font-weight : normal !important; }

a[href="#top"]:before
{ content : "ãƒšãƒ¼ã‚¸ãƒˆãƒƒãƒ—ã¸"; }

.arrowR a:before
{ content : "æ¬¡ã¸"; }

.arrowL a:before
{ content : "å‰ã¸"; }

}
