css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://mangafox.me/manga/") {

#header {
    background:#3a3a3a none !important;
    position:relative !important;
    top:-75px !important;
    z-index:10 !important;
    padding-bottom:15px !important;
}

#header:hover {
    background:#5a5a5a none !important;
    position:relative !important;
    top:0px !important;
}

body {
    background-image: url("/*[[backgroundimg]]*/") !important;
}

.ad, #tip, #chnav, #pgnav, #related, #tags, #footer, #shares, #tool, .page #top_right_bar, .page #top_center_bar, .page #top_left_bar, .page a.prev_page, .page a.next_page, #bookmark, #comments, .news-block-magick{
    display:none !important;
}

.page #top_right_bar, .page #bottom_right_bar, .page #top_center_bar, .page #bottom_center_bar, .page #top_left_bar, .page #bottom_left_bar{
    background: none !important;
}

.widepage.page{
top:-100px !important;
position:relative !important;
}

.l{
color:#fff !important;
}

#viewer{
position:relative !important;
top:-85px !important;
border:0px solid rgba(0,0,0,0.3) !important;
padding:0px !important;
background:transparent !important;
}

#menu li{
border-radius:0.3em !important;
margin-right:5px !important;
}

#menu li a:hover{
border-radius:0.3em !important;}

#menu li{
background:#fff !important;
}

#menu li a{
padding-top:8px !important;
height:26px !important;
}

#bottom_chapter_list, select.m{
background:#1a1a1a !important;
border-width:0px !important;
box-shadow: rgba(0, 0, 0, 0.6) 0px 1px 3px 0px inset, rgba(255, 255, 255, 0.05) 0px 0px 0px 1px, rgba(255, 255, 255, 0.1) 0px 1px 0px 0px !important;
}

}
