css:
@-moz-document url-prefix("https://www.google.com/calendar/") {

/* Globals */
body { background: #505667 !important; }
div#calcontent { background: #505667 !important; }

/* Header */
div#gbx1 { height: 50px !important; }
div#gbq1 { margin-left: 13px !important; line-height: 15px !important; }
div#gbq2, div#gbu { padding-top: 10px !important; }
span.gbem { margin-top: -10px !important; }
div#vr-nav { background: #505667 !important; border-bottom: none !important; }
div#vr-nav div.applogo { left: 10px !important; }
span#mainlogo { color: #FFF !important; font-family: calibri, sans-serif !important; font-size: 18pt !important; }
div#mainnav { background: #505667 !important; }
div.trans-strip { display: none !important; }
div.date-top { color: #FFF !important; font-family: calibri, sans-serif !important; font-size: 12pt !important; }

/* Left Nav */
#nav, div#nav { width: 160px !important; margin-left: 14px !important; padding-right: 5px !important;}
#sidebar { width: 160px !important; margin-left: -5px !important; padding-left: 0px !important; }
div#sidebar div.qnb-container div.goog-imageless-button { background: #C6322A !important; }

/* Left Nav - Month Box - Inactive */
h2#dp_h { width: 165px !important; margin-left: -5px !important; }

/* Left Nav - Month Box - Active */
div#dp_0 { width: 165px !important; margin-left: -5px !important;  }
div.monthtableSpace { width: 165px !important; margin-right: 0px !important; margin-left: 0px !important;}
tr#dp_0_header {  }
td#dp_0_cur { margin-left: 5px !important; margin-right: 0px !important; padding-left: 0px !important; padding-right: 0px !important;}
td.dp-cell td.dp-sb-nav { padding-right: 0px !important; width: 10px !important; }
table#dp_0_tbl { padding-left: 0px !important; }

/* Left Nav - My Calendars Box */
h2#clst_my { width: 165px !important; margin-left: -5px !important; }
div#lhscalinner_my { width: 165px !important; margin-left: -5px !important; }
div#calendars_my { margin: 0px !important; }
div.calListLabelOuter { padding-left: 5px !important; padding-right: 0px !important; }

/* Left Nav - Other Calendars Box */
h2#clst_fav, div#lhscalinner_fav { width: 165px !important; margin-left: -5px !important; }
div#calendars_fav { margin: 0px !important; }

/* Main Content */
div#gridcontainer { margin-right: 18px !important;  background: url("http://i.imgur.com/egJTG.jpg") !important; background-repeat:no-repeat !important; background-position: 0% 0px !important; }

/* Main Content - Month view */
div#mvEventContainer, div.mv-container { background: transparent !important; }
div.mv-container { border-right: 1px solid #DDD !important; margin-left: 0px !important; margin-right: 0px !important; }
div#mvEventContainer { border-right: none !important; top: 19px !important; border-top: 1px solid #666 !important; }
th.mv-dayname { padding-top: 4px !important; line-height: 15px !important; }
td.st-dtitle-nonmonth span { color: #CCC !important; }
td.st-dtitle { background: rgba(40, 110, 150, .7) !important; }
td.st-bg { background: rgba(255, 255, 255, .8) !important; }

/* Main Content - Week view - Top section */
table.wk-weektop, tr.wk-daynames { background: #FFF !important; }
tr.wk-daynames th { line-height: 10px !important; }
tr.wk-daynames div.wk-today { background: #DFDFDF !important; }
table#weekViewAllDayBgwk td.st-bg, table#weekViewAllDayBgwk td.st-bg-today, td.wk-allday, div#weekViewAllDaywk { background: transparent !important; }
tr.wk-daynames div.wk-dayname { border-left: 1px solid #AAA !important; border-bottom: 1px solid #AAA !important; }
table#weekViewAllDayBgwk td.st-bg-lc { border-right: 1px solid #AAA !important; }
table#weekViewAllDayBgwk { border: none !important; }
table#weekViewAllDayBgwk td.st-bg, table#weekViewAllDayBgwk td.st-bg-today, table#weekViewAllDayBgwk td.st-bg-next, td.wk-webcontent-td { border-left: 1px solid #AAA !important; }
tr.wk-webcontent { display: none !important; }

/* Main Content - Week view - Main section */
div#scrolltimedeventswk { background: rgba(255, 255, 255, .8) !important; border-top: 1px solid #AAA !important; border-bottom: 1px solid #CCC !important; }
table#tgTable, table#tgTable td.tg-times-pri, td.tg-col-today, div.tg-today { background: transparent !important; }
table#tgTable td.tg-col, table#tgTable td.tg-col-today { border-left: 1px solid #AAA !important; }
table#tgTable div.tg-dualmarker { border-top: 1px solid #BBB !important; border-bottom: 1px solid #BBB !important; }
div.tg-time-pri { border-bottom: 1px solid #BBB !important; }

/* Main Content - 7 Days view */
td.st-bg-td-first { background: transparent !important; border-left: 1px solid #AAA !important; }

/* Main Content - Agenda view */
div.lv-gridcontainer { background-position: 50px 20px !important; }
div#lvHeader { background: #FFF !important; margin-right: 18px !important; border-bottom: 1px solid #666 !important; padding-bottom: 4px !important; line-height: 9px !important; }
span#collapse_all_link, span#expand_all_link { font-size: 12px !important; }
div#lv_listview { background: rgba(255, 255, 255, .8) !important; }
table.listv, div#lv_listview table.listv tr { background: transparent !important; }

}
