css:
/* 
Style by Kinky
http://games.on.net/forums/memberlist.php?mode=viewprofile&un=Kinky__ 
*/

@-moz-document url-prefix("http://games.on.net/forums/"){

    /* Containers */
    #phpbb {
        background-image:url("http://i48.tinypic.com/zk1il0.jpg") !important;
    }
    #skin_left, #skin_right, a.skin { 
        width:0px !important; overflow:hidden; 
    }
    #body_container, #overall_container { 
        width: 960px !important; 
        background-color:#FFF !important;
    }
        
    /* Userbar */
    #mspace-block { 
        background-color:none !important; 
        overflow:hidden !important; 
        float:left !important;
        padding:0px !important;
        height:70px !important;
        width:900px !important;
    }
    #mspace-block LI {
        background-color:none !important; 
    }
    
    /* Logged in */
    #mspace-block LI.mspace {
    
        float:left !important;
        box-shadow:0px 0px !important; 
        border:1px solid #CCC !important;
        padding: 0px !important;
        min-height:34px !important;
        width: 898px !important;
        height:34px !important;
        overflow:hidden !important;
        clear:both !important;
    }
    LI.mspace P.username {
        margin:0px !important;
        padding:0px !important;
    }
    LI.mspace P,
    LI.mspace A {
        float:left;
        padding:0px 10px !important; 
        line-height:34px !important;
        height:34px !important;
        border-right:1px solid #CCC !important;
    }
    LI.mspace P.self BR, LI.mspace P.avatar { 
        display:none !important; 
    }
    LI.mspace P.self { 
        display:inline-block !important;
        overflow:hidden !important;
        margin:0px !important;
        padding:0px !important;
        color:#FFF !important; 
    }
    LI.mspace P.self A { 
        color:#333333 !important;
    }
    LI.mspace P:last-child {
        border-right:none !important;
    }
    LI.mspace P.self A:hover {
        background-color:#222 !important; 
        color: #FFF !important;
        text-decoration:none !important;
    }
    
    /* News -- Default: Off */
    LI.mspace_news {
        background:none !important;
        width:900px !important;
        overflow:hidden !important;
        margin-bottom:10px !important;
    }
    
    LI.mspace_news P.latest_news, LI.mspace_news P.news {
        display: none !important;
    }
    
    /** Post Tools -- default: on **/
    
    LI.mspace_news .latest_posts {
        display:inline-block !important;

        clear:left !important;
        display:none !important;
    }
    
    LI.mspace_news .post_tools {
        margin-left:105px !important;
        margin-top:10px !important;
        display:inline-block !important;
    }
    LI.mspace_news .post_tools A {
        background:none !important;
        color:#333 !important;
        padding-left:15px !important;
        padding-right:15px !important;
        border:1px solid #CCC !important;
    }
    LI.mspace_news .post_tools A:hover {
        background:#222 !important;
        color:#FFF !important;
    }

    /* Brand Bar */
    #brand_bar_and_login {
        height:1px !important;
        margin-top:-43px !important;
        margin-bottom:43px !important;
    }
    #brand_bar {
        display:none !important;
    }
    #login {
        position:absolute !important;
        top:125px !important;
        right:20px !important;
    }
    
    /* Breadcrumbs */
    #page-header .navbar {
        float:left !important;
        z-index:3;
        margin-top:0px !important;
        margin-bottom:0px !important;
        background: none !important;
    }
    #page-header .navbar LI,
    #page-header .navbar LI A{
        color: #333 !important;
    }
    #page-header .navbar LI A:first-child { padding-left:0px !important; }
    #page-header .navbar LI A {
        display:inline-block !important;
        line-height:25px !important;
        padding: 0 10px !important;
    }
    #page-header .navbar LI A:last-child {
        color: #536482 !important;
        font-family: "Trebuchet MS",georgia,Verdana,Sans-serif !important;
        font-size:18px !important;
    }
    
    /* Topic List */
    .section-viewforum #page-body div:first-child {
        clear:both !important;
    }
    .section-viewforum H2 {
        display:none !important;
    }
    #page-body DIV.rules {
        display:none !important;
    }
    .topiclist.topics * IMG {
        display:none !important;
    }
    #page-body .topic-actions .search-box INPUT {
        display:inline-block !important;
        line-height:30px !important;
        background-position:4px center !important;
        padding:0 10px !important;
    }
    #page-body .topic-actions .search-box INPUT:first-child {
        padding-left:25px !important;
        padding-right:0px !important;
    }
    .topic-actions .pagination {
        margin-top:0px !important;

    }
    .topic-actions .pagination A.markread {
        line-height:19.6px !important;
        margin-left:10px !important;
    }
    .topic-actions .pagination A,
    .topic-actions .pagination SPAN {
        line-height:37px !important;
    }
    .topic-actions .pagination SPAN A,
    .topic-actions .pagination SPAN STRONG {
        padding:5px !important;
    }
	
	/* Icons */
    
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/sticky_read.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/sticky_read_mine.gif); background-repeat: no-repeat;']{
        background-image:none !important;
        opacity:.7 !important;
    }
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/sticky_read.gif); background-repeat: no-repeat;'] *,
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/sticky_read_mine.gif); background-repeat: no-repeat;'] *{
        font-weight:normal !important;
        color:#444 !important;
    }
    
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/sticky_unread.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/sticky_unread_mine.gif); background-repeat: no-repeat;']{
        background-image: url('data:image/png;base64,') !important;
    }
    
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/announce_unread.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/announce_read.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/announce_unread_locked.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/announce_read_mine.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/announce_read_locked.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/sticky_unread_locked.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/sticky_read_locked.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/sticky_read_mine_locked.gif); background-repeat: no-repeat;']{
        background-image: url('data:image/png;base64,') !important;
    }
      
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_read.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_read_mine.gif); background-repeat: no-repeat;']{
        background-image:none !important;
        opacity:.7 !important;
    }
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_read.gif); background-repeat: no-repeat;'] *,
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_read_mine.gif); background-repeat: no-repeat;'] *{
        font-weight:normal !important;
        color:#444 !important;
    }

    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_moved.gif); background-repeat: no-repeat;'] {
        background-image: url('data:image/png;base64,') !important;
        opacity:.7 !important;
    }
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_moved.gif); background-repeat: no-repeat;'] .topictitle:before {
        content: "Moved: ";
    }
    
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_unread.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_unread_hot.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_unread_mine.gif); background-repeat: no-repeat;']{
        background-image:none !important;
    }
    
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_read_hot.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_read_hot_mine.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_unread_hot_mine.gif); background-repeat: no-repeat;']{
        background-image:url("data:image/png;base64,") !important;
        opacity:1 !important;
    }
    
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_read_locked.gif); background-repeat: no-repeat;'],
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_unread_locked.gif); background-repeat: no-repeat;'] {
        background-image:none !important;
    }
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_read_locked.gif); background-repeat: no-repeat;'] *,
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_unread_locked.gif); background-repeat: no-repeat;'] * {
        opacity:.7 !important;
        text-decoration:line-through;
    }
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_read_locked.gif); background-repeat: no-repeat;'] .topictitle:before,
    .topiclist.topics DL[style='background-image: url(./styles/gon2012/imageset/topic_unread_locked.gif); background-repeat: no-repeat;'] .topictitle:before {
        content: "Locked: ";
    }
    
    /* BUTTONS */
    .topic-actions .markread {
        float:right;
        display:inline-block !important;
        color:#FFF !important;
         text-transform: uppercase;
         font-size: 14px !important;
         padding: 7px !important;
         background: #29a64a;
         background: -moz-linear-gradient(top,#73bf44 0%,#29a64a);
         background: -webkit-gradient(linear, left top, left bottom, from(#73bf44),to(#29a64a));
         border-radius: 5px;
         border: 2px solid #72c44a;
            -moz-box-shadow:	0px 0px 0px rgba(68,123,42,0),inset 0px 0px 1px rgba(255,255,255,1);
        	-webkit-box-shadow: 0px 0px 0px rgba(68,123,42,0), inset 0px 0px 1px rgba(255,255,255,1);
         text-shadow:
        	0px 1px 0px rgba(68,123,42,1),
        	0px -1px 0px rgba(68,123,42,1);
    }
    .topic-actions .markread:hover {
        text-decoration: none !important;
         background: -moz-linear-gradient(top,#29a64a 0%,#73bf44);
         background: -webkit-gradient(linear, left top, left bottom, from(#29a64a),to(#73bf44));
    }
    P A.left-box {

        clear:both !important;
        display:inline-block !important;
        font-size: 14px !important;
        text-align: center !important;
        line-height:30px !important;
        padding:0 7px !important;
        background: -moz-linear-gradient(top,#222 0%,#333) !important;
        background: -webkit-gradient(linear, left top, left bottom, from(#29a64a),to(#73bf44)) !important;
        color: #FFF !important;
        font-weight:600 !important;
        border-top:1px solid #999 !important;
        margin-bottom: 10px !important;
        opacity:.9 !important;
        border-radius: 5px;
        text-transform:uppercase !important;
    }
    
    P A.left-box:hover {
        opacity:1 !important;
    }
}
