/* By Yog for pardus MMOG community 
- except all pardus mods. They are not allowed to use all or parts of this user style. This is because of their clear actions against development of this user style. */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("pardus.at") {

/* Configuration */
iframe#ChatFrame {
height:365px !important;
  /* Set there height of your chat iframe. */
}
body img[title~="dock."] {
background:url(http://psgrafika.republika.pl/pardus/spacecraftrepairswide.png) !important; 
  /* see http://stud3.tuwien.ac.at/~e0326004/pardus/dockings/ for 
  backgrounds of other ships. Be aware that their author doesn't 
  like when you hotlink them directly and eat his bandwith, so you 
  may want to host it by yourself, on imageshack maybe. */
padding:286px 850px 0px 0px !important; 
  /* Set height and width of the dock image there. The first number 
  is height and the second is width. */
background-position:center center !important;
}

input[value="maneuver"]+input {
display:none !important;  /*Remove this line if you want to exchange Maneuver for ASP*/
} 

/*
body, body table, body div, a {
font-family:teuton normal CE !important;
}
table {
font-size:1.05em !important;
}
div.postcolor,
div.maintitle {
font-size:1.2em !important;
}
*/
  /* Remove comment markings from the above rules if you have 
  teutonnormalce.ttf installed on your system
  http://psgrafika.republika.pl/pardus/teutonnormalce.ttf */

/* End of configuration */

body {
background:url(http://psgrafika.republika.pl/background.png) #111 !important;
color:#A6D1F6 !important;
margin-top:0px !important;
margin-bottom:0px !important;
}
table {
color:#A6D1F6 !important;
}

/*nav fields*/
td[valign="top"]:first-child+td[valign="top"] > table[cellspacing="0"][cellpadding="0"][border="0"][align="center"] {
position:relative;
z-index:15;
background:url(http://psgrafika.republika.pl/navborder.png);
background-repeat:no-repeat;
background-position: center center;
padding:10px 8px 10px 8px;
margin-top:-18px;
height:100%;
}
table#navarea {
border-collapse:collapse;
width:255px !important;
height:255px !important;
background:#082338 !important;
}
table#navarea td {
border:1px solid #111 !important;
background-repeat:no-repeat !important;
background-position:center center !important;
}
table#navarea a {
display:block !important;
list-style:none !important;
}
table#navarea a:hover {
background:url(http://psgrafika.republika.pl/navcoordbg1.png) !important;
display:block;
}
/* hover on tiles without a building - may slow down frame refresh rate */
table#navarea a img.nf:hover {
opacity: .5;
}
table#navarea td[style*="background-image"] a img.nf:hover {
opacity: 1.0;
}
table#navarea tr:first-child+tr+tr+tr td:first-child+td+td+td a {
background:url(http://psgrafika.republika.pl/opacity20.png) !important;
background-position:center center !important;
}



/* headers */
.first, th {
border:1px #172C46 solid !important;
background:url(http://psgrafika.republika.pl/button1.png) !important;
padding:6px 0px 4px 0px !important;
font-variant:small-caps !important;
font-weight:bold !important;
font-size:15px !important;
color:#00001C !important;
background-repeat:repeat-x !important;
background-position: center !important;
white-space:nowrap !important;
}
.messagestyle th, th[width='180'], tr.first td {
border-left:2px #00396E ridge;
border-right:2px #00396E groove;
background:url(http://psgrafika.republika.pl/button1.png) !important;
background-repeat:repeat !important;
background-position: center !important;
padding:2px 0px 2px 0px !important;
}
th[style*="white-space: nowrap"] {
background:url(http://psgrafika.republika.pl/background2.png) !important;
color:#A6D1F6 !important;
}
h1:first-letter {
border-bottom:3px #2776C3 solid;
border-right:1px black solid;
border-left:1px black solid;
padding:0px 30px 0px 10px !important;
margin-right:-30px;
color:#C1EAFF;
line-height:0px;
}
h1 {
background:url(http://psgrafika.republika.pl/thingy1.png);
background-repeat:no-repeat !important;
background-position:-20px top !important;
padding:28px 10px 15px 12px !important;
text-transform:capitalize;
font-variant:small-caps;
font-size:28px !important;
color:#70D1E2;
display:table-cell !important;
letter-spacing:2px !important;
border-bottom:3px #A2D3EA solid;
line-height:0px;
}
p strong {
background:url(http://psgrafika.republika.pl/thingy2.png) !important;
background-repeat:no-repeat !important;
background-position: 0px center !important;
padding:28px 20px 30px 44px !important;
color:#98E4F4;
display:table !important;
letter-spacing:1px !important;
opacity:.9;
}
.messagestyle h1 {
background-image:none !important;
padding:0px !important;
display:table !important;
text-transform:none;
letter-spacing:0.18em !important;
border-bottom:0px;
}
h3 {
background-image:url(http://psgrafika.republika.pl/thingy4.png) !important;
background-repeat:no-repeat !important;
background-position: 0px center !important;
padding: 12px 20px 6px 44px !important;
color:#CBFFF8;
display:table !important;
letter-spacing:-0.6px !important;
opacity: .9;
}
h4,
center h4~h3 {
background:url(http://psgrafika.republika.pl/opacity20.png) !important;
padding:10px !important;
font-size:16px !important;
width:95% !important;
opacity:1;
}
body[onload*="resizeChatWnd()"] h1 {
position:absolute !important;
top:-11px;
right:6px;
text-align:right !important;
padding-right:200px !important;
}
hr {
height:0px !important;
border-width:0px !important;
opacity: .5;
border-bottom:#255EBB dashed 2px !important;
background-color:#11286A !important;
}
center table.messagestyle[style*="bgd.gif"] th {
background:url(http://psgrafika.republika.pl/opacity70.png) !important;
padding:1px !important;
}
th[colspan="4"] > table[cellpadding="2"][align="center"] th[valign="center"] {
padding:0px 8px 0px 8px !important;
border-width:0px !important;
background:none !important;
vertical-align:bottom !important;
}
th[colspan="4"] > table[cellpadding="2"][align="center"] span:first-child {
font-size:22px !important;
color:#00001C !important;
}
th[colspan="4"] > table[cellpadding="2"][align="center"] span {
font-size:14px !important;
color:#ABD3FF !important;
line-height:130% !important;
}
span.cached {
font-family:impact, tahoma, verdana !important;
font-size:12px !important;
color:#6DB2FF !important;
font-weight:normal !important;
letter-spacing:1px !important;
border-left:17px solid #4A94D5 !important;
outline:#366590 1px solid !important;
background-color:#18365A !important;
padding:0px 4px 1px 3px !important;
margin-left:-11px !important;
float:left !important;
opacity:.8 !important;
}
span.cached br {
display:none;
}
*[style*="rgb(161, 161, 175)"] {
background-color:#71ADFC !important;
border-left:15px double #378FD3 !important;
border-right:15px double #378FD3 !important;
padding:1px 2px 0px 2px !important;
}

/* bulletin boards */
table.messagestyle[width='95%'] td[height='125'][style*='500px']+td,
table.messagestyle[width='95%'] td[height='125'][style*='width:500px']+td+td {
height:0px !important;
display:none;
}
table.messagestyle[width='95%'] tr:first-child table[width='100%'][cellspacing="0"][cellpadding="0"] {
height:0px !important;
display:block;
}
table.messagestyle[width="95%"] td[height="125"][style*="500px"] {
height:0px !important;
display:block;
}
img[src*='/fedbb1.png'],
img[src*='/impbb1.png'],
img[src*='/unibb1.png'],
img[src*='/bb1.png'] {
background-repeat:no-repeat !important;
width:0px !important;
height:0px !important;
position:absolute !important;
top:142px !important;
right:0px !important;
}
img[src*='/fedbb1.png'] {
background-image:url(http://psgrafika.republika.pl/pardus/bbf.png) !important;
padding:580px 192px 0px 0px !important;
}
img[src*='/impbb1.png'] {
background-image:url(http://psgrafika.republika.pl/pardus/bbe.png) !important;
padding:580px 185px 0px 0px !important;
}
img[src*='/unibb1.png'] {
background-image:url(http://psgrafika.republika.pl/pardus/bbu.png) !important;
padding:580px 177px 0px 0px !important;
}
img[src*='/bb1.png'] {
background-image:url(http://psgrafika.republika.pl/pardus/bbn.png) !important;
padding:580px 180px 0px 0px !important;
}
img[src*='/fedbb3.png'],
img[src*='/impbb3.png'],
img[src*='/unibb3.png'],
img[src*='/bb3.png'] {
display:none;
}

#tipBox~table.messagestyle[width="70%"] > tbody {
background:url(http://psgrafika.republika.pl/bbfog.png) 100px bottom repeat-x !important;
border:1px black solid !important;
display:block;
height:76px !important;
position:relative;
}
#tipBox~table.messagestyle[width="70%"] > tbody > tr:first-child th {
background:url(http://psgrafika.republika.pl/bbfog.png) -50px bottom repeat-x !important;
border-bottom:3px #373737 double !important;
font-size:16px !important;
padding:0px !important;
}

/*emp*/
table.messagestyle[style*="bglight.gif"][style*="187, 0, 0"],
table.messagestyle[style*="bglight.gif"][style*="#BB0000"] {
border:2px #B13B2F solid !important;
background-color:#2F2929 !important;
color:#EBDCD9 !important;
}
#tipBox~table.messagestyle[style*="bglight.gif"][style*="187, 0, 0"] > tbody,
#tipBox~table.messagestyle[style*="bglight.gif"][style*="#BB0000"] > tbody {
background-color:#342021 !important;
}
#tipBox~table.messagestyle[style*="bglight.gif"][style*="187, 0, 0"] > tbody > tr:first-child th,
#tipBox~table.messagestyle[style*="bglight.gif"][style*="#BB0000"] > tbody > tr:first-child  th {
background-color:#7B4141 !important;
color:#301010 !important;
}
table.messagestyle[style*="bglight.gif"][style*="187, 0, 0"] div[id*="m"] div[id*="m"] a,
table.messagestyle[style*="bglight.gif"][style*="#BB0000"] div[id*="m"] div[id*="m"] a {
border-color:#B13B2F;
background:#312D2D !important;
}

/*uni*/
table.messagestyle[style*="bglight.gif"][style*="255, 204, 0"],
table.messagestyle[style*="bglight.gif"][style*="#FFCC00"] {
border:2px #B18E2F solid !important;
background-color:#303129 !important;
color:#EAEADA !important;
}
#tipBox~table.messagestyle[style*="bglight.gif"][style*="255, 204, 0"] > tbody,
#tipBox~table.messagestyle[style*="bglight.gif"][style*="#FFCC00"] > tbody {
background-color:#303129 !important;
}
#tipBox~table.messagestyle[style*="bglight.gif"][style*="255, 204, 0"] > tbody > tr:first-child th,
#tipBox~table.messagestyle[style*="bglight.gif"][style*="#FFCC00"] > tbody > tr:first-child  th {
background-color:#645738 !important;
color:#301010 !important;
}
table.messagestyle[style*="bglight.gif"][style*="255, 204, 0"] div[id*="m"] div[id*="m"] a,
table.messagestyle[style*="bglight.gif"][style*="#FFCC00"] div[id*="m"] div[id*="m"] a {
border-color:#B18E2F;
background:#34312C !important;
}

/*fed*/
table.messagestyle[style*="bglight.gif"][style*="0, 0, 187"],
table.messagestyle[style*="bglight.gif"][style*="#0000BB"] {
border:2px #2F75B1 solid !important;
background-color:#2D3135 !important;
color:#DAE4EA !important;
}
#tipBox~table.messagestyle[style*="bglight.gif"][style*="0, 0, 187"] > tbody,
#tipBox~table.messagestyle[style*="bglight.gif"][style*="#0000BB"] > tbody {
background-color:#2D3135 !important;
}
#tipBox~table.messagestyle[style*="bglight.gif"][style*="0, 0, 187"] > tbody > tr:first-child th,
#tipBox~table.messagestyle[style*="bglight.gif"][style*="#0000BB"] > tbody > tr:first-child  th {
background-color:#455E71 !important;
color:#101C2E !important;
}
table.messagestyle[style*="bglight.gif"][style*="0, 0, 187"] div[id*="m"] div[id*="m"] a,
table.messagestyle[style*="bglight.gif"][style*="#0000BB"] div[id*="m"] div[id*="m"] a {
border-color:#2F75B1 ;
background:#2C2F32 !important;
}

/*neu*/
table.messagestyle[style*="bg_grey.gif"][style*="160, 177, 201"],
table.messagestyle[style*="bg_grey.gif"][style*="#A0B1C9"] {
border:2px #777 solid !important;
background-color:#222 !important;
color:#C0D4D4 !important;
}
#tipBox~table.messagestyle[style*="bg_grey.gif"][style*="160, 177, 201"] > tbody,
#tipBox~table.messagestyle[style*="bg_grey.gif"][style*="#A0B1C9"] > tbody {
background-color:#222 !important;
}
#tipBox~table.messagestyle[style*="bg_grey.gif"][style*="160, 177, 201"] > tbody > tr:first-child th,
#tipBox~table.messagestyle[style*="bg_grey.gif"][style*="#A0B1C9"] > tbody > tr:first-child th {
background-color:#535353 !important;
color:#111111 !important;
}
table.messagestyle[style*="bg_grey.gif"][style*="160, 177, 201"] div[id*="m"] div[id*="m"] a,
table.messagestyle[style*="bg_grey.gif"][style*="#A0B1C9"] div[id*="m"] div[id*="m"] a {
border-color:#777;
background:#2D312F !important;
}

p+#tipBox~table.messagestyle[style*="bg"] {
background-image:none !important;
outline:2px black solid !important;
margin:5px 10px 5px 0px !important;
padding:0px !important;
border-spacing:0px !important;
width:504px !important;
height:78px !important;
float:left;
overflow:hidden;
display:block;
font-size:6px !important;
}
p+#tipBox~table.messagestyle[style*="bg"] b {
font-size:13px !important;
vertical-align:middle;
}
p+#tipBox~table.messagestyle[style*="bg"] tr:first-child+tr td:first-child[valign='top'][align='center'] b {
display:block;
width:100px;
text-align:center;
position:absolute;
bottom:14px;
}
p+#tipBox~table.messagestyle[style*="bg"] tr+tr+tr td[valign='top'][align='center'] b {
display:block;
width:100px;
text-align:center;
position:absolute;
bottom:0px;
}
p+#tipBox~table.messagestyle[style*="bg"] div[id*="s"] {
display:block !important;
position:absolute;
right:120px;
bottom:0px;
padding:2px 0px 0px 4px !important;
width:250px;
font-size:13px !important;
font-variant:small-caps !important;
border-width:1px 1px 0px 1px !important;
border-style:solid !important;
border-color:#666 !important;
outline:1px black solid !important;
background-color:#2D312F;
opacity:.9;
}
p+#tipBox~table.messagestyle[style*="bg"] div[id*="s"] font {
color:#FF536F !important;
font-weight:bold;
font-size:15px;
vertical-align:middle;
}
p+#tipBox~table.messagestyle[style*="bg"]+br {
display:none !important;
}
p+#tipBox~table.messagestyle[style*="bg"] img[height='64'] {
height:36px !important;
margin-top:-5px;
}

p+#tipBox~table.messagestyle div[id*="m"] div[id*="m"] a {
display:block !important;
position:absolute;
right:0px;
bottom:0px;
padding:0px 0px 0px 10px !important;
width:70px;
line-height:24px;
font-size:19px !important;
font-variant:small-caps !important;
letter-spacing:0.15em !important;
border-width:1px 0px 0px 1px !important;
border-style:solid !important;
outline:1px black solid !important;
color:#A0ECD4 !important;
opacity:.9;
}
p+#tipBox~table.messagestyle div[id*="m"] div[id*="m"] a:hover {
text-decoration:none !important;
outline:0px #555 solid !important;
color:#CBFFF8 !important;
}
p+#tipBox~table.messagestyle[style*="bg"] div[id*="m"] a b {
font-size:19px !important;
}
p+#tipBox~table.messagestyle div[id*="m"]>font[color="green"],
table.messagestyle div[id*="m"]>font[color="red"] {
display:block !important;
position:absolute;
right:0px;
bottom:0px;
font-size:17px !important;
padding:2px 4px 1px 4px !important;
font-variant:small-caps !important;
letter-spacing:0.15em !important;
}





/* tables */
#sectioncontent2 > table[cellpadding="0"][border="0"] {
overflow:visible !important;
padding:1px !important;
margin-right:10px !important;
border-spacing:0px !important;
background-color:#010911;
background-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAASwAAAABCAIAAADrWgR%2BAAAAK3RFWHRDcmVhdGlvbiBUaW1lAIxyIDI5IGt3aSAyMDA5IDIwOjMxOjU0ICswMTAwhZP%2FyQAAAAd0SU1FB9kEHRIpCPsprgIAAAAJcEhZcwAACxIAAAsSAdLdfvwAAAAEZ0FNQQAAsY8L%2FGEFAAAAFElEQVR42mNsaGhgGAWjYBQMHAAAS9UBgvw%2Bu9YAAAAASUVORK5CYII%3D);
background-position:782px !important;
background-repeat:repeat-y !important;
}
#sectioncontent2 > table[cellpadding="0"][border="0"] > tbody {
overflow:visible !important;
background-color:#010911;
padding:4px 4px 4px 0px !important;
display:block !important;
}
.tabstyle {
background:url(http://psgrafika.republika.pl/background.png) !important;
border:1px #2871C2 inset !important;
color:#9FD3F7 !important;
padding:0px 2px 0px 2px !important;
}
script+table,script+table table {
height:10px !important;
overflow:hidden !important;

}
.messagestyle {
border:1px #2871C2 solid !important;
color:#A6C3D0 !important;
background:rgba(6,26,78,0.65) !important;
}
table.messagestyle[background*='bgd.gif'] {
border:1px #375799 solid !important;
}
table[background*='bgd.gif'],
th[nowrap=""],
table.std_table th,
center table.messagestyle[style*="bgd.gif"] {
background:url(http://psgrafika.republika.pl/background2.png) !important;
color:#BBE1FF !important;
background-position: 110px 110px !important;
}
div.event-text {
background:url(http://psgrafika.republika.pl/background.png) !important;
margin-top:4px !important;
border-top:2px #2871C2 dotted !important;
opacity: .9;
}
div.event-text:first-letter {
font-size:36px !important;
float:left;
padding:3px 2px 3px 0px;
margin:0px 1px -2px -2px ;
background:url(http://psgrafika.republika.pl/marker2.png) !important;
background-position: 7px !important;
background-repeat:repeat-y !important;
}
td[width="20%"] td.marginstyle {
background:url(http://psgrafika.republika.pl/background2.png) !important;
border:1px #172C46 solid !important;
border-top:0px !important;
}
.messagestyle[width="966"] tr[valign="top"] {
background:url(http://psgrafika.republika.pl/marker1.png) !important;
background-repeat:repeat-y !important;
background-position: center center !important;
}
td[width="15%"][style^="padding: 0.2cm; vertical-align: top;"] {
margin-top:-115px !important;
display:table !important;
}
.messagestyle[width="95%"] {
width:100% !important;
}

/* Faction Relations */
table[width="380"][border="1"],
table[width="800"][cellpadding="5"][border="1"],
table[width="740"][cellpadding="0"][border="1"] {
background:url(http://psgrafika.republika.pl/background2.png) !important;
background-position:center center !important;
border:1px solid #304C6C !important;
border-spacing:3px !important;
}
table[width="380"][border="1"]>tbody>tr>td,
table[width="800"][cellpadding="5"][border="1"]>tbody>tr>td,
table[width="740"][cellpadding="0"][border="1"]>tbody>tr>td {
background:url(http://psgrafika.republika.pl/opacity20.png) !important;
border-width:0px !important;
}
table[width="800"][cellpadding="5"][border="1"]>tbody>tr>th,
table[width="740"][cellpadding="0"][border="1"]>tbody>tr>th {
background:url(http://psgrafika.republika.pl/opacity70.png) !important;
border-width:0px !important;
padding:0px !important;
}
table[width="800"][cellpadding="5"][border="1"] table{
border-width:0px !important;
}
table[width="800"][cellpadding="5"][border="1"] table table[border="1"] td{
border-width:0px !important;
height:10px !important;
}
table[width="400"] td[width="1"] {
background-color:red !important;
}

table[cellspacing="0"][cellpadding="0"][border="1"] > tbody > tr > td[width="180"][valign="top"], 
table[cellspacing="0"][cellpadding="0"][border="1"] > tbody > tr >  td[width="30"][valign="top"][align="center"] {
background:url(http://psgrafika.republika.pl/opacity20.png) !important;
}
table[cellspacing="0"][cellpadding="0"][border="1"] td[width="30"][valign="top"][align="center"] {
font-weight:bold !important;
}
table[cellspacing="0"][cellpadding="0"][border="1"] table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"] td[width="18"][align="center"] {
display:none !important;
}
table[cellspacing="0"][cellpadding="0"][border="1"] table[width="100%"][cellspacing="0"][cellpadding="0"][border="0"] td[width="80"] {
width:20% !important;
}
td[width="80"] > span[style*="8px"] {
display:block;
font-family:ms sans serif,arial,helvetica,sans-serif !important;
font-size:10px !important;
white-space:nowrap !important;
padding:2px 0px 2px 18px !important;
}
td[width="80"] > span[style*="rgb(231, 0, 0)"],
td[width="80"] > span[style*="#06E800"] {
background:url(http://static.pardus.at/images/disposition_single_minus.png) left center no-repeat #1E2618 !important;
color:#91FF2F !important;
}
