@-moz-document url-prefix("http://vk.com/") {


.emoji {
    background:none !important;
    height:0;
    font-size:0;
    color:transparent;
    position:relative;
    content:"I'm just fake content";
}
.emoji::before {
    position:relative;
    top:3px;
    font-size:11px;
    line-height:11px;
    height:11px;
    color:#000;
}

img[emoji='D83DDE22']{
    width:15px;
}
img[emoji='261D'], img[emoji='270C'], img[emoji='D83DDC4C']{
    width:20px !important;
}
