css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('https://www.tumblr.com/login'),
url('http://www.tumblr.com/dashboard'),
url('http://www.tumblr.com/new/blog'),
url('http://www.tumblr.com/inbox'),
url('http://www.tumblr.com/help'),
url('https://www.tumblr.com/preferences'),
url('http://www.tumblr.com/lookup'),
url('http://www.tumblr.com/explore'),
url('http://www.tumblr.com/following'),
url('http://www.tumblr.com/likes'),
url-prefix('http://www.tumblr.com/new/'),
url-prefix('http://www.tumblr.com/blog/'),
url-prefix('http://www.tumblr.com/edit/'),
url-prefix('http://www.tumblr.com/tumblelog/'),
url-prefix('http://www.tumblr.com/reblog/'),
url-prefix('http://www.tumblr.com/dashboard/'),
url-prefix('http://www.tumblr.com/tagged/'),
url-prefix('http://www.tumblr.com/following/'),
url-prefix('http://www.tumblr.com/spotlight/')
, url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/edit/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/reblog/"), url-prefix("https://www.tumblr.com/dashboard/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/following/"), url-prefix("https://www.tumblr.com/spotlight/") {

*{text-shadow:none !important; color:#544f4c !important; border:0px !important}

Body{
background-color:transparent !important;
color:#565656 !important;
font-size:12px !important;
background-image:none !important
}

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue,#dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_members, #main_tag, #dashboard_upload_static_file, #main_about, #main_doc_page, #main_themes, #main_meetups, #main_jobs, #main_directory_page, #dashboard_settings, #dashboard_goodies, #dashboard_tumblelog_settings, #dashboard_inbox{
background-image:url('http://i47.tinypic.com/35kqzwl.jpg') !important;
background-color:#transparent !important;
background-repeat:repeat !important;
background-position: left top !important;
background-attachment:fixed !important;
font-family:times serif !important;
letter-spacing: 1px !important;
}

#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(0,0,0,0) !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color:transparent !important
}

.content.is_media, .mask.top, .mask.bottom {  
background: none !important;
}

#left_column #posts .post .permalink {
background-color:#eeebe5 !important;
}

#left_column #posts .post .post_avatar,
#left_column #posts .post .sub_avatar .sub_avatar_inner {
border-bottom: 0px !important;
}

#left_column #posts .post{
border-bottom:0px #d6d1c8 solid !important;
}

#left_column #posts .post .post_avatar{
width: 64px !important; 
height: 64px !important; 
margin: 0px !important;
-moz-border-radius: 30px !important; 
-webkit-border-radius: 60px !important; 
}

#left_column #posts .post .sub_avatar{
width: 30px !important; 
height: 30px !important; 
margin: 0px !important;
-moz-border-radius: 25px !important; 
-webkit-border-radius: 60px !important; 
border-radius: 25px !important;
}

#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#d6d1c8 !important;
background-image:none !important;
border:0px !important
}

#nav .nav_item .nav_menu {
border-bottom:0px !important
}

#nav .nav_item .nav_menu a {
color:#888 !important;
border-top-color:#eee !important;
}

#nav .nav_item .nav_menu a:hover {
color:#ba2a41 !important;
}

#footer, #footer a {
color:#666 !important;
background-color:transparent !important;
background-image:none !important;
}

.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {
color:#333 !important
}

#right_column li{
background: transparent !important;
border:0px !important;
background:rgba(256,256,256,0.3) !important;
}

#right_column .dashboard_nav_item {
border-bottom:0px solid #eee !important;
background-image:none  !important
}

#right_column .dashboard_nav_item .dashboard_nav_title,
                    #right_column .dashboard_nav_item .dashboard_nav_title a,
                    #right_column .dashboard_nav_item a,
                    #right_column .dashboard_nav_item ul.dashboard_subpages,
                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {
                        color:#d6d1c8 !important;

border:0px !important;
}
#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
background:rgba(256,256,256,0.7) !important;

}


#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
                        color:#d6d1c8 !important ;
                    }

#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
                        background: none !important;
                    }

#show_radar a, #hide_radar a,
                        #hide_recommended_blogs a, #show_recommended_blogs a {
                            color:#d6d1c8 !important;
                        }


#left_column #posts .notification {
                        background-color:#eeeae1 !important;
                        color:#666 !important;
                        border-bottom-color: #eee !important
                    }

#left_column #posts .notification .block {
                            color:#333 !important;
                        }

#left_column #posts .notification.alt {
                        background-color: #f7f7f7 !important;
                    }
#left_column #posts .notification.last_notification,
                    #left_column #posts .notification.single_notification {
                        border-bottom-color:#ececec !important;
                    }
#left_column #posts .notification a {
                        color:#565656 !important;
                        border-bottom:0px !important;
                    }
#left_column #posts .notification blockquote {
                        border: 1px solid #d6d1c8 !important;
                    }

#left_column #posts .notification .avatar {
                        border-color: #d6d1c8 !important;
                        background-color:#d6d1c8 !important;
opacity:0.5 !important;
                    }

#left_column #posts .notification .avatar:hover{
opacity:1 !important;
                    }

.dashboard_switch_blog_menu_header{
background-color: #transparent !important;
color: #666 !important;
font-family: times serif !important;
font-size: 11px !important;
}
.dashboard_switch_blog_menu_item{
background-color:#fff !important;
color:#666 !important;
border:1px solid #d6d1c8 !important;
font-family: times serif !important;
font-size: 11px !important;
}
.dashboard_switch_blog_menu_footer{
background-color:#fff !important;
color:#d6d1c8 !important;
}
.dashboard_switch_blog_menu_wrapper{
background-color:#fff !important;
}

#new_post {
background-color: #fff !important;
}

.new_post_label_icon {
background-image: none !important;
}

.new_post_label {
padding-top: 74px !important;
}

#new_post .arrow,.avatar_and_i .arrow,.arrow {
background: none!important;
width: 0px!important;
height: 0px!important;
border: 0px !important;
margin-top: 0!important;
}

.post_avatar,.sub_avatar {
background-color: transparent !important;
}

.post.photo_post .photo, .post.photo_post .video_thumbnail {
-moz-box-shadow: 0px 0px 0px #999999 !important;
}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{
background-image:none !important
}
.notification_type_icon{
display:none !important
}

h1,h2,h3,h4,h5,h6{color:#333 !important}

a:link, a:visited {
color: #9f6e7d !important; 
text-decoration: none !important;
font-weight: normal !important;
text-transform: lowercase !important;
filter: alpha(opacity=100) !important;
-moz-opacity: 1 !important;
opacity: 1 !important;
-webkit-transition: color 1s !important;
font-family: times serif !important;
font-size: 11px !important;
font-style: normal !important;
}

a:hover {
color: #efe0e0 !important;  
filter: alpha(opacity=100) !important;
-moz-opacity: 0.100 !important;
opacity: 1 !important;
-webkit-transition: color 1s !important;
font-family: times serif !important;
font-size: 11px !important;
font-style: italic !important;
}

#new_post_notice_container{
color:#d6d1c8 !important
}

#new_post_notice_container a{
color:#fd6d1c8 !important
}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{
background-image:none !important
}
.notification_type_icon{
display:none !important
}

A[class^="card"]{
-moz-box-shadow: 0px 2px 2px #dacfc8 !important;
}

.card .photo {
border-bottom:solid thin #dacfc8 !important;
-moz-box-shadow: 1px 2px 2px #dacfc8, -1px 1px 1px #a18892 !important;
}

.conversation_lines li{
border-color: #a18892 !important;
}

blockquote{
background-color: #f9f9f9 !important;
margin-left: -5px !important;
border-left: dotted 1px #dacbcf !important;
border-bottom-left-radius:8px!important;
border-top-right-radius:8px!important;
border-top-left-radius:8px!important;
border-bottom-right-radius:8px!important;
}

li.selected,li.recessed,a.active,div.post_filter_tabs a{
background:none!important;
}

.post_question_nipple {
background: none !important;
width: 0 !important;
height: 0 !important;
margin-top: 0 !important;
border-top: 8px solid transparent !important;
border-right: 8px solid transparent !important;
border-left: 8px solid transparent !important;
}

DIV[class^="post_question"]:not([class="post_question_nipple"]),.chat_line:nth-child(even),PRE{
background-color: #f1f0ed !important;
color: #666!important;
}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i46.tinypic.com/15f0v4g.jpg') center center no-repeat !important;
}
.new_post_label{ padding-top:73px !important;}
.new_post_label_icon{background-image:none !important}



.sub_avatar{
width:32px !important;
height:32px !important;
background-color:transparent !important}

.arrow{display:none !important}

.tab a{
color:#d6d1c8 !important;
}

.items li a {color:#d6d1c8 !important}
.last_item a{color:#d6d1c8 !important}

.selected a{text-shadow:none !important; color:#d6d1c8 !important}
.recessed a{text-shadow-color:none !important; color:#d6d1c8 !important}

.right_column_section li{background:rgba(0,0,0,0.1) !important; border:0px !important; color:#333333!important}
.right_column_section li a{color:#333 !important}
.right_column_section li a:hover{background:rgba(0,0,0,0.5) !important}
.notification, .avatar_frame, .nipple{border:0px !important}

.permalink{
background-image:none !important;
height:15px !important;
width:15px !important;
margin-top:4px !important;
margin-right:5px !important;
background: #bab4b1!important;
border-bottom-left-radius:10px!important;
border-top-right-radius:24px!important;
-moz-box-shadow:-1px 1px 2px #bab4b1!important;
-webkit-box-shadow:-1px 1px 2px #bab4b1!important;
box-shadow:-1px 1px 2px #bab4b1!important;
}

.permalink:hover{
background:#bca5ad!important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:rgba(0,0,0,0) !important}
.dashboard_options_form, .dashboard_options_form_disclaimer{ border:0px !important; color:#333 !important; font-weight:bold !important; text-shadow:1px 1px 1px #444 !important}


.selection_nipple{
display:none !important;
}

.return_to_top_icon{
content: "" !important;
background: #dad2ce !important;
border: solid thin #f5f2e4 !important;
border-radius: 9px !important;
-moz-border-radius: 9px!important;
-webkit-border-radius: 9px!important;
}

.return_to_top_icon:before{
width: 0px!important;
position: absolute!important;
content: ""!important;
margin-top: 12px!important;
margin-left: 12px!important;
border-right: solid 13px transparent!important;
border-bottom: solid 13px #f5f2e4 !important;
border-left: solid 13px transparent!important;
}

.return_to_top_icon:after{
position:absolute!important;
content: ""!important;
width: 12px!important;
height: 13px!important;
margin-top: 25px!important;
margin-left: 19px!important;
background-color: #fff !important;
}

#left_column #posts .post .post_avatar{-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}

#left_column #posts .post .sub_avatar_inner {-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;
}

#right_column li:hover {
   opacity:1; 
   background: rgba(256, 256, 256, 0.6) !important;
 }
ul#user_channels{color:#333333 !important}
#right_column a{color:#333333 !important}
*{text-shadow:none !important}
#logo {
height: 0 !important;
width: 0 !important;
padding-left: 245px !important;
padding-top: 65px !important;
background: url(http://i45.tinypic.com/rk7aqt.jpg) no-repeat !important;

}
*{-moz-box-shadow: none !important;
  -webkit-box-shadow:none !important;
  box-shadow: none !important;}
}
