css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("backlog.jp") {

table.view td.user img.user {
 width:16px !important;
 height:16px !important;
}
td.status div {
 font-size:80% !important;
 line-height:100% !important;
}
#comments .comment-section .loom h2 {
    font-size: 1.10em !important;
    margin-bottom: 2px !important;
    margin-top: 2px !important;
    padding-bottom: 0.1em !important;
    padding-left: 0.4em !important;
    padding-right: 0.1em !important;
    padding-top: 0.1em !important;
}
div.loom blockquote {
    margin-bottom: 2px !important;
    margin-left: 0.2em !important;
    margin-right: auto !important;
    margin-top: 2px !important;
    padding-bottom: 0.2em !important;
    padding-left: 0.2em !important;
    padding-right: 0.2em !important;
    padding-top: 0.2em !important;
}
#bodyLeft #comments ul.changelog {
    font-size: 90% !important;
    margin-bottom: 3px !important;
    margin-top: 3px !important;
}
#bodyLeft #comments table.comment-header td {
    padding-bottom: 4px !important;
    padding-left: 8px !important;
    padding-right: 8px !important;
    padding-top: 4px !important;
}
#bodyLeft #comments table.comment-header {
    margin-bottom: 0px !important;
}
#bodyLeft #comments h4 {
    margin-bottom: 0px !important;
    margin-top: 0px !important;
}
.loom {
    font-size: 93% !important;
    line-height: 1.2 !important;
}
}
