css:
@-moz-document url-prefix("http://forummotor.israelinfo.ru/"), url-prefix("http://forumpravo.israelinfo.ru/"), url-prefix("http://forum.israelinfo.ru/") {
a.topictitle, a.forumtitle{
font-weight:normal !important;
}

dt[title="Непрочитанные сообщения"] a.topictitle,
dt[title="Непрочитанные сообщения"] a.forumtitle{
font-weight:bold !important;
text-decoration:underline !important;
}

.row.bg1,.row.bg2{
background-color:white !important;
}

.post.bg2{
background-color:white !important;
}

.post{
border:1px solid darkblue !important;
border-radius:12px !important;
box-shadow: 10px 10px 5px #888;
margin:12px 12px 18px 0px !important;
padding:0 18px;
}

.post img{
max-width:100%;
}

.post.bg1{
    background-color: #f3f3f3 !important;
}

ul.topiclist li{
   background-color:white !important;
}

.postprofile{
border-right:1px dotted darkblue !important;
}

.postprofile a{
   font-size:140% !important;
}

.row.sticky{
background-color:ivory !important;
}

.lastpost a:nth-of-type(1){
color: #800000 !important;
}

.row .icon{
background-size:0% !important;
margin-left:-28px !important;
}

#ii-side,#page-body table:nth-of-type(1){
display:none !important;
}

#page-body,#page-header{
max-width:1000px !important;
margin:0 auto !important;

}

.navbar{
background-color:white !important;
border:1px solid darkblue !important;
border-radius:8px !important;
}

.panel{
background-color: #ECF1F3 !important;
}

.panel.bg2{
backgorund-color: #ECF1F3 !important;
}

.topiclist .header{
display:none !important;
}

.posts,.views,.topics{
font-size:90% !important;
margin-top:11px;
}



.pagination span a{
font-size:120% !important;
color:#161646 !important;
}

.pagination span a:last-child{
font-weight:bold !important;
}

.post span.corners-bottom,.post span.corners-top{
display:none !important;
}

.post .inner{
padding-top:4px;
}

.back2top{
padding-bottom:4px;
}

#qr_editor_div{
display:block !important;;
}

#qr_showeditor_div{
display:none !important;;
}

a.right-box.up[title="Скрыть быстрый ответ"]{
display:none !important;
}
}
